-- ----------------------------
-- Table structure for ac_intro
-- ----------------------------
DROP TABLE IF EXISTS `ac_intro`;
CREATE TABLE `ac_intro` (
  `update_time` varchar(255) DEFAULT NULL,
  `content` text,
  `ac_id` int(11) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`ac_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ac_intro
-- ----------------------------
BEGIN;
INSERT INTO `ac_intro` VALUES ('1532482684344', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;成都榜样。为积极培育和践行社会主义核心价值观，充分发挥先进典型人物的示范引领作用，成都启动“2015成都榜样”主题活动。本次主题活动由成都市委宣传部、成都市文明办主办，成都传媒集团、成都广播电视台承办，各市级有关部门、各区（市）县共同参与。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp; “成都榜样”不仅是我市优秀市民的杰出代表，更是我市践行社会主义核心价值观的人格化体现，“成都榜样”评选活动是成都创新打造的培育和践行社会主义核心价值观的品牌之一。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp; “2015成都榜样”活动紧紧围绕市委市政府中心工作，并结合中央文明办第五届全国道德模范评选活动、“我推荐、我评议身边好人”活动以及第四届四川省道德模范评选活动，以“我推荐、我评议身边好人”活动为基础，改进完善评选方法，延伸拓展评选范围，梳理明晰评选流程，创新提升宣传效果，在评选中宣传，在宣传中评选，通过广泛发动，层层选树，最终实现月月有榜样，不断推出新典型，在全市掀起“推荐身边榜样、弘扬榜样事迹、践行榜样精神”的热潮，使 “成都榜样”大型主题活动逐渐成为具有成都特色、影响广泛的成都市精神文明建设品牌，为奋力打造西部经济核心增长极、建设现代化国际化大都市提供的精神动力和道德支撑。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp; “2015成都榜样”主题活动创新推荐方式、宣传模式等，主要特点为：<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一、联动市级部门，创新开展行业系统主题推荐评选。2015年的主题活动创新联动市公安局、市总工会、团市委等部门，开展好警察、 好工人、好青年等系列评选活动，突出行业系统主题，策划打造行业系统榜样系列活动；<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二、扩大评选范围，开门办评选。本次活动进一步拓宽了榜样人物征集推荐渠道，通过组织推荐、媒体发掘、群众推荐、网络推荐等多种方式，并将成都文明热线96110作为征集热线，全方位发动广大市民和社会单位参与推荐。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三、整合全媒体，创新宣传方式。2015成都榜样主题活动在宣传模式上整合传统媒体、网络媒体、城市电子显示屏、城市宣传栏、微信等传播方式，开设专题网页，以全媒体的方式对候选人物进行宣传，让榜样人物的事迹凝聚人、引导人、激励人、塑造人，树立知荣辱、讲正气、作奉献、促和谐的社会风尚。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;四、月榜、年度榜，层层选榜样。本届主题活动首次推出“成都榜样”月度人物榜，年底通过市民在“成都榜样”专题网页点赞投票与评委会评议在所有月榜人物中评选出2015年度十大“成都榜样”及30名提名奖，在全社会掀起“推荐身边榜样、弘扬榜样事迹、践行榜样精神”的热潮。<br style=\"white-space: normal; box-sizing: border-box; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; background-color: rgb(240, 255, 255);\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;五、丰富多彩互动活动，让榜样走入成都百姓家。本次活动将开展“成都榜样高校分享会”、“我学榜样做公益”志愿者活动、“成都榜样广播剧进高校”、成都榜样走进道德讲堂等一系列丰富多彩的互动活动，让榜样人物走进市民生活，走进百姓家庭，形成向上向善的良好风尚。</p>', 0);
COMMIT;

