-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `article_ID` int(20) NOT NULL AUTO_INCREMENT COMMENT '稿件的ID，主键',
  `publish_time` varchar(255) NOT NULL COMMENT '新闻发布日期',
  `article_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章的标题',
  `keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章的关键词',
  `admin_id` varchar(255) NOT NULL COMMENT '管理人的id，即发布的人的id',
  `admin_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '管理人的名字',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  PRIMARY KEY (`article_ID`),
  KEY `admin_id` (`admin_id`),
  KEY `admin_name` (`admin_name`)
) ENGINE=InnoDB AUTO_INCREMENT=51 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of article
-- ----------------------------
BEGIN;
INSERT INTO `article` VALUES (10, '1532142236715', '“中国好人”耿琦：百姓安民警安做好群众喜爱的普法员', '民警做普法员', '4', 'admin', '<p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160708/1467948442.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\"></span><span style=\"color: rgb(51, 127, 229);\">耿琦在郫县新民场小学上为孩子们上法制课。</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160708/1467953777.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">耿琦在成都金琴路小学参加“名师一堂课，文明交通行”大型志愿者活动。</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160708/1467955252.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">耿琦为幼儿园的孩子们上安全教育课。</span><span style=\"color: rgb(51, 127, 229);\"></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160708/1467949909.jpg\"/><br/></span></p><p><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">耿琦走进校园开展法制宣传。</span><span style=\"color: rgb(51, 127, 229);\"></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 我叫耿琦，今年37岁，我是成都市郫县公安局的一名民警，我入党13年。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 我是一名人民警察，是纪律部队的一员，于2003年光荣地加入了中国共产党。作为一名党员，我必须踏踏实实地做好自己的本职工作，做一个对党和国家、对社会和人民有益的人。同时，我要用自己的实际行动去感染周围的人，给他们带去社会正能量。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;我从事的是公安宣传工作，主要负责宣传法律知识、安全防范技巧和警察故事。“5+2”“白加黑”是我工作状态的真实写照，虽然辛苦，但我热爱自己的工作。业余时间我也会积极主动地向社会宣传和普及法律知识。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 法制宣传教育是提高全民素质，推进依法治国基本方略。用什么样的法制宣传形式才能让老百姓爱听、喜欢听、听得明白，是我长期思考的问题。为此，我不断摸索创新，陆续创作了《天下无贼》《毒品的危害》《红绿灯的警示》等评书段子，同时通过“散打评书”的方式开展诙谐普法。从2014年至今，我合计宣讲40余场影响5万余人，把法律知识传递给社区居民，获得了“成都市百姓故事会十大故事王”和两届“成都市群众最喜爱的普法员”的称号。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;2016年5月成都市交通安全月，我参与了大型公益活动——“名师一堂课，文明交通行”活动，授课时间达170多分钟。虽然经常讲得口干舌燥，但作为一名民警，动动嘴便能为人们分忧解愁，在笑声中就能给百姓传讲法律知识，也让我感到无比开心。百姓安，咱们民警才能安。无论我身在何地，都要义不容辞地为百姓排忧解难，传递法治的力量。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2015年12月，我有幸获得了“中国好人”的称号。自己不懈的努力得到了社会的认可，我觉得很有成就感。但是站在聚光灯下，各种压力也随之而来了，有人说我“不务正业”，对于不同的声音，我的心态还算比较平和。于我而言，作为一名宣传警察，就是通过自己的评书表演，让更多人了解公安工作，知晓法律知识和安全防范常识。如果有一天遇到某个诈骗犯，人们能够想起曾有个耿警官教过如何防范，我就知足了。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 我是一名有着13年党龄的老党员，因为人民群众的信任，我当选过成都市公安局的优秀党员，我感谢党这么多年来对我的栽培，也感谢群众对我的信任，我将继续坚守一个共产党员的本色，在党的领导下继续前行，用更加昂扬的斗志、坚定的信念、顽强的作风迎接新的挑战，继续做为人民服务的好党员。（四川文明网 杜瑾萱 采访整理）</span></p><p><br/></p>');
INSERT INTO `article` VALUES (13, '1532082941745', '成都两民警荣获四川首届“十大最美人民警察”称号', '最美民警', '4', 'admin', '<p>&nbsp;</p><p><span style=\"line-height: 2;\">&nbsp;16日晚7时50分，泸州四川警察学院礼堂内，四川警察学院学生身着警服，用一曲《少年壮志不言愁》拉开了“四川首届十大最美人民警察颁奖仪式”的帷幕。我市的“经侦尖刀”林正良、青羊区分局刑警大队中队长阮立波等10名民警被授予“最美人民警察”称号，包括成都市公安局特警支队民警朱伟华在内的20名民警获得“最美人民警察”特别奖。</span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 破获重特大刑事案件30余起</strong></span><br/><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 他，被誉为“辖区平安守护神”</strong></span><br/><span style=\"line-height: 2;\"></span><br/></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160617/1466138689.jpg\"/><br/></strong></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">“最美警察” 阮立波 成都市公安局青羊区分局刑警大队中队长</span><span style=\"color: rgb(0, 51, 153);\"></span><br/></strong></span></span></p><p><span style=\"line-height: 2;\"><span style=\"text-align: justify; line-height: 24px; white-space: normal;\">&nbsp; &nbsp; &nbsp;&nbsp;颁奖仪式上</span>，阮立波第一个上台领奖。阮立波是市公安局青羊区分局刑警大队三中队中队长。10年刑警生涯，他带头破获重特大刑事案件30余起，涉案金额900多万元，抓获犯罪嫌疑人330余名，被辖区市民誉为“辖区平安的守护神”。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2013年9月13日晚10时50分，时任金沙派出所副所长、刑侦中队长的阮立波带领刑警在金沙鹭岛小区查处一个吸毒窝点。为防止屋内嫌疑人翻窗逃逸，他和战友摸黑翻到后窗外的围栏边，准备从这唯一的突破口冲进屋内。在缺失的围栏玻璃后面，是一口深达9米的管道井！黑暗中，冲在最前面的阮立波掉了下去，当场昏迷。后经医生诊断，阮立波颅脑损伤，全身多处骨折，胸部闭合性损伤，创伤性湿肺，急性肺水肿并失血性休克。医院当晚下达两次病危通知。此后，阮立波一直靠呼吸机维持生命。经医生全力抢救，他在连续昏迷5天后，终于苏醒过来。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;2014年7月，阮立波从原来所在的金沙派出所调至分局刑警大队任刑警中队长。虽然旧伤未愈，但他依旧带领民警冲在一线，接连攻克一系列重案要案。</span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 曾“铸”中国反假币“里程碑”<br/>&nbsp; &nbsp; &nbsp;&nbsp;他，经侦线上的一把尖刀<br/></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: justify; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"color: rgb(0, 0, 0); line-height: 2;\"></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160617/1466136600.jpg\"/><br/></strong></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">“最美警察” 林正良 成都市公安局经侦支队三大队大队长</span><span style=\"color: rgb(0, 51, 153);\"></span><br/></strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 成都另一位被评为“最美人民警察”的是市公安局经侦支队三大队大队长林正良。从事经侦工作20余年，林正良作为打击假币犯罪的一把尖刀，经手了247卷4万余页的案侦材料，将33名重大嫌犯抓捕归案，接待了1200余人次的群众来访。让他引以为傲的，应属2011年率队侦破的0901特大假币案，该案曾被公安部领导称为中国反假币犯罪“里程碑”案件。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2009年，广东、河北、四川一带出现大量假币，线索显示，成都可能存在假币原料生产基地。林正良根据线索，发现可疑人员孟某涉嫌销售用于制造假币的纸张。为了摸清源头，他对其展开长达两年的监控。在孟某六次改变纸张生产地点后，林正良派出一名资深民警，先去印刷厂学了两周印刷技术，后至孟某的公司应聘工人，借此摸清该团伙的作案规律。</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: justify; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;2011年8月，林正良率队捣毁了烫印“防伪安全线”的假币生产窝点，抓获13名重大假币犯罪嫌疑人，查获了能印制超10亿元假币的防伪烫金纸及原材料。同时，福建、广东等地警方根据此线索捣毁5个假币窝点，收缴假币4.46亿元。</span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 四川首届“十大最美人民警察”名单</span></strong></span><span style=\"color: rgb(0, 0, 0); line-height: 2;\"></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;阮立波 成都市公安局青羊区分局刑警大队三中队中队长</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 杨 星 遂宁市公安局安居区分局西眉派出所副所长</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;江筱辉 乐山市夹江县公安局情报信息大队大队长</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 解古依格 凉山州甘洛县公安局法制大队大队长（女）</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 李永权 南充市公安局嘉陵区分局特勤大队指导员</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;王 艳 内江市公安局市中区分局刑事科学技术室法医中队副指导员（女）</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 解 冻 泸州市公安局禁毒缉毒支队情报站副主任</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;林正良 成都市公安局经济犯罪侦查支队三大队大队长</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 母俊峰 四川省公安厅交通警察总队高速公路支队内宜高速公路二大队民警</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;杨武东 四川消防总队甘孜支队色达县大队参谋</span></p><p></p>');
INSERT INTO `article` VALUES (14, '1532151075738', '锦江区组织开展道德模范事迹巡讲活动 传播榜样精神', '事迹巡讲活动', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span>　</p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160726/1469522957.png\"/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"color: rgb(51, 127, 229); line-height: 2;\">锦江区三圣街道栀子街社区组织开展了道德模范事迹志愿者巡讲活动</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 为让道德模范走进群众身边，把道德模范的典型事迹传播到全社会，形成健康向上的道德舆论氛围，7月18日上午，锦江区三圣街道栀子街社区组织开展了道德模范事迹志愿者巡讲活动，40多名社区志愿者参加了此次活动。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 栀子街社区党员志愿者代表张余素老人，是一本鲜活的教科书，她用实际行动谱写了不平凡的人生。此次活动邀请了锦江区道德模范张余素为大家作报告，她通过以下四点分享了“雷锋精神”对她的影响：一是“雷锋精神”让我想读书、要读书、勤奋读。张老师言传身教，通过自己学习道路的坎坷来告诫孩子们要拥有凿壁偷光、囊萤映雪的精神，通过刻苦学习创造属于自己的宝贵财富。二是雷锋精神激励我帮助青少年成长，杜绝未成年人上网。张老师组织“五老”志愿者，通过明察暗访，监督网吧等帮助了许多沉迷网络的青少年，并且对在场的青少年进行劝诫和忠告。三是退休以后，熔化在我心灵中的雷锋精神永存。张老师通过服务居民、助老助残、做好党员志愿者服务工作三个方面，阐述了退休以后，依然践行“雷锋精神”服务群众。四是饮水不忘挖井人，幸福不忘共产党。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">活动结束后，大家纷纷表示今天的宣讲确实很好，使我们倍受教育和启发，这种用身边的人教育身边人的方式，活灵活现，事迹看得见、摸得着，实实在在，这种巡讲活动能成为广大党员和群众自我教育、自我提高的有效途径。（来源：锦江区文明办）</span></span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (15, '1532150885113', '成都举行我学榜样做公益活动 多种形式传递爱', '“我学榜样”公益活动', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span>　</p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454317442.png\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\">成都榜样讲述经历传递正能量</span><span style=\"color: rgb(51, 127, 229);\"></span><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 为让榜样人物走进市民生活，走进百姓家庭，进一步密切社区居民的邻里关系，1月30日，由成都市文明办主办，成都公益组织服务园等单位承办的“我学榜样做公益·街坊邻居动起来——成都榜样进社区·便民服务在身边大型公益活动”在武侯区得胜社区顺利举办。活动邀请7位榜样代表在传递正能量的同时，与社区居民共同开展“迎新春社区文化节”特色活动。活动当天，阴冷的天气，丝毫没有阻挡成都榜样和社区居民们的热情。7位成都榜样走进武侯得胜社区，与众多居民们共同开启新年新篇章。</span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454322356.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229); line-height: 2;\">成都榜样事迹介绍</span><span style=\"color: rgb(51, 127, 229); line-height: 2;\"></span><br/></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><strong><span style=\"line-height: 2;\">见证榜样力量，倡导志愿精神。</span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">活动现场，杨高鉴、拉孟、蒲良香、裴丽娟、林德明、黄健能、王乐等七位成都榜样代表就自身经历与感悟，从孝老、环保、敬业、友爱、互助等方面，为社区居民进行了分享，希望大家都能拥有正能量，为社会的发展贡献一份微薄的力量。榜样们也对社区居民们致以新春祝福，希望家家团圆美满、顺顺利利。随后榜样代表和街道、社区领导对社区困难家庭、孤寡老人发放了慰问物资，让他们安心快乐过好年。</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454320659.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229); line-height: 2;\">舞蹈表演</span><span style=\"color: rgb(51, 127, 229); line-height: 2;\"></span><br/></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;</span><strong><span style=\"line-height: 2;\">&nbsp;活动精彩纷呈，多种形式传递关爱。</span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">欢乐的鼓声，热情的舞蹈，吸引了不少居民驻足观看，据了解，此次活动分为创新微社区·榜样在身边、爱心集市、社区游园活动、社会服务展览展示、舞台表演等五个部分，可谓涵盖衣食住行方方面面。 舞台上，《等待》，《欢聚一堂》，《爷爷奶奶》，《俏花旦》等精彩的节目得到了居民一致好评；社区卫生服务中心与眼科医院的服务处，不少居民进行免费医务咨询和身体健康检查；爱心集市处，很多小朋友将家中玩具、DIY手工艺品、闲置物品拿出来现场出售或置换，还自愿把售卖收入捐助给社区用于帮扶济困……</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454323021.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\">免费健康咨询现场</span><span style=\"color: rgb(51, 127, 229);\"></span><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\"><br/></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454320495.jpg\"/><br/></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\">社区居民拔河比赛</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\"><br/></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160201/1454316291.jpg\"/><br/></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229);\"><span style=\"line-height: 2;\">成都榜样拉孟老师为社区居民写春联送祝福</span><span style=\"line-height: 2;\"></span><br/></span></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;</span><strong><span style=\"line-height: 2;\">&nbsp;迎新春联免费送，榜样牵头暖到心。</span></strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “老师，能请您帮我写一副春联吗？”</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">“可以，可以，没问题！”</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 会场一隅的长桌旁不少社区居民围观，纷纷手拿红纸，满怀期待。原来这是迎新春联免费送，成都榜样拉孟老师和诸位书法家现场为大家书写新春祝福，并免费赠送给广大居民。拉孟老师作为非物质文化遗产传承人，他的生活中只有唐卡和孩子们。他先后资助40多个孩子接受教育学画画，致力于让唐卡被更多人知晓和被传承下去。活动现场，他书写的藏文版的“扎西德勒”和“吉祥如意”更是获得了居民们的一致赞赏。能把自己的祝福融入书画中，获得大家的喜爱，拉孟老师表示很高兴。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">榜样黄健能也表示，这样的活动很有意义，大家的参与性很高，对于以后，自己也会保持平常心，做好自己该做的事，而且有很多精神都值得去发扬。活动在一派祥和喜庆的氛围中接近尾声，但榜样的力量与志愿服务的精神早已在我们的心中发芽生长。相信新的一年，我们会有更好的精气神面对生活，服务他人。</span></span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (16, '1532149074297', '成都榜样为癌症患者举办生日庆典', '关爱癌症患者', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span>　</p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450254851.png\"/></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"color: rgb(0, 51, 153);\"><strong>“我学榜样做公益”关爱癌症患者志愿服务活动现场</strong></span><strong><span style=\"color: rgb(0, 51, 153);\"></span></strong></p><p style=\"margin: 0px; padding: 0px;\"><br/></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 15日，由成都市文明办主办、成都公益组织服务园、成都抗癌协会承办的“我学榜样做公益”关爱癌症患者志愿服务活动在成都三圣花乡开心农庄举行。成都榜样林德明、江艳、王乐和拉孟来到现场，和300多位癌症病人一起为33位癌龄五周年的病友举办隆重的生日庆典，祝贺他们重返健康人群。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 15日上午，在一曲“生命之河”的动人音乐里，300多位癌症病友和成都榜样们一起翩翩起舞，感恩我们每天的快乐生活，也拉开了“我学榜样做公益”关爱癌症患者志愿服务活动的序幕。在活动过程中，多位癌症病友走上舞台，与大家分享自己与癌症抗争的故事，呼吁大家要乐观面对生活。</span></p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;在成都榜样中，其实也不乏深处绝境却依然积极向上的正能量人群。在这次的活动中，成都榜样也来到现场，与癌症病友面对面相互交流，让他们感受到来自榜样和社会的真挚关怀。</span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; line-height: 2; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450257180.png\"/></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">成都榜样来到活动现场</span></strong><strong><span style=\"color: rgb(0, 51, 153);\"></span></strong><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450261674.png\"/><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">成都榜样拉孟送上自己的唐卡画<br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450258444.png\"/><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">活动方为抗癌成功五年的人群颁发奖章</span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450257626.png\"/><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">活动方为癌症病友准备的蛋糕<br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450256322.png\"/><br/></span></strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(0, 51, 153);\">合影留念<br/></span></strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “我抗癌成功2年”、“我抗癌成功5年”、“我抗癌成功10年”......在活动现场，癌症病友们兴奋地告诉记者他们与癌症“赛跑”的故事，活动方还在现场宣布了重返健康人群抗癌五周年的英雄名单，并为他们颁发了奖章。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;“生命掌握在自己的手中，我们要摆正心态，乐观地面对现在的生活。”78岁的徐继东老人已经抗癌成功24年了。他告诉记者，这些年他学着用一种感恩的心态面对生活，尽管现在年纪大了，可每天仍然坚持散步2公里，坚持做早操。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 和徐继东老人一样，在成都还有一大批癌症病人正在为生命努力抗争，他们未曾想过放弃，并积极投身志愿服务事业中。“这些癌症病人在生命的最后阶段聚在一起，形成一个爱的家庭，他们经常组织一些主题活动去帮助身边的人，值得我们称赞。”成都抗癌协会宣传部部长袁晓倩说到。（成都文明网记者 杨金祝 摄影报道）</span></p><p><br/></p>');
INSERT INTO `article` VALUES (17, '1532149090409', '成都榜样之“蓉城最美社工”、“十强社工机构”和“十佳社工项目”评选活动启动', '最美社工，十佳社工', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span>　</p><p>&nbsp; &nbsp; &nbsp; 为弘扬社工精神、提升社会工作认知度、认同度和参与度，形成示范引领和推动效应，大力宣传社工典型、树立社工机构标杆、打造社工项目品牌，展示成都市专业社会工作发展和社工人才队伍建设成果，近日，由成都市民政局、成都市精神文明建设办公室联合开展，成都市民政事务中心承办的成都榜样之“蓉城最美社工”、“十强社工机构”和“十佳社工项目”评选活动正式启动。</p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 记者从民政局了解到，本次评选活动将评选出“蓉城最美社工”、“十强社工机构”、“十佳社工项目”各10名。其中，“蓉城最美社工”需来自成都市社会工作一线实务领域，且具备2年以上的基层一线工作经历；“十强社工机构”需是在民政部门登记并注册满两年及以上的社会工作专业服务机构，且长期在成都市开展专业社会工作服务；“十佳社工项目需来源于实际专业社会工作，具有真实的服务机构、服务对象和服务过程。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 本次评选活动的材料提交时间截止到8月底，届时采用“网站投票+微信投票”的形式进行为期30天的公开投票。大众可在规定时间内进入成都文明网及官方微信公众平台进行投票。评选活动揭晓后，将通过网络、电视、电台、报刊等媒体对获奖社工、机构和项目进行报道。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp;<strong>&nbsp;&nbsp;&nbsp;</strong></span><span style=\"line-height: 2;\"><strong>附：成都榜样“蓉城最美社工”、“十强社工机构”和“十佳社工项目”评选活动通知</strong></span></span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 一、活动目的</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">以培育和践行社会主义核心价值观为目的，通过评选活动，宣传榜样、鼓励先进，展示我市专业社会工作发展成果和社会工作专业人才建设风采，着力营造专业社会工作发展和社会工作专业人才队伍建设的良好氛围和社会环境，引领和带动全市专业社会工作发展，促进社会工作专业人才队伍建设，为成都市加强和创新社会治理及精准扶贫提供社会力量和专业人才支撑。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;二、评选范围和要求</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong>（一）成都榜样“蓉城最美社工”（10名）</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">1. 在成都市范围内，年满18周岁，持有全国社会工作职业资格证书或拥有社会工作专业大专以上学历教育背景。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2. 来自成都市社会工作一线实务领域，具备2年以上的基层一线工作经历。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">3. 坚持社会工作专业价值理念，具备社会工作基本专业知识和技巧，能熟练运用社会工作方法，协助服务对象解决问题。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 4. 遵纪守法，热爱社会工作，坚持社会工作者伦理价值观，有强烈的事业心、责任感和良好的职业道德，在社工岗位成绩显著，表现突出，对推动社会工作专业发展具有示范引领作用。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\"><strong>（二）成都榜样“十强社工机构”（10个）</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 1. 在民政部门登记并注册满两年及以上的社会工作专业服务机构，且长期在成都市开展专业社会工作服务（时间截止2016年7月1日）。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2. 成立以来每年年检合格，符合社会工作专业组织资质要求。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">3. 严格按章程办事，组织机构健全，内部制度完善，决策科学民主，运作规范有序，信息公开透明，自觉接受监督。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 4. 模范遵纪守法，坚持非营利宗旨，诚信自律，积极承担社会责任，品牌影响力大，社会公信度高。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">5. 围绕专业社会工作发展的大局，充分利用自身优势，积极发挥服务功能，在社会发展和社会治理以及精准扶贫工作中作用显著。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong>（三）成都榜样“十佳社工项目”（10个）</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">1. 参评项目为近年以来在社会福利、社会救助、慈善事业、社区建设、婚姻家庭、精神卫生、残障康复、教育辅导、就业援助、职工帮扶、犯罪预防、禁毒戒毒、矫治帮扶、人口计生、纠纷调解、应急处置等社会服务领域，组织开展并实施完成的专业社会工作服务项目（部、省、市、县级支持项目均可）。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2. 参评项目须来源于实际专业社会工作，具有真实的服务机构、服务对象和服务过程，所提交的各项申报材料真实有效。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 3. 参评项目须具备社会工作专业要素，体现社会工作专业理念，运用社会工作专业方法与技巧，发挥社会工作专业作用。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">4. 参评项目须具有健全的项目管理制度、完整的组织实施方案、明确的目标任务，符合项目管理和专业社会工作服务的规范要求。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 5. 参评项目在实施过程中要体现理念创新、技术创新、制度创新，创造性地探索实践专业社会工作项目管理服务方法与模式。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">6. 参评项目须全面实现项目预期目标，有效满足服务对象的实际需求，解决服务对象的实际问题，在增进社会融合、维护社会稳定、促进社会和谐、推动社会发展方面取得良好成效。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 7. 参评项目在本领域专业社会工作服务项目中具有一定代表性，体现我市专业社会工作本土特色，对同类项目的设计、组织、实施具有示范和引领作用。</span></p><p><br/></p>');
INSERT INTO `article` VALUES (18, '1532151123097', '第三届“道德模范·新都好人”投票启动', '第三届投票启动', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都全搜索新闻网</span>　</p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 经过了两个多月的群众推荐、调查核实和征求意见，7月18日，第三届“道德模范·新都好人”推荐评选表彰活动正式进入投票阶段。<span style=\"color: rgb(229, 51, 51);\">（</span><a style=\"color: rgb(8, 8, 8); text-decoration: none; cursor: pointer;\" href=\"http://news.chengdu.cn/2016/0708/1800656.shtml\" target=\"_blank\"><span style=\"color: rgb(229, 51, 51);\">此前报道&gt;&gt;</span></a><span style=\"color: rgb(229, 51, 51);\">）</span></span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 时隔三年 “新都好人”评选再续榜样力量</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;本次活动由新都区精神文明建设委员会办公室主办，距离上一届“新都好人”评选，已经过去了三年。连日来，活动组委会从一百余名推荐人中选中选定了20名候选人，并将他们的事迹通过网络专题呈现。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 即日起至8月3日，您可通过纸质投票卡投票、网络投票、微信投票、电话投票、信函投票的方式，为你心目中的“道德模范·新都好人”点赞。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;记者在候选人列表中看到，他们大都来自基层，在自己的工作岗位勤勤恳恳，立足本职，并做出贡献。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 候选人当中，有将自己全部酬劳用于村公益事业的村支书；有放弃公职，参与“8.03”云南鲁甸地震灾后心理援助的90后志愿者；有身残志坚，“一根银针治百病，一颗红心暖千家”的好郎中；有年逾古稀，20多年来从不请假，从不延误的乡村邮递员……</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “他们用自己的努力，为新都建设添砖加瓦，为大家诠释着助人为乐、见义勇为、诚实守信、敬业奉献和孝老爱亲的真谛。”组委会相关负责人表示，本次活动将继承前两届的精神，推选出最有代表性的新都好人，让他们更好的发挥榜样的力量，传播正能量，让新都人民都行动起来，更有冲劲地建设更好的新都。</span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;4种投票方式 为心目中的好人投票“点赞”</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 1、网络投票</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;进入文明新都网，点击小浮窗为好人投票。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 网址：<a style=\"color: rgb(8, 8, 8); text-decoration: none; cursor: pointer;\" href=\"http://www.cdwmxd.com/\" target=\"_blank\"><span style=\"color: rgb(229, 51, 51);\">http://www.cdwmxd.com/</span></a></span></span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;2、微信投票</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 扫描二维码，通过微信方式参与投票。</span></span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 3、纸质卡片投票</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;收集《新都资讯》2016年7月18日“第三届‘道德模范·新都好人’推荐评选纸质投票卡”，按照投票办法说明要求填写之后，报送或寄送至新都区文明办（香城南路60号文广中心9楼，邮政编码：610500）完成投票。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 4、电话投票</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;拨打组委会设立的投票热线028-83972625、028-83928030进行电话投票。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\"></span></strong></span><span style=\"color: rgb(229, 51, 51); line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 20名“新都好人”候选人 带来20种感动</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;周远银，助人为乐的泥瓦匠，照顾患有智力残障的兄弟，帮助邻里解决困难。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 杨洪燕，心有大爱献身瑞云，全身心地投入到社区工作和公益事业之中。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 刘小英，紫瑞街上的热心人，被人亲切地称为“刘妈”，无私帮助孤寡老人、社区居民。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;廖永忠，不要工资酬劳的村支书，将全部报酬和补贴用于村公益事业，出钱为村民盖房屋。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 郭官浪，放弃公职的90后志愿者，参与“8.03”云南鲁甸地震灾后心理援助，照顾有需要的老年人，为外来务工人员子女提供学业辅导。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;赵秀花，孝心感人的“前儿媳”，辞去工作照顾身患胃癌的前夫的父亲。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 肖红玲，不离不弃姐弟情，不管吃多少苦，受多少累，最大的心愿就是想办法把弟弟给治好。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;温成春，用亲情对抗苦难的女人，照顾着长年生病的年迈父母，两个智障的哥哥，生活不能自理的儿子。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 黄君芳，古稀之年的孝女、贤妻和良母，任劳任怨照顾残疾的丈夫、母亲和精神状态不佳的儿子。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 张正明，心系群众的社区书记，带领支部党员和居民群众创造性地开展工作。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;张惠，在坚守中建功的女狱警，她坚持“四心教育”，爱心关怀、真心感化、热心帮助、耐心教育。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 杨素蓉，待“五保”老人如亲人的敬老院院长，她以自己的行动诠释了一则古训：“老吾老以及人之老”。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;魏婷，年轻的“检察官妈妈”，从事检查工作十余年，曾办理过多起大要案、疑难案。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 谭志荣，身残志坚的好郎中，“一根银针治百病，一颗红心暖千家”。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;陈玉芳，未成年人的法制“保护伞”，退休后一直从事关心下一代工作，至今已有14个年头。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 曾德英，年逾古稀的乡村邮递员，20多年来从不请假，从不延误。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 魏寿超，太平社区的守护神，全心全意保护社区21年的治安队长。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;马传贵，救人于危难的搬运工，对次跳入大水之中勇救小女孩。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 廖家，有勇有谋的90后，空手搏歹徒，智救女青年。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;庄道华，肯吃亏的信义农场主，抵押房产支付工人工资。</span></span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (20, '1532151138713', '《我的村民我的村》诗意展现成都新农村建设成就', '建设成就', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span>　</p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 由成都传媒集团、峨眉电影集团联合出品的电影《我的村民我的村》，7月7日在峨影1958影城举行发布会。该片以成都市蒲江县西来镇两河村党支部书记姚庆英为原型，融入我市其他基层干部、群众的先进事迹进行创作拍摄，艺术地塑造了一位基层女性党支部书记形象，全面展现了成都市新农村建设的最新成就。</span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160708/1467949183.jpg\"/><br/></span></p><p><span style=\"line-height: 1.5;\"><span style=\"color: rgb(51, 127, 229); line-height: 2;\">《我的村民我的村》新闻发布会</span><span style=\"color: rgb(51, 127, 229); line-height: 2;\"></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 《我的村民我的村》预计今年10月国庆档期上映。导演苗月在发布会上信心十足：将把成都如画的新农村，通过大银幕展示给全国观众。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">该片未映先火，在不久前举行的第16届华表奖颁奖典礼上，《我的村民我的村》作为四川的唯一代表，成为中国电影向建党95周年献礼的8部优秀影片之一，也是唯一一部聚焦我国新农村建设题材的影片，被列入中宣部2016年重点推荐影片。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;主旋律影片探索创新 将刷新观众对农村的认识</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">苗月的上一部电影《天上的菊美》让峨影集团时隔16年再度获得华表奖。从《天上的菊美》到《我的村民我的村》，苗月也在探索主旋律电影的创新。同样是讲述基层党员干部，《我的村民我的村》用喜剧手法来表现，更加诗意。影片通过村里三代村支书如何全心全意为百姓谋福利，带领村民奔小康，在民主建设、产业创新、土壤改造、绿色农业、文化建设、“互联网＋”等方面引领乡村生活新时尚的各种片段，演绎了一段社会主义新农村温暖而精彩的故事。影片以艺术手法展现了成都市基层党组织建设、农村民主政治、精准扶贫、大学生村官等目前重点推进的新农村建设工作，让观众感受翻天覆地、日新月异的新农村变化。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 发布会现场放映了剧组刚刚剪出的3分钟片花。诗意、温暖、幸福，成为影片的关键词。该片云集岳红、巴登、孙敏、向小乔、陈西贝、赵亮、媛凤等本土老中青实力演员，男主角白威是剧组主演中唯一的外地人，而与超女陈西贝的10年恋情也让他成为“成都女婿”。白威感慨此次来蒲江拍摄，深深爱上了这片土地，“这里人生活幸福指数很高，每个人都很和气、脾气特别好，生活节奏也不像都市人那么紧张焦虑，看得出来生活得很快乐，完全颠覆了我对农村的印象。作为一个外地人，我也喜欢上了这里，准备在成都买房安家。”影片主创纷纷表示，这部电影颠覆了自己对农村的感受，“相信影片将刷新观众对农村的认识与看法。”</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;导演发布会上激动落泪</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;</strong></span><span style=\"line-height: 2;\"><strong>生活的激情让这部电影诞生</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 影片编剧、导演苗月在发布会上激动落泪，她说：“感谢出品方邀请我来执导这部电影。去年接到邀请，我正在做《大火种》的后期，讲述95年前山东农民跟着共产党闹革命的故事，而《我的村民我的村》则展现了现在的基层党员干部，带领村民实现共同富裕的故事。两部电影都是农村题材，一北一南，一旧一新，一悲一喜，让我们深刻感受了中国百年历史的发展变迁。”苗月深情地说：“这部电影诗意而激情地展现了成都新农村建设成就，希望能给观众带来有质感的认识。其实，不是我们拍了一部电影，而是这片土地上生活的激情让我们去发现、去呈现，我希望通过这部电影，让观众感受到生活的诗意和温暖。”</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">据介绍，《我的村民我的村》将进行3个月的后期制作，预计国庆节期间在全国上映。该片不仅是峨影集团继《天上的菊美》后，再次冲击电影高地的重磅影片，也是成都传媒集团积极探索拓宽宣传舆论阵地的一次突破性尝试。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;母女同演一个角色</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;</strong></span><span style=\"line-height: 2;\"><strong>岳红表示要“常回家看看”</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 金鸡影后岳红在《我的村民我的村》中扮演村支书杨丽英，这个角色以蒲江西来镇两河村党支部书记姚庆英为原型。在华表奖颁奖典礼上，岳红深情地向全国观众推荐这部电影：“这是一部反映农村新风貌的影片，讲述基层干部扎根故土，服务群众，建设‘业兴、家富、人和、村美’新农村家园的故事。”昨日发布会上，岳红感慨万端：“我是成都人，但离开家乡太久了。这次有机会回家乡拍戏，让我看到了成都巨大的变化，感受到当地人的淳朴善良，感受到这片土地的厚重，以及在这片土地上生活的人们的努力。”岳红动情地说，“我自己也是一名共产党员，我一边拍一边学，一边净化心灵，他们的点点滴滴，让我看到怎样才是一个高尚的人，这将影响我今后的创作和生活。”这部电影也让岳红母女留下美好记忆，岳红和她的女儿首次在电影中扮演同一角色，女儿扮演青年时代的杨丽英。岳红表示，今后将“常回家看看”，回两河村看望众乡亲，她也希望随着电影上映，让全国观众通过电影感受成都的幸福指数。(成都日报）</span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (21, '1532083521518', '话剧《承诺》巡演活动走进蒲江县 演绎两河村的故事', '话剧《承诺》巡演活动', '4', 'admin', '<p>&nbsp;</p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160705/1467688916.png\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\"></span><span style=\"color: rgb(51, 127, 229);\">话剧《承诺》巡演活动在蒲江县举行</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160705/1467698267.png\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">《承诺》演出现场</span><span style=\"color: rgb(51, 127, 229);\"></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160705/1467698035.png\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">《承诺》演出现场</span><span style=\"color: rgb(51, 127, 229);\"></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(81, 81, 81); text-transform: none; text-indent: 0px; letter-spacing: normal; font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; font-style: normal; font-weight: 400; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160705/1467691928.png\"/><br/></span></p><p><span style=\"line-height: 2;\"><span style=\"color: rgb(51, 127, 229);\">《承诺》演出现场</span><span style=\"color: rgb(51, 127, 229);\"></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 7月1日，由中共成都市委宣传部主办，成都市广播电视台、中共蒲江县委宣传部承办的“永远跟党走”成都市庆祝中国共产党建党95周年——话剧《承诺》巡演活动在蒲江县举行，演绎西来镇两河村的故事。市委外宣办副主任高永虹；县委书记、县长刘刚，县人大常委会主任罗超，县政协主席徐耘等县委、县人大、县政府、县政协领导；县法院院长、县检察院检察长，其他副县级领导，离退休正县级领导代表；驻蒲部队领导及部分官兵；各乡镇（街道）、村（社区）、县级各部门代表；劳模代表；优秀党员、党务工作者代表等集中观看。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 该原创话剧《承诺》由全国知名编剧王爰飞创作，全国知名话剧导演胡筱坪全程执导。本剧以蒲江县两河村党支书姚庆英带领全村建设“三基地一家园”为线索，通过种植改良柑橘和农民自建集中居住房两件导致两河村巨变的大事，生动展现了新时期基层党员干部在城乡统筹过程中扎根故土，信守共产党员承诺，与市场接轨与时代同行，为了新农村建设而努力的动人形象。全剧引入了独具川西特色的艺术元素，既轻松活泼、又感人至深，让人们领略到这片土地的亲切美丽。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;从今年4月话剧《承诺》首轮在成都公演，到此次再度提升、重装登台，引起社会各界广泛关注。演出现场掌声依旧、热度不减。据了解，本次演出活动是成都市庆祝中国共产党建党95周年系列活动重要组成部分，旨在庆祝中国共产党建党95周年之际，结合全市开展的“两学一做”学习教育，以群众喜闻乐见的艺术形式营造浓厚的庆祝氛围，弘扬主旋律，宣传和培育身边的先进典型，进一步发挥先进典型对社会的引领和示范作用。（来源：蒲江县文明办）</span></p><p></p>');
INSERT INTO `article` VALUES (22, '1532151153601', '全国“最美家庭”评选揭晓 成都3户家庭入选', '最美家庭评选', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span>　</p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 14日，记者从市文明办获悉，在日前揭晓的2016年全国“最美家庭”评选中，成都王宇家庭、杨海平家庭、杨德力家庭当选全国“最美家庭”，成都获评全国“最美家庭”户数位列中西部副省级城市第一。记者了解到，这3户家庭均来自2016年成都市“最美家庭”。此前，在由市委宣传部、市文明办、市网信办、市妇联联合开展的2016年成都市“最美家庭”评选活动中，共评选出100户“最美家庭”和63户“最美家庭”提名奖。</span></p><p style=\"margin: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 2016年全国“最美家庭”名单</strong></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160616/1466068782.jpg\"/><br/></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"color: rgb(0, 51, 153); line-height: 2;\"></span><span style=\"color: rgb(0, 51, 153); line-height: 2;\">延续2800多年的“四足”“四重”家风家训的杨德力家庭</span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160616/1466071450.jpg\"/><br/></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><span style=\"color: rgb(0, 51, 153);\"></span><span style=\"color: rgb(0, 51, 153);\">夫妻携力投身公益事业的杨海平家庭</span></span><span style=\"color: rgb(0, 51, 153); line-height: 2;\"></span></span></p><p style=\"margin: 0px; padding: 0px; text-align: center;\"><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"><img style=\"margin: 0px; border: 0px currentColor; border-image: none; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160616/1466071315.jpg\"/><br/></span></span></p><p><span style=\"line-height: 1.5;\"><span style=\"color: rgb(0, 51, 153); line-height: 2;\">放弃晋升机会照顾岳父的王宇家庭</span><span style=\"line-height: 2;\"></span></span></p><p><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;6户最具正能量的“最美家庭” 当选四川省和全国“最美家庭”</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “‘家庭是社会的基本细胞，是人生的第一所学校’，良好的家风家教对传承中华民族家庭概念有着积极影响”。市文明办相关负责人表示，于今年3月4日正式启动的“最美家庭”评选活动是群众性精神文明创建活动的重要组成部分，旨在通过宣扬文明和谐、互助友爱的家庭文明理念，从而达到“以家风连成民风，民风相融汇国风。”</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;据市妇联相关负责人介绍，本次评选活动采取组织推荐、媒体推荐、社会推荐、家庭互推自荐等多种方式进行推荐，各区（市）县在经过群众推荐、群众评议后产生本地本单位的“最美家庭”，并将评选名单进行公示。4月14日，2016年成都市“最美家庭”评选活动落幕。市文明办、市妇联根据2015年、2016年成都市“最美家庭”基本资料和家庭事迹，从中挑选出了6户最具代表性、最具正能量的“最美家庭”参与到四川省“最美家庭”和全国“最美家庭”的评选。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 在人民大会堂举行的全国“最美家庭”揭晓仪式上，放弃晋升机会照顾岳父的王宇家庭，夫妻携力投身公益事业的杨海平家庭，延续2800多年的“四足”“四重”家风家训的杨德力家庭3户家庭当选全国“最美家庭”。成都凭借3户全国“最美家庭”数量，位居中西部副省级城市第一。同时，我市王莉坤家庭、段丽丽家庭、吴云家庭荣获四川省“最美家庭”称号。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\"><strong>&nbsp; &nbsp; &nbsp; 市民积极参与评选“最美家庭” 网络投票数达337.11万</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;本次“最美家庭”评选活动自3月启动以来就受到广大市民的高度关注和热情参与。据统计数据显示，3月25日成都市寻找“最美家庭”活动网络专题上线以来，截至4月14日24时，专题访问量达80余万次，公示的163户“最美家庭”候选家庭总投票数达337.11万，超过2万票的家庭达41户，总点赞数达6.2万余次。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 除了通过网络为“最美家庭”投票外，本次活动还邀请了专家评审团与人大代表、往届“最美家庭”代表、媒体代表组成的大众评审团进行了现场投票。“最终结果采取专家评审、大众评议、网民投票相结合的方式，按7∶3∶4的比例评选出100户‘最美家庭’和63户‘最美家庭’提名。”市妇联相关负责人表示，此次评选出来的“最美家庭”类型涵盖了“夫妻恩爱型”、“尊老爱幼型”、“廉洁文明型”、“邻里互助型”、“爱岗敬业型”、“诚信创业型”、“热心公益型”、“拥军爱军型”、“书香家庭型”、“家庭和睦型”、“创新创业型”等11个类型。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;记者从市文明办、市妇联还了解到，本次活动将通过“好家风 好家训”家庭文化活动风采大赛、“最美家庭”故事分享会、制作“最美家庭”宣传集等方式持续展示“最美家庭”风采。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “最美家庭”美在何处？网友质朴而充满感动的点评</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;“公益理念纯正，实实在在做事，真真切切服务，一个中国式的有爱心家庭的典范”</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “继承和发扬中华民族传统的优良家风和家训，引导社会家庭的和谐，这种传承让人感动”</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; “他们的相亲，相爱，他们的不易，值得我们学习”</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;……</span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\"></span><span style=\"color: rgb(229, 51, 51);\">2016年四川省“最美家庭”名单（3户）</span></strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 王莉坤 段丽丽 吴云</span></span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\">2016年成都市“最美家庭”名单（100户）</span></strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;锦江区 罗萍 马新生</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 青羊区 蔡胜 林科夫 李毅 申文皓 白晓玲 刘畅</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;金牛区 蒋静</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 武侯区 李国育 曾方武</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 成华区 王莉坤 巨红琳 &nbsp; 郑丽 张明辉</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;高新区 肖永福</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 龙泉驿区 陈学良 赖文忠</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;青白江区 庞玉康 钟城 郭倩 曹春全 刘强 唐润 向勇</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 新都区 张仕贵 马映霞 孙家银 杜春娃 杨德力 唐成华</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;温江区 袁忠群 何定镛 帅红 江伟 赵宝辉</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 都江堰市 张丽 袁巧 邹光琴</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 彭州市 米道会 王一词 强华琼 杨功丽</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;邛崃市 卢发勋 朱建军 寇显莲 黄玉芬 邓家玉</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 崇州市 周艳梅 杨加真 张世昌 李永强 李碧辉</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;金堂县 周云霞 王雪莲 蒋水英 陈婷 陈绍林 郑升贵</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 双流区 曾阳 苏丹 李建 刘娟 康银 周艳飞 朱军 张新军</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;郫 县 李天清 漆素君</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 大邑县 李正华 顾兴庆 王槐亮 何菊芬 李琼英</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 蒲江县 杨建 高魁明</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;新津县 罗凌云 万光华 葛川新 戴德安 王开香 赵新越 万术成</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 市总工会 王宏敏 柳倩 李成文 陈江明 王丽 李群 代和英 邓学玲</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;市直机关工委 苟丽华 柴海波 童勤 曾礼英</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 武警成都支队 王宇 陈业</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;地铁公司 陈晶晶</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 社会组织 袁杰 杨海平</span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\">2016年成都市“最美家庭”提名奖名单（63户）</span></strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 锦江区 许秀茹 傅宗序 卿烈贵 李丽</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;金牛区 董学蓉 姜 红 李 龙 卿凤文 邓训英</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 成华区 蒲 静 何艳敏 黄德富 曹凤群</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;高新区 马维棋 付可成 周晋芬 魏木香 廖昌荣</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 龙泉驿区 廖新燕 周礼琼 钟贵琼 张旭东</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;青白江区 李 华 李 芳</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 新都区 艾则璇</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 温江区 李素莲 刘大明 薛万海 魏 芳 黄美琳 姚 兰</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;都江堰市 文德丰 吴 斌 孙 毅</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 彭州市 张坤万 刘芳德</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;邛崃市 徐茂群</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 崇州市 刘 尧 胡安均</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;双流区 王璞 李雪梅 白洪志 罗燕 张运英 林灵 葛建荣 耿学燕 杨世英</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 郫 县 李敏 李术林 谢燕 曾秀芬</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 大邑县 王绍松 蒙仕英 龚丽</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;蒲江县 杨国君 徐兴福 姜德超 杨志玉</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 社会组织 杨晓清 段永秀</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;火车支队 张智鹏</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\"></span><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 地铁公司 王尹</span></span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (23, '1532151168978', '第四届成都市道德模范评选活动启动', '投票启动', '4', 'admin', '<p>&nbsp;</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span>　</p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;道德模范是社会主义核心价值观的人格化身，是民族精神的生动写照，是引领社会主流价值的鲜明旗帜。20日，市委宣传部、市文明办、市人社局、成都警备区政治部、市总工会、团市委、市妇联七个部门发出《关于开展第四届成都市道德模范评选活动的通知》，启动第四届成都市道德模范评选系列活动，部署在全市大力选树和学习宣传道德模范，加强思想道德建设，为顺利实现成都“十三五”发展目标、加快建设国际化大都市凝聚起强大的精神力量和有力的道德支撑。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2011年以来，市委宣传部、市文明办等相关部门已经评选三届成都市道德模范，在全社会兴起了学习道德模范、崇尚道德模范、争当道德模范的热潮，促进公民道德素质和社会文明程度不断提高。第四届成都市道德模范评选活动将坚持高标准评选，广泛发动城乡基层群众推荐候选人，在全社会树立值得景仰和学习的楷模，推动公民道德建设深入发展。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;两种方式推荐候选人</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 本次评选活动除了组织择优推荐，市民可通过网络推荐、热线推荐的方式，推荐候选人。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong>网上推荐：</strong></span><span style=\"line-height: 2;\">发送候选人事迹至</span><span style=\"line-height: 2;\">cdby61880591@126.com</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\"><strong>热线推荐：</strong></span></span><span style=\"line-height: 2;\">拨打成都文明热线96110、</span><span style=\"line-height: 2;\">成都雷锋热线81234567</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\"><strong>可以推荐的对象：</strong></span></span><span style=\"line-height: 2;\">凡在成都市工作、学习、生活的中国公民，或在外地工作、学习、生活的成都籍市民，不分性别、民族、职业，均可被推荐为成都市道德模范候选人。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">注意：每人只参加一个类别的评选。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;7月至9月初审 10月出结果</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 评选启动后，7月至9月，活动评委会会对上报的推荐对象进行初审，评选出80名候选人并在媒体公布，接受市民群众网络点赞和公众代表投票评议，并综合网络点赞、公众代表投票、评委投票结果，评选出50名拟表彰的第四届成都市道德模范。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">公示之后，预计10月揭晓结果。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 评选5类模范 每类选10名</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">第四届成都市道德模范评选分为“助人为乐模范”“见义勇为模范”“诚实守信模范”“敬业奉献模范”“孝老爱亲模范”5类，每类评选10名左右。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 快来看看这些评选标准，推荐你身边曾给予你感动、震撼、启示、帮助等等的模范们。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp;&nbsp;<strong>&nbsp;&nbsp;</strong></span><span style=\"line-height: 2;\"><strong>“助人为乐模范”</strong></span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 长期帮扶他人，对遭遇不幸或遭受灾害者奉献爱心，积极参与社会公益事业和公益活动等。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;“见义勇为模范”</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">在公民合法权益受到侵害时挺身而出；勇于同正在实施的违法犯罪行为作斗争；在抢险救灾中，奋力排除险情等。</span></span></p><p><span style=\"line-height: 2;\">&nbsp;&nbsp;<strong>&nbsp; &nbsp; “诚实守信模范”</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">从事生产活动坚持质量至上，从事经营活动坚持信守契约；在人际交往中，坚持信守承诺等。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp;&nbsp;<strong>&nbsp; “敬业奉献模范”</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">立足岗位，刻苦钻研，有重要发明创造或重大贡献；长期在艰苦条件下尽职尽责、默默奉献，办事公道、服务优质等。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;“孝老爱亲模范”</strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 模范践行家庭美德，孝敬父母；关爱子女，夫妻和睦；在家人亲属有伤病、残疾等困难情况下，不离不弃等。</span></p><p><br/></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp;<strong>&nbsp;&nbsp;&nbsp;</strong><strong>我们为什么寻找道德模范</strong></span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">入夏，成都东风渠水位上涨，水流湍急，58岁的吴永秀带着“义务阻泳队”，早上8点便开始在河边巡逻，劝导人们远离危险；对于高新供电公司的刘源来说，夏日同样忙碌，这位党员服务队队长，24小时待命，上门为群众解决用电问题，夏夜里被叫醒抢修对他来说是家常便饭……也许，人群里，你无法一眼认出这些道德模范的身影，但是，我们这座城市却因他们的小善大爱、美德修养，而温暖动人，而欣然向上。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 20日，第四届成都市道德模范评选活动启动，我们渴望找到平凡名字下那些朴素的坚守，渴望找到普通面容下那些可贵的品行。对于3000年历史的成都，对于“新常态、万亿级，再出发”的成都，我们渴望让更多道德模范引领人们前行，汇聚成这座城市更有力的精神力量。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">道德模范是中华传统美德的践行者。从“苟利国家 不求富贵”，到“天行健 君子以自强不息”；从“轻诺必寡信”，到“君子莫大乎与人为善”，中华民族始终沿袭着爱国、自强、诚信、友善等美德，积淀成我们共同的价值认同和民族自豪，支撑着中华民族生生不息、薪火相传。如今，在道德模范身上，我们看到美德在当下的生动诠释，我们寻找道德模范，传承的是民族的精神底色。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 道德模范是良好社会风尚的助力者。成都人见贤思齐，道德模范不孤单。去年，50多人通过“口碑调查”加入“吴大姐爱心救助队”，与她一起义务阻泳、维护环境、调解纠纷、帮贫扶困。80后道德模范秦坤发起成都市首届普特共融·乐享棒球联赛，聋人小朋友和普通孩子同场竞技，酣畅淋漓。而这背后，是300多名志愿者加入秦坤团队、长时间陪伴聋人孩子、不断推广手语的努力汗水……在成都，道德模范引领时代风尚，道德模范的榜样力量正转化为无数市民的亲身实践。小善聚大爱，溪流成江海。我们寻找道德模范、学习道德模范，这个过程，正是文明向上的社会风尚、崇德向善的社会氛围形成的过程。</span></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 2;\">道德模范是人们精神世界的指引者。“镜子明则尘埃不染，智明则邪恶不生”，明辨是非，才能不染尘垢，道德模范的行为准则宛如明镜，给予人们明晰的生活提示。毫无血缘关系的邻居瘫痪，是否去照顾？路遇他人车祸，是否停车而救？利益面前，是否坚持用良心做事？艰辛的工作中，是否能做到每一个数据的准确？人生起伏，难免困惑，是非、善恶、美丑常以混淆之态呈现。所幸，道德模范们的助人为乐、见义勇为、诚实守信、敬业奉献、孝老爱亲，给予了我们最直观的答案和借鉴。我们寻找道德模范，愿他们的选择指引我们找到至善至美至纯的人生。</span></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 全国道德模范2名，全国道德模范提名奖10名；四川省级道德模范23名；成都市道德模范146名……今年，在第四届成都市道德模范评选活动后，这份成都的道德模范名单将再增50人。我们评选道德模范，这是对凡人善举的褒奖，是对人间大爱的致敬，他们的故事随着评选活动传播久远，激起更多市民的共鸣，形成全社会正能量的合唱。而对道德模范的一系列礼遇和关怀，培育起成都厚德与扬善的土壤，让好人不断涌现，文明蔚然成风。当前，成都正朝着建设国家中心城市和国际化大都市的目标奋力前进，通过道德模范唤起和凝聚起的道德力量和城市精神，将春风化雨，将润物无声，成为城市发展强大的支撑和动力。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 我们寻找道德模范，为他们喝彩；我们寻找道德模范，与他们同行！</span></p><p>&nbsp;</p>');
INSERT INTO `article` VALUES (35, '1532156351445', '成都7名“向上向善好青年”事迹展示', '事迹展示', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 崇义友善类</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;<span style=\"color: rgb(0, 0, 0);\"> 武侯区</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">杨文毅 （区交通与市政设施管理局职工）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 帮扶贫困山区师生 一干就是6年</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;过去6年，武侯区交通与市政设施管理局交通运输管理所的杨文毅基本上没有属于自己的业余时间。41岁的他把自己的业余时间，都用来帮扶四川贫困山区的师生，用实际行动去影响和带动身边的人，让更多的人不断地加入到社会公益事业中。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 杨文毅的爱心之旅，源于2001年在电视上看到成都一所民办残疾学校资金困难。他多次从微薄的收入中，定期拿出一部分钱帮助这所学校的学生。从2009年开始，杨文毅与西华大学教师杨晓龙等13人发起成立了“爱心背包”民间公益团队，从此开始了系统、长期性的针对四川巴中南江、凉山州和阿坝州贫困山区小学师生的助学帮扶活动。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 6年时间里，杨文毅与“爱心背包”团队的队员们一起，利用业余时间源源不断为南江、甘洛县和阿坝县求吉玛乡等最为偏远的贫困山区村小（含教学点）师生组织购买并送去了近百万元的各种助学物资。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;2013年年底，“爱心背包”在央视举办的“温暖中国 身边好人”评选活动中，成为唯一一个获此殊荣的民间公益团队。从去年下半年开始，“爱心背包”开始启动“一对一帮扶”和众筹项目，前者对急需帮助的师生提供更深入的帮助，后者则能调动团队内外的资源，筹钱购买贫困师生所需物资，又多了一种帮助贫困师生的渠道。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 孝老爱亲类</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: rgb(0, 0, 0);\">成都大学</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 严柳 （工业制造学院2013级学生）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">90后志愿者 公益路上找到“婆孙情”</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;</span><span style=\"line-height: 28px;\"> 义卖、环境维护、无偿献血……从2013年进入成都大学工业制造学院开始，1994年出生的严柳就加入了学校青年志愿者协会工业分会，将热情投入到各类志愿服务活动中。在志愿服务中，他找到了未曾体验的特殊“婆孙情”</span><span style=\"line-height: 28px;\">。</span></p><p><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2013年10月，严柳在城东康疗院陪老人聊天时，认识了一位邓奶奶。此后，他保持着每两周就去陪对方一下午的习惯。在严柳看来，从相识到现在，他们在彼此的生命里留下了深刻的记忆，他和邓奶奶之间，早已有了“婆孙情”，“作为新时代的学生，关爱身边的老人，我们义不容辞。”</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 除了自己做志愿服务，严柳还会随时随地给身边的人传递作为志愿者的幸福。他不仅将每次活动的所见所闻、感受等发布在朋友圈，还在学校举行的演讲比赛中，号召更多同学去关爱老人。学校青年志愿者协会里，有很多同学就是在听完他的演讲后加入的，“我的大学仍在继续，我的人生仍在继续，那么我的志愿活动就会永不停歇。”</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 新津县</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">张天欢 （兴义镇杨牌村村民）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">80后孝媳的双肩 为公婆撑起一片天</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 丈夫因车祸离开人世，她用自己柔弱的双肩，为公婆撑起了一个温暖的家……这是在新津县兴义镇杨牌村，村民都知道的80后孝媳张天欢的故事。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2004年，张天欢与杨牌村村民刘科结合了。婆婆患有严重的类风湿，肢体行动不便，公公眼睛耳朵也不好，右脚还略有残疾。由于丈夫在外工作辛劳，张天欢便心甘情愿地在家担负起了照顾公婆的责任。2008年，刘科遭遇车祸离世，尽管家人再三劝说让22岁的张天欢回娘家，可她一直放不下公婆，“我走了，公公婆婆怎么办？”于是，她决定留下来继续照顾二老。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;60多岁的公公刘伙清说：“每次提到改嫁的事，她都拒绝了，还说要嫁都要把我们带到一起去嫁……”婆婆陈玉芳也早把这份婆媳情转化成了母女亲情，不希望年轻又漂亮的儿媳因为他们两个老人，耽误美好的生活。张天欢却很坚持：“这是我应尽的义务……”</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 创业创优类</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: rgb(0, 0, 0);\">邛崃市</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 李伟 （长腰山养殖专业合作社负责人）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 打不倒的小伙 带乡亲搞养殖致富</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在邛崃市临邛镇盘陀村，只要提起李伟，人人都会竖起大拇指。他21岁回乡创业养山鸡，在遭遇一次次挫败后，终于探索出“生态土鸡、土鸭、珍禽”种养循环的生态模式。5年后的今天，他已是养殖专业合作社负责人，带着村民搞养殖致富。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2010年，在双流一家电子厂上班的李伟，带着自己的梦想和微薄的打工积蓄回乡创业。在父母的帮助下，他把自家的院坝开辟出来，建了简易鸡舍。引进的800只山鸡苗，20天就死了近半，他一个人查阅资料、请教养殖达人。剩下的一半山鸡终于长大了，本想孵化鸡苗出售，不幸又遭遇禽流感。更想不到的是，一场暴雨将围网冲垮，山鸡全跑了……李伟欠下几万元债，只好外出打工，又因手指头被卷进齿轮，造成九级伤残。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;一连串不幸，却没有打垮这位年轻人。在家养伤期间，他顶着家人朋友的劝阻和旁人的嘲笑，继续搞养殖。有了之前的教训，加上慢慢学习摸索，李伟终于成功了。短短一年，不仅还清了几万元的债务，养殖场也从一个不足1亩的院坝，发展成为面积达10余亩的养殖场。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 周围村民纷纷上门取经，李伟总会不厌其烦地为村民讲解。“大家一窝蜂地盲目发展种养业，会造成很多问题。”李伟说，不能等问题出现了再解决，让村民白白蒙受损失。于是，他开始引导村民，合理分流，并在2014年建立起了长腰山合作社，不仅无偿向外村村民提供技术咨询，还定期进行市场调研，合理分配现有种养资源，帮助村民销售自家的生态土鸡、鸭、猪、柑橘、花生等农产品。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 诚实守信类</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;<span style=\"color: rgb(0, 0, 0);\"> 青白江区</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">曾金才（区人社局公务员与工资退休科科长）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 全力兑现退休人员福利 从不对群众发脾气</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;32岁的曾金才在人事岗位上，已经工作了6年多。为了处理好各种繁琐的人事工作，他精通各类人事政策，都是从群众的角度出发，为群众办实事。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 工作中，曾金才不仅严格执行政策，还敢于为广大职工争取合法权益。最典型的例子是2012年，龙王镇退休事业单位人员李盛全去世，其家属只在区社保局领取了李盛全生前1个月基本退休费标准的抚恤金。按照国家政策，事业单位职工去世后抚恤金和丧葬费标准为本人生前25个月基本工资或基本退休费，李盛全剩余24个月抚恤金和丧葬费迟迟未能兑现。其家属多次到相关部门反映，均未能得到解决。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 曾金才认为，不应该只考虑解决李盛全的个别问题，应该从面上解决该类问题。于是，在尽快解决李盛全个案的同时，他草拟了《关于建议解决乡镇事业单位退休人员相关待遇的说明》，多次到区财政局沟通协商。在反复努力和争取下，全区乡镇事业单位退休人员相关待遇得到了保障，李盛全家属也按规定足额领取了丧葬费和抚恤金。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;曾金才还是出了名的“好脾气”，对待办事群众，他没有发过一次脾气，没有说过一句重话。无论群众反映的诉求是否符合政策规定，他都耐心细致地解释，并为群众提供充分的政策和文件依据，让办事群众理解、放心。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 爱岗敬业类</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">青羊区</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">袁罗群 （区特殊教育中心教师）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 为了特殊儿童 千千万万遍不厌倦</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;袁罗群26岁，2011年8月，她进入青羊区特殊教育中心，担任班主任。工作没多久，她便发现孩子们的教材都不太适合他们，便决定自己为孩子们重新编一本教材。她根据学生的特点，再联系大学所学的专业课程，花了2个多月编写出的新教材，更能够让学生缺陷补偿、潜能开发，还使教学的内容能够根据孩子的掌握情况来灵活调整。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 面对特殊的孩子们，时常需要重复课堂常规教学，每日反复的叮嘱陪护，袁罗群很有耐心。她以平等且包容的心态来对待孩子们，针对每个孩子的特殊情况，创造各式的学习任务，鼓励他们参与、尝试。“让每个孩子都能在活动中获得成功的体验，有助于增强他们的自信心与勇气。”袁罗群说。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 特殊儿童比一般的孩子需要更多的关爱，除了学校，家长的教育和关怀也非常重要。袁罗群时常和家长交流，帮助家长建立积极乐观的心理，帮助家长对孩子进行教育康复训练……面对特殊儿童因理解、记忆、空间能力等方面的不足，需要老师反复教授的挫败感，袁罗群一直坚定着“为你，千千万万遍”的信念。</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;青白江区</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;<span style=\"color: rgb(0, 0, 0);\"> 薛常华 （祥福农业综合服务站兽医师）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">当兽医师的妹子 用技术带领农民致富</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 薛常华的工作总是繁重，她虽然平日里话不多，却总以微笑待人。2007年，她被调到农业综合服务站上班，工作非常繁琐，涵盖了畜牧、农业机械、农村建设、农村经济运行、农产品质量安全等方面。由于站上工作人员少，她不仅承担了畜牧工作，还必须面对其他几项工作。在全新的工作岗位上，除了熟悉的畜牧工作，其他工作她一窍不通。怎么办？她经常拿着笔记本，穿梭在各个办公室虚心请教老同志，并查阅大量专业书籍，学政策、专业务；她勤下基层，收集归纳整理农民群众在实际生产中的工作经验和好做法；工作之余，她加强专业技术学习，并于2012年6月取得了西昌学院动物医学专业的本科文凭。</span> </p><p><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在综合服务站工作的6年多时间里，她常年奔波于各个养殖场，帮助他们建立养殖档案，规范养殖业安全生产，解决畜禽养殖场污染治理难题；先后参与成都市马坪蔬菜种植专业合作社等单位无公害蔬菜认证8个，绿色食品认证4个；推广“芹菜-芹菜-水稻”种植模式，使每亩土地收益达“千斤粮万元钱”，积极试验并推广旱育秧、优化定抛、杂糯间栽、秸秆还田、配方施肥等新技术；作为技术指导员，在2012年、2013年基层农技推广体系改革与建设项目（养殖业）中，共指导科技示范户20户，入户指导200多次，开展6场技术培训，辐射带动220余户。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 易启明 周维 实习生 易欣雨 图据本人</span></p><p><br/></p>');
INSERT INTO `article` VALUES (36, '1532156488063', '成都5名“乡村好青年”事迹展示', '事迹展示', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			<span style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 金堂县</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(229, 51, 51);\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"line-height: 28px;\"></span><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">段丽丽</span></span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"line-height: 28px;\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">（金满堂农业开发</span></span><span style=\"color: rgb(0, 0, 0); line-height: 28px;\">有限公司总经理）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0);\"><strong><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0); line-height: 28px;\">&nbsp; &nbsp; &nbsp; 80后女博士 带领乡</span><span style=\"color: rgb(0, 0, 0); line-height: 28px;\">亲增收致富</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 段丽丽是毕业于四川农业大学的80后女博士，她放弃了轻松的事业单位工作，婉拒了在高校任教的优厚条件，和朋友一起发展现代农业，带领乡亲增收致富。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;2009年拿到博士学位后不久，成都一家事业单位将段丽丽录用。新工作简单轻松，可她觉得这并不是她想要的。2011年6月，她和朋友一起注册成立了成都金满堂农业开发有限公司，在金堂县官仓镇建起了千亩现代产业园区，积极向当地农民推广绿色有机种植。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2012年3月，云南大学一个分院向她递来“橄榄枝”，希望她去任教，并承诺提供20万元的安家费和副教授职称，但她婉拒，并在当年成立了“金满仓果蔬专业合作社”，传授农民先进栽培技术，帮他们建钢架大棚，并力所能及地提供资金扶持……如今，其现代农业产业园不仅为当地农户起到了示范带头作用，还带动了周边600户农户生产致富，也为大学生提供了学习就业的好去处。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;崇州市</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">周维松</span></span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"></span><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">（市农村发展局崇阳片区站站长）</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 田间地头的CEO</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"></span><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 农民的“职业经理人”</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;从前的崇州市隆兴镇，村里年轻人都外出打工，春耕时无人种田；如今，村里的土地不仅不再“撂荒”，在种田“职业经理人”的带动下，年年丰收，户户增收。周维松就是其中一位“职业经理人”。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 如何当好农业职业经理人，周维松认为关键是做好“怎样种田”。在合作社，他对传统种田的方式进行了“变革”。合作社的社员和理事会只需要明确“种什么”就行，“怎么种”全由周维松来操作，理事会和监事会负责监督种田过程。在生产过程中，他实现了种子、农药、肥料的三统购和机播、机耕、机插秧、机防、机收的全程机械化，每亩可降低成本100余元。通过运用先进农业技术，2013年亩产比未入社农户高近75公斤。2010年，这里只有1个合作社，入社面积101.27亩。如今，已组建土地股份合作社361个，入社面积21.33万亩，入社农户9.46万户。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;此外，周维松还参与搭建了当地人才培养、人才管理、人才发展、政策扶持四大平台，创建了“职业经理人”和“职业农民”双培训体系，培养了“职业经理人”1460人。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 大邑县</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: rgb(0, 0, 0);\">陈飞</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; （铁溪人家大闸蟹养殖专业合作社理事长）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 把大闸蟹养出名</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">带动农户增收</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2009年从四川农业大学毕业后，陈飞作为一名西部志愿者，选择去大邑县董场镇龙兴社区，并在大邑县扎根、创业，成立铁溪人家大闸蟹养殖专业合作社，带领这里的村民致富。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;2010年7月，她在董场镇龙兴社区创办了大邑县高井竹编制品厂，但因经验不足停产。随后，她通过远程教育学习，学习了不少农村种养殖技术。2012年5月，在团县委、当地政府的帮扶下，她成立了如今的铁溪人家大闸蟹养殖专业合作社，养殖大闸蟹、丁桂鱼、鲤鱼、鲫鱼。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 经过几年的发展，铁溪大闸蟹已闻名于成都、绵阳等地区，每年9月至11月吸引成都周边近万名市民前往体验、垂钓；占地规模也从20余亩发展到现在的150亩。大闸蟹养殖基地的发展不仅让当地农民通过流转土地有了经济保障，还带动周边群众就业及创业，解决周边50余个劳动力的就业问题，农民每年每人可增加收入1.5万元以上；带动周边农户发展其他配套项目，每户每年增加收入近万元。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;青白江区</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">&nbsp; 唐润</span></span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; （大弯街道肖家院社区组织员）</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">地震时得到过帮助</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 如今用爱回报社会</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;汶川特大地震发生后，唐润所在的都江堰向峨乡受灾严重，他和妻子带着刚出生不久的儿子，被安排到了青白江安置点。当时，唐润的妻子还在月子中,婴儿因奶水不够常常“哇哇”大哭，志愿者常常买奶粉等营养品来看望他们。从那时起，唐润就和妻子决定，在青白江安定下来，用自己的实际行动回报社会。在当地政府的支持、帮助下，唐润办起了“成都瑞洁水处理有限公司”。创业之初，唐润和妻子起早贪黑、诚信经营，很快使公司走上正轨，不仅解决了一家人的生计，也带动了一部分人就业。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 随后，唐润和妻子加入“成都义工”组织，坚持到青白江各敬老院、留守儿童集中的乡村和区特残教育学校，参加敬老、助残、爱幼、助学等活动，至今已坚持6年。除了经常参与捐款、义务劳动等活动，唐润还组建起文艺志愿者小分队，一次次在关爱敬老院老人和留守儿童、残疾孩子的活动中，赢得老人和孩子的掌声，为他们带去欢乐。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;新津县</span></strong></span> </p><p><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; <span style=\"color: rgb(0, 0, 0);\">杨杰</span></span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; （供电公司花桥所技术员）</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"color: rgb(0, 0, 0); line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\"></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(0, 0, 0);\">困难户缴不起电费</span></span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 0, 0);\">&nbsp; &nbsp; &nbsp; 他都会悄悄垫付</span></strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;穿行在乡村的田间地头，挎着十几斤重的工具包挨家挨户抄表，平均每天徒步10公里以上；不仅做好巡视、检修和维护工作，还为困难群众提供力所能及的帮助……杨杰，2002年进入新津供电公司花桥供电所，后被当地村民亲切地称作“最美抄表哥”。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2003年9月，花源镇共和村一位70多岁的老人因病花光了家里的积蓄，杨杰从当月起，每月为老人缴清电费，直至老人去世。此后，只要看到有困难户缴不起电费，他都会悄悄为对方垫付。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;芦山地震后，听说单位要组织农电应急抢险队前往芦山抢修电网，正在住院的杨杰便向领导请战。虽然领导不让他出征，但他还是提前办了出院手续，第二天就随队奔赴芦山抢修电网。在灾区的那段时间，他常常困了在车上打盹，饿了吃方便面，昼夜值守在保电一线。杨杰说，他只是一个再平凡不过的人，只希望社会上能有更多的热心人，用平凡的行动，为需要帮助的人送去温暖。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;成都晚报记者 易启明 </span><span style=\"line-height: 28px;\">实习生 易欣雨 </span><span style=\"line-height: 28px;\">图据受访者</span></p><p><br/></p>');
INSERT INTO `article` VALUES (37, '1532156586450', '“2015成都榜样”主题活动8月人物榜出炉', '人物榜单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			&nbsp; &nbsp; &nbsp;<span style=\"line-height: 28px;\"> 学习榜样先进事迹，传递向上、向善正能量。昨日，记者从成都市文明办获悉，“8月成都榜样名单”正式出炉。其中，包括第二届全国道德模范徐茂、第五届全国道德模范候选人芮银超在内的榜样共计11人。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 入伍26年来，先后维护过6种机型的直升机，芮银超熟练掌握500多类直升机“疑难杂症”的诊治办法，成为我军唯一参与直升机故障分析手册编撰、新型直升机定型研究的“士兵专家”；坚持了六年志愿服务，郝成桃被称为“铁杆志愿者”。 2014年末，郝成桃因病去世，按照她生前的意愿，她的器官被捐献，挽救了7个人的生命……在8月的榜单人物中，这样的故事还有很多，他们从容面对生活中的困难与挑战，用汗水与泪水书写生命的意义，彰显出榜样的力量。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;<strong> 8月成都榜样名单</strong></span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 芮银超</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 中国人民解放军77120部队特设师芮银超，他是全军闻名的“兵专家”，是飞行员心中的“定海神针”。他先后维护过6种机型，熟练掌握500多类直升机“疑难杂症”的诊治办法，独立和参与完成了16项技术革新成果，是我军唯一参与直升机故障分析手册编撰、新型直升机定型研究的士兵专家。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 牟红强</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">青白江绿杏餐饮公司的董事长牟红强，18年来打造出“百姓信赖，守信如一”的青白江区知名餐饮品牌——绿杏餐饮。他严把餐饮用料质量关，出现问题厨师长和加工厨师停职反省，取消年终考核。绿杏餐饮坚持数年如一日地在菜品原材料上真材实料，在菜品烹饪上原汁原味，在经营服务中坚持童叟无欺，赢得了消费者的青睐。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 阿效诚</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 崇州市第二人民医院的名誉院长阿效诚，是全国卫生模范。他被病人亲切地称为老父亲，也被医院内部推崇为骨科之父。他今年已经78岁高龄，但他却仍然坚持工作。“我就是闲不住，不做手术、不看病人了我会不习惯的。”一句简单的话语，道出的是这个有着51年从医经验的老医生的坚持。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">杨高鉴</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 大邑县城市管理局环卫工人杨高鉴，坚守环卫工作一线14年，不管冬寒夏暑，总是每天开着清运垃圾车穿行在大街小巷。十多年来，他在这个平凡的岗位上竭尽所能地发光、发热，并赢得省级环卫行业“先进个人”、“四川省劳动模范”、市级“十佳”环卫标兵、成都市优秀党员多个荣誉。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">刘健鸣</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 温江区邮电局退休职工刘健鸣，10年前，曾经照顾过他的保姆高玉清因突发脑溢血而半身瘫痪，他把高玉清接回同住，每天早上都要给她穿衣服、梳头发，照顾她的起居、吃喝拉撒。如今，高玉清年事已高，听力严重下降，动作也不再利索，刘健鸣给她买了助听器、新式轮椅，像对待自己亲生母亲一样细心照顾着这位老人。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 郝成桃</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">武侯区志愿者郝成桃，坚持了六年志愿服务，被大家亲切称呼“桃子”。2014年末，桃子因脑动脉瘤破裂抢救无效去世，按照她生前的意愿，她的器官被捐献，挽救了7个人的生命。在桃子走了后，她的丈夫继续着她的志愿服务，并设立了“桃子”爱心基金，传承桃子的志愿精神，帮助更多人。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 拉孟</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">成都拉孟绘画艺术交流中心创始人拉孟，是藏族唐卡噶玛嘎孜画派项目省级代表性传承人。7年来，他用自己的绘画收入，无偿资助40余孩子学习藏文和绘画。除了学习绘画外，拉孟还给孩子们安排了藏文学习课，组织孩子成立了民族团结歌舞队、民族团结志愿者队伍、少数民族联络服务站。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 徐茂</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">电子科技大学教师徐茂，曾被表彰为第二届全国道德模范。获得荣誉后，他并没有停止公益和志愿服务活动。2013年徐茂放弃了企业年薪聘请，来到电子科技大学成为一名教育工作者。他团结和带领自己的同事、学生加入抗震救灾、助学济困、公益宣讲等各种志愿服务活动。累计帮助山区贫困儿童1200多人，个人捐款30多万元，募集善款和物资超过200万元。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 黄健能</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 就职于成都明湖农业科技有限公司的黄健能，曾担任69010部队警卫班班长。在部队服役期间，黄健能同志平时刻苦训练、执勤冲锋在前，多次立功受奖并光荣负伤。2011年退伍返乡后在成都明湖农业科技有限公司从事行政管理工作。2014年12月8日，他奋不顾身冲入火海，救出患有老年痴呆症的老人。后又组织带领社区居民及时控制火势、消灭明火，将火灾损失降到了最低。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">姚庆英</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 蒲江县西来镇两河村支部书记姚庆英，荣获2015年“全国劳动模范”光荣称号。她把带领群众增收致富作为村支两委全部工作的重中之重。她坚持先进理念引领，先后多次组织村社党员干部和村民代表到郫县、都江堰等地参观学习现代农业发展先进模式，切实转变村民传统种植观念，带领全村居民致富。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">王琮</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都市青羊区府南街道石人北路社区的王琮，组织辖区单位和文体队伍分别组建了“区域化党建共建理事会”和“文化共建理事会”，推行了“民情365沟通全天候”“一院一档三色管理”、前后台分离网格化服务管理的工作模式和“金色阳光”养老工程，“430”学校，其中“430”学校的巴菲特集市——儿童跳蚤市场荣获2014年中宣部“节约之星”的称号。</span></p><p><br/></p>');
INSERT INTO `article` VALUES (38, '1532157026537', '“2015成都榜样”主题活动9月人物榜出炉', '人物榜单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都榜样，带动更多人内心向上向善。昨日，记者从成都市文明办获悉，“9月成都榜样名单”正式出炉，共计10人。 <br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">敬业奉献类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈明蓉</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 彭州市小鱼洞镇九年制学校教师，担任班主任工作30年，班级管理形成特色。并与新教师建立“师徒结对”关系，使他们迅速成长为优秀教师。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">余琳</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都市第十六幼儿园教师，成都市特级教师。28年里，她让十六幼变成孩子们的大玩具，把一所普通企业幼儿园变成了全国知名游戏研究特色园。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 叶德元</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都七中育才学校教师，担任班主任工作多年，设计了五百多个特色活动，编写班级博文1900多篇，在150多个学校进行了经验分享。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 江蓉秋</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都工业职业技术学院教师，成都市第十七届优秀青年教师，对不同层次类型的班级分层次教学。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 尹宏</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都市龙盛小学教师，省级教学名师，致力于为农民工子女或农村孩子提供更好的英语教育。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">胡水斌</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 彭州中学教师，成都市优秀青年教师，采取有针对性的分层教学方式。所负责的班班风好，每年年度考核居全校第一。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">黄真</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 蒲江县青年志愿者协会理事长，2009年参与大学生志愿服务西部计划支援蒲江发展。6年来，尽职尽责、创新开展青年志愿者协会工作。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">助人为乐类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刘丽</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 金泉小学校教师，在班上一学生父母突遇车祸双亡后，将失去父母的两姐妹接至自己家中，独自照顾其起居、学习，并组织年级老师和家长募捐。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 见义勇为类</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">&nbsp;&nbsp; 蒋吉东</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2015年8月25日，双流县凤翔湖森林公园湖边不慎落水一小男孩和阿姨，蒋吉东立即跳湖施救，最后因体力不支，沉入水中。22小时后，他的遗体被打捞上岸，双手还保持着救人的姿势。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">孝老爱亲类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 郑升贵</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2005年，郑升贵岳父被诊断出“尿毒症”，他便每天为其翻身、清理大便，直到2012年岳父永远地离开。然而，岳母也被诊断为“运动神经元病”，他依然不离不弃地照顾。</p><p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span>　<span style=\"color: rgb(81, 81, 81);\">日期：2015-09-24</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都榜样，带动更多人内心向上向善。昨日，记者从成都市文明办获悉，“9月成都榜样名单”正式出炉，共计10人。 <br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">敬业奉献类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈明蓉</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 彭州市小鱼洞镇九年制学校教师，担任班主任工作30年，班级管理形成特色。并与新教师建立“师徒结对”关系，使他们迅速成长为优秀教师。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">余琳</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都市第十六幼儿园教师，成都市特级教师。28年里，她让十六幼变成孩子们的大玩具，把一所普通企业幼儿园变成了全国知名游戏研究特色园。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 叶德元</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都七中育才学校教师，担任班主任工作多年，设计了五百多个特色活动，编写班级博文1900多篇，在150多个学校进行了经验分享。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 江蓉秋</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都工业职业技术学院教师，成都市第十七届优秀青年教师，对不同层次类型的班级分层次教学。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 尹宏</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成都市龙盛小学教师，省级教学名师，致力于为农民工子女或农村孩子提供更好的英语教育。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">胡水斌</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 彭州中学教师，成都市优秀青年教师，采取有针对性的分层教学方式。所负责的班班风好，每年年度考核居全校第一。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">黄真</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 蒲江县青年志愿者协会理事长，2009年参与大学生志愿服务西部计划支援蒲江发展。6年来，尽职尽责、创新开展青年志愿者协会工作。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">助人为乐类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刘丽</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 金泉小学校教师，在班上一学生父母突遇车祸双亡后，将失去父母的两姐妹接至自己家中，独自照顾其起居、学习，并组织年级老师和家长募捐。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 见义勇为类</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">&nbsp;&nbsp; 蒋吉东</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2015年8月25日，双流县凤翔湖森林公园湖边不慎落水一小男孩和阿姨，蒋吉东立即跳湖施救，最后因体力不支，沉入水中。22小时后，他的遗体被打捞上岸，双手还保持着救人的姿势。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">孝老爱亲类</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"> 郑升贵</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2005年，郑升贵岳父被诊断出“尿毒症”，他便每天为其翻身、清理大便，直到2012年岳父永远地离开。然而，岳母也被诊断为“运动神经元病”，他依然不离不弃地照顾。</p><p><br/></p>');
INSERT INTO `article` VALUES (39, '1532157190836', '10月成都榜样名单出炉 共10人入选 ', '榜样名单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			&nbsp; &nbsp; &nbsp; 学习榜样先进事迹，传递向上、向善正能量。成都市文明办发布成都榜样10月名单。其中，包括身残志坚的残疾人创业先锋范正权、武侯区黉门街社区书记申民辉在内的榜样共计10人。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp; &nbsp; &nbsp; 自幼双腿残疾，范正权自强不息，自主创业十多年，带动了更多的残疾人一起开始新的生活；八旬老中医范忠贵，坚持为社区群众免费看病，义举已经持续9年……在10月的榜单人物中，这样的故事还有很多。他们爱岗敬业、诚信友善，用汗水与泪水书写生命的意义，践行着社会主义核心价值观。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>&nbsp; &nbsp; &nbsp;&nbsp;<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 21px;\">本报记者 安羽</span></p><p><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">榜单人物简介</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">敬业奉献类</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 刘胜平</p><p>&nbsp; &nbsp; &nbsp; 四川师范大学文理学院演艺学院兼职声乐老师刘胜平，2015年7月，因病逝世。今年5月31日下午2时，病情危重的刘胜平老师选择了在病房里为心爱的学生们再上“最后一课”。他说，时间不多了，想趁自己还有说话的力气，将毕生的感悟传授给弟子们。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp;</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 申民辉</p><p>&nbsp; &nbsp; &nbsp;&nbsp;申民辉担任武侯区玉林街道黉门街社区党委书记已经13个年头了，他坚持每天早上7点半到社区，一干就到深夜，在他的日历里，从来就没有星期天。“把居民当亲人，把社区的事当家里的事”是申民辉常常提到的口头禅，也是他常做的。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">助人为乐类</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 范忠贵</p><p>&nbsp; &nbsp; &nbsp;&nbsp;八旬老人范忠贵是龙泉驿区大面街道的居民。退休后，老人坚持9年，在社区一个简陋的休闲亭里办起一个义诊服务点，将每月的10、20、30日上午设为固定的义诊日，为数千名有需要的居民看病，并为他们写下厚厚几本健康档案记录。虽然义诊很辛苦，有时一天忙得连饭也吃不上，但老人却乐此不疲。他说他愿把60余年所积累的医学知识，无私奉献给社会。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 裴丽娟</p><p>&nbsp; &nbsp; &nbsp; 裴丽娟是双庆小学副校长。2014年8月，作为成华区新一批援藏工作队员，她与丈夫带着10岁女儿举家前往丹巴支援教育。裴丽娟将成都师生对等的教育理念带到丹巴，试图打破一贯僵持的课堂氛围。尽量多地和当地老师进行交流、分享经验，尽力影响他们的教育观念和教学模式。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 范正权</p><p>&nbsp; &nbsp; &nbsp; 范正权是武侯区的一名居民。先天残疾，但他并没有向命运低头。为了改善生活，1993年，他和妻子一起将4000元积蓄拿出来创业。之后，他与妻子又一起开起了副食店，并带动更多的残疾人一起致富。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">孝老爱亲类</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 黄再军</p><p>&nbsp; &nbsp; &nbsp; 2000年，黄再军从部队转业来到成都龙泉驿，成立黄风湿医院。在医院临床诊疗过程中，他发现很多老人都患有各种慢性疾病，因此，他依托医院资源，建立龙泉阳光康托苑，开通助老热线。投资500万元建成四川省首家老年公园，丰富老年朋友的文化娱乐生活。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 杨福太</p><p>&nbsp; &nbsp; &nbsp; 杨福太，都江堰市青城山镇民兴社区16组村民。十多年前，杨福太岳母瘫痪在床了，双目接近于失明。当得知岳母瘫痪在床，他执意回乡照顾岳母，十几年来，不离不弃，一直悉心照顾。大家都称他为“好丈夫”“好女婿”。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 严柳</p><p>&nbsp; &nbsp; &nbsp; 义卖、环境维护、无偿献血……从2013年进入成都大学工业制造学院开始，1994年出生的严柳就加入了学校青年志愿者协会工业分会，将热情投入到各类志愿服务活动中。2013年10月，严柳在城东康疗院认识了一位邓奶奶。此后，他保持着每两周就去陪对方一下午的习惯。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 赵群刚</p><p>&nbsp; &nbsp; &nbsp; 赵群刚是温江区和盛镇村民。赵家有兄弟姐妹四人，赵群刚是老幺。父亲去世后，赵群刚毅然承担了赡养母亲和三哥的义务，他坚持20年，将母亲与三哥放在第一位。曾经被医生告知最多活二十多岁的三哥，如今44岁了。这都是靠他长期细心地照顾换来的。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">见义勇为类</strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp; &nbsp; &nbsp; 罗阳</p><p>&nbsp; &nbsp; &nbsp; 成都市新阳建筑劳务有限公司董事长罗阳。2015年4月4日13点30分左右，他带小女儿坐着三轮车到新津县南河南岸去会朋友，救起了一位落水女子，直至120救护车赶到之后罗阳才悄悄离开。</p><p><br/></p>');
INSERT INTO `article` VALUES (40, '1532157229447', '“2015成都榜样”主题活动11月人物榜出炉', '榜样名单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	　　昨日，记者从市文明办获悉，经过层层评选，11月成都榜样名单正式出炉，10位榜样在孝老爱亲、敬业奉献、助人为乐、见义勇为、诚实守信等方面事迹特别突出，值得大家学习。另外，11月还同时评出了10名“成都榜样·最美警察”，他们爱岗敬业、惩恶扬善、守望平安，受到了市民喜爱。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">11月“成都榜样”名单</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 孝老爱亲</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刁祖民</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　郫县犀浦镇龙吟村村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他不离不弃，照顾双目失明妻子26年，同时还要照顾妻子年迈的父母和一对儿女，他用强烈的责任感构筑了一个温馨的家，用实际行动诠释了中华民族孝老爱亲的传统美德。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">王书琼</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县胜利镇花龙村6组村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：她在丈夫的大哥意外去世、大嫂改嫁后，主动担起了照顾两个幼小侄女的责任，现在已经成家的两个侄女，还是经常回家看望这个“妈妈”。与婆婆住一起的她，一直把婆婆当作自己的亲生父母来对待，全家人其乐融融。她30年来用责任、细心和爱心向众人诠释了孝老爱亲的真谛。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 敬业奉献</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">李建书 周小芳</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　都江堰龙池景区夫妇护林员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：10年来，他们克服不通水、电、气等诸多生活困难 ，坚守深山景区护林，守望着亚洲最大的杜鹃花园。他们10年如一日，重复着修剪杜鹃、巡山、背干粮上山等琐碎的事情，把所有的爱都洒在了这片森林。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刘礼国</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　蒲江县蒲江中学教师<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他身患肺癌晚期，仍默默坚守三尺讲台，为学生们传授知识、讲述人生哲理。他用智慧播种，用爱心浇灌，用耕耘和付出践行教育人的梦想。他积极面对困难和病魔，乐观的精神感染着身边的每一个人。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">王永法</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　四川武警总队第一支队反恐特战大队副大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他投身军营，先后9次拒绝地方老板高薪聘请，3次婉拒省公安特警队特招。为完成执勤处突、联演联训任务，他5次推迟婚期。自成为特战队员以来，因高强度训练，腰、腿、关节等部位前后17次受伤患病，但从没因伤病下过训练场。他精湛的专业技能，执着的军人情怀，树立了一代反恐维稳尖兵的光辉形象。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 助人为乐</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">曹永能</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　新都区国家税务局工作人员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：未至而立之年，他的青春足迹已遍布两省三地。他组织支教，关爱盲童、农民工子女、留守儿童，参与灾后重建、环境保护等，他把真诚的爱奉献给了公益事业和贫困山区。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">李小兵</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县供电公司九江供电所副所长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2013年，他在双流县金桥镇永和村建立为老年人服务的免费“爱心茶室”和双流义工队，坚持每周末前往敬老院为孤寡老人提供无偿服务。他把爱无私地奉献给了孤寡老人们。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">牟绍武</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　龙泉驿区桃花源出租汽车有限公司驾驶员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他大胆开拓，牵头成立“阳光车队”，在全队实行资源共享、就近优先等原则，很好地诠释了团队精神和团队力量；他见义勇为，路遇抢劫，毫不犹豫帮助追拦犯罪嫌疑人；他诚信做人，乘客掉在车上的钱物，他千方百计将其物归原主。他的行为和举动正是人生最至真至诚的有力写照。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 见义勇为</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">杨廷勇</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　大邑县鹤鸣乡鹤鸣街12号水洞农家乐老板<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2015年8月30日下午3时许，快速上涨的河水把两个小孩困在了农家乐附近的河中，他听到呼救后，不假思索地跳入河中，耗尽体力将两个小孩先后救上岸。他无私、无畏的举动让两个家庭免受了莫大的痛苦。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 诚实守信</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈素珍</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县胜利镇白塔社区2组村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：上世纪90年代，她开了“陈妈豆花饭”饭店，几十年，坚持每天的菜品原料亲自到附近村民家采购，每天剩下的菜严格处理掉。同时，坚持在不亏本的情况下，菜品坚决不涨价。她的做法体现了以诚为本、善以待人的高贵品质。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">“成都榜样·最美警察”名单</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">朱伟华</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局特警支队民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2015年5月27日，他在抓捕犯罪团伙的过程中中弹受伤后仍奋勇追击，为最终击毙犯罪嫌疑人赢得宝贵战机。而他因胸部、腿部中弹，造成肋骨和胫骨粉碎性骨折。他用鲜血捍卫了人民警察的威严。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈晓林</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局温江区分局公平派出所所长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他带领全所民警，刻苦钻研基层警务工作，努力与群众打成一片，创新形成了“警察扮小偷督导小区防范”“巧用车轮锁防止茶楼盗车”等“土办法”；开拓完善了“民警、保安、治保三向监督”、平安社区“细胞工程”“QQ群小区安防曝光台”等有效防范措施。他用细致入微、踏实管用的办法保障了一方老百姓的平安。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">狄旺</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　</strong>　成都市公安消防支队特勤大队一中队执勤中队长助理<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：当兵8年以来，他参加灭火作战，抢险救援和社会救助共计6500余次，服务群众达万余人。2008年，在“5·12”地震赈灾工作中，与战友们成功搜救出幸存者51名，抬出遇难者遗体96具；2013年，在芦山地震中，与战友们成功搜救出幸存者21名，抬出遇难者遗体32具。他用自己坚定的使命颂扬着一个消防战士的至高荣誉。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">雷强</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局交通警察支队第二分局二大队副大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：针对仿冒出租车，他总结出了发现和处置的三步辨识法， 在2015年专项整治中挡获仿冒出租车32辆，占全支队56.1%，拘留6人，占全支队85.7%。他还建立了分局网络宣传平台，开通了分局QQ账号、QQ便民服务群、官方微博，充分利用网络向更多的人分享了自己的成果和相关知识。他立足本职岗位，创造性地开展着扎实有效的工作。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">林正良</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局经济犯罪侦查支队三大队大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年，他在“0901”特大假币案侦破中，捣毁伪造人民币的生产窝点，抓获13名重大假币犯罪嫌疑人，避免了逾10亿元假币流入市场。2013年，他带领民警破获近20年来全国最大的伪造假美元案，查获假外币1200万元，公安部发嘉奖令祝贺。2014年7月以来，组织、指挥并参与侦破非法集资案17起。他用精湛的侦查技术，为经济犯罪筑起了一条牢不可破的坚固防线。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">罗健</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局成华区公安分局双水碾派出所民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年6月，他在抓捕犯罪嫌疑人时不幸负伤，手术后体内留下4根钢针，忙于工作至今未取，被称为“钢铁侠”。2015年4月，一持刀男子朝过往群众挥刀乱舞，他奋不顾身冲上前与之搏斗，被持刀男子刺伤，血流不止，他强忍伤痛奋起开枪，最终制服了歹徒。他用坚定的信念和执着的追求，捍卫着人民警察的崇高职责。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">阮立波</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局青羊公安分局刑警大队三中队中队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2013年9月，他在查处吸贩毒窝点时，不慎跌落近9米深的负楼层内，造成5天昏迷，2次被下病危通知，后经全力抢救，活下来了，但他在还未完全康复的情况下，一瘸一拐地拄着拐棍坚决地回到了工作岗位。此役，破获贩毒窝点1个，抓获贩毒嫌疑人3名，查获各类毒品310克、仿真手枪1把。他用坚定的精神，诠释着对警察事业的热爱。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">徐飞</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市武侯区分局巡警（防暴）大队便衣中队中队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2014至今,他带领中队成功挡获各类违法犯罪嫌疑人300余名，刑事拘留违法人员209名，挡获各类被盗抢机动车69台，缴获非法枪支29支，为群众挽回经济损失700余万元。他用职业的精神保护着人民群众生命财产安全。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">徐苑榕</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局天府新区公安分局兴隆派出所教导员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：作为市局机关专职政工信息调研民警，他文字理论功底扎实深厚，多篇政工调研成果获上级表彰；作为特巡警实战部门政工干部，他工作能力全面丰富，多次随队赶赴青海玉树、甘肃舟曲、芦山宝兴抢险救灾一线；作为基层派出所的政工主官，他创造性践行“身教大于言传”理念，迅速扭转警心涣散、纪律松懈、业务垫底的困难局面，实现公安业务和思想政治工作的同频共振和双丰收。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">黄涔涔</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局锦江区分局出入境管理大队外事民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年4月入警以来，她在窗口接待中，立足平凡岗位，以其特有的亲和力、文明规范的举止、热情周到的服务和业精技强的工作得到了领导和同志们的肯定及群众的称赞，用实际行动展现出了锦江出入境“出入有境，服务无境”的服务理念和工作宗旨。&nbsp;</p><p style=\"text-align:right;margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	本报记者 杜文婷</p><p><br/></p>');
INSERT INTO `article` VALUES (41, '1532157266094', '“2015成都榜样”主题活动11月人物榜出炉', '榜样名单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span>　<span style=\"color: rgb(81, 81, 81);\">日期：2015-11-26</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	　　昨日，记者从市文明办获悉，经过层层评选，11月成都榜样名单正式出炉，10位榜样在孝老爱亲、敬业奉献、助人为乐、见义勇为、诚实守信等方面事迹特别突出，值得大家学习。另外，11月还同时评出了10名“成都榜样·最美警察”，他们爱岗敬业、惩恶扬善、守望平安，受到了市民喜爱。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">11月“成都榜样”名单</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 孝老爱亲</span></strong></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刁祖民</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　郫县犀浦镇龙吟村村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他不离不弃，照顾双目失明妻子26年，同时还要照顾妻子年迈的父母和一对儿女，他用强烈的责任感构筑了一个温馨的家，用实际行动诠释了中华民族孝老爱亲的传统美德。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">王书琼</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县胜利镇花龙村6组村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：她在丈夫的大哥意外去世、大嫂改嫁后，主动担起了照顾两个幼小侄女的责任，现在已经成家的两个侄女，还是经常回家看望这个“妈妈”。与婆婆住一起的她，一直把婆婆当作自己的亲生父母来对待，全家人其乐融融。她30年来用责任、细心和爱心向众人诠释了孝老爱亲的真谛。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 敬业奉献</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">李建书 周小芳</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　都江堰龙池景区夫妇护林员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：10年来，他们克服不通水、电、气等诸多生活困难 ，坚守深山景区护林，守望着亚洲最大的杜鹃花园。他们10年如一日，重复着修剪杜鹃、巡山、背干粮上山等琐碎的事情，把所有的爱都洒在了这片森林。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">刘礼国</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　蒲江县蒲江中学教师<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他身患肺癌晚期，仍默默坚守三尺讲台，为学生们传授知识、讲述人生哲理。他用智慧播种，用爱心浇灌，用耕耘和付出践行教育人的梦想。他积极面对困难和病魔，乐观的精神感染着身边的每一个人。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">王永法</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　四川武警总队第一支队反恐特战大队副大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他投身军营，先后9次拒绝地方老板高薪聘请，3次婉拒省公安特警队特招。为完成执勤处突、联演联训任务，他5次推迟婚期。自成为特战队员以来，因高强度训练，腰、腿、关节等部位前后17次受伤患病，但从没因伤病下过训练场。他精湛的专业技能，执着的军人情怀，树立了一代反恐维稳尖兵的光辉形象。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 助人为乐</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">曹永能</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　新都区国家税务局工作人员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：未至而立之年，他的青春足迹已遍布两省三地。他组织支教，关爱盲童、农民工子女、留守儿童，参与灾后重建、环境保护等，他把真诚的爱奉献给了公益事业和贫困山区。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">李小兵</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县供电公司九江供电所副所长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2013年，他在双流县金桥镇永和村建立为老年人服务的免费“爱心茶室”和双流义工队，坚持每周末前往敬老院为孤寡老人提供无偿服务。他把爱无私地奉献给了孤寡老人们。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">牟绍武</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　龙泉驿区桃花源出租汽车有限公司驾驶员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他大胆开拓，牵头成立“阳光车队”，在全队实行资源共享、就近优先等原则，很好地诠释了团队精神和团队力量；他见义勇为，路遇抢劫，毫不犹豫帮助追拦犯罪嫌疑人；他诚信做人，乘客掉在车上的钱物，他千方百计将其物归原主。他的行为和举动正是人生最至真至诚的有力写照。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 见义勇为</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">杨廷勇</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　大邑县鹤鸣乡鹤鸣街12号水洞农家乐老板<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2015年8月30日下午3时许，快速上涨的河水把两个小孩困在了农家乐附近的河中，他听到呼救后，不假思索地跳入河中，耗尽体力将两个小孩先后救上岸。他无私、无畏的举动让两个家庭免受了莫大的痛苦。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 诚实守信</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈素珍</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　双流县胜利镇白塔社区2组村民<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：上世纪90年代，她开了“陈妈豆花饭”饭店，几十年，坚持每天的菜品原料亲自到附近村民家采购，每天剩下的菜严格处理掉。同时，坚持在不亏本的情况下，菜品坚决不涨价。她的做法体现了以诚为本、善以待人的高贵品质。<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">“成都榜样·最美警察”名单</span></strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">朱伟华</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局特警支队民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2015年5月27日，他在抓捕犯罪团伙的过程中中弹受伤后仍奋勇追击，为最终击毙犯罪嫌疑人赢得宝贵战机。而他因胸部、腿部中弹，造成肋骨和胫骨粉碎性骨折。他用鲜血捍卫了人民警察的威严。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">陈晓林</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局温江区分局公平派出所所长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：他带领全所民警，刻苦钻研基层警务工作，努力与群众打成一片，创新形成了“警察扮小偷督导小区防范”“巧用车轮锁防止茶楼盗车”等“土办法”；开拓完善了“民警、保安、治保三向监督”、平安社区“细胞工程”“QQ群小区安防曝光台”等有效防范措施。他用细致入微、踏实管用的办法保障了一方老百姓的平安。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">狄旺</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　</strong>　成都市公安消防支队特勤大队一中队执勤中队长助理<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：当兵8年以来，他参加灭火作战，抢险救援和社会救助共计6500余次，服务群众达万余人。2008年，在“5·12”地震赈灾工作中，与战友们成功搜救出幸存者51名，抬出遇难者遗体96具；2013年，在芦山地震中，与战友们成功搜救出幸存者21名，抬出遇难者遗体32具。他用自己坚定的使命颂扬着一个消防战士的至高荣誉。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">雷强</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">　　</strong>成都市公安局交通警察支队第二分局二大队副大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：针对仿冒出租车，他总结出了发现和处置的三步辨识法， 在2015年专项整治中挡获仿冒出租车32辆，占全支队56.1%，拘留6人，占全支队85.7%。他还建立了分局网络宣传平台，开通了分局QQ账号、QQ便民服务群、官方微博，充分利用网络向更多的人分享了自己的成果和相关知识。他立足本职岗位，创造性地开展着扎实有效的工作。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">林正良</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局经济犯罪侦查支队三大队大队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年，他在“0901”特大假币案侦破中，捣毁伪造人民币的生产窝点，抓获13名重大假币犯罪嫌疑人，避免了逾10亿元假币流入市场。2013年，他带领民警破获近20年来全国最大的伪造假美元案，查获假外币1200万元，公安部发嘉奖令祝贺。2014年7月以来，组织、指挥并参与侦破非法集资案17起。他用精湛的侦查技术，为经济犯罪筑起了一条牢不可破的坚固防线。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">罗健</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局成华区公安分局双水碾派出所民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年6月，他在抓捕犯罪嫌疑人时不幸负伤，手术后体内留下4根钢针，忙于工作至今未取，被称为“钢铁侠”。2015年4月，一持刀男子朝过往群众挥刀乱舞，他奋不顾身冲上前与之搏斗，被持刀男子刺伤，血流不止，他强忍伤痛奋起开枪，最终制服了歹徒。他用坚定的信念和执着的追求，捍卫着人民警察的崇高职责。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">阮立波</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局青羊公安分局刑警大队三中队中队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2013年9月，他在查处吸贩毒窝点时，不慎跌落近9米深的负楼层内，造成5天昏迷，2次被下病危通知，后经全力抢救，活下来了，但他在还未完全康复的情况下，一瘸一拐地拄着拐棍坚决地回到了工作岗位。此役，破获贩毒窝点1个，抓获贩毒嫌疑人3名，查获各类毒品310克、仿真手枪1把。他用坚定的精神，诠释着对警察事业的热爱。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">徐飞</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市武侯区分局巡警（防暴）大队便衣中队中队长<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2014至今,他带领中队成功挡获各类违法犯罪嫌疑人300余名，刑事拘留违法人员209名，挡获各类被盗抢机动车69台，缴获非法枪支29支，为群众挽回经济损失700余万元。他用职业的精神保护着人民群众生命财产安全。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">徐苑榕</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局天府新区公安分局兴隆派出所教导员<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：作为市局机关专职政工信息调研民警，他文字理论功底扎实深厚，多篇政工调研成果获上级表彰；作为特巡警实战部门政工干部，他工作能力全面丰富，多次随队赶赴青海玉树、甘肃舟曲、芦山宝兴抢险救灾一线；作为基层派出所的政工主官，他创造性践行“身教大于言传”理念，迅速扭转警心涣散、纪律松懈、业务垫底的困难局面，实现公安业务和思想政治工作的同频共振和双丰收。</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\">黄涔涔</strong><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　成都市公安局锦江区分局出入境管理大队外事民警<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/>　　入选理由：2012年4月入警以来，她在窗口接待中，立足平凡岗位，以其特有的亲和力、文明规范的举止、热情周到的服务和业精技强的工作得到了领导和同志们的肯定及群众的称赞，用实际行动展现出了锦江出入境“出入有境，服务无境”的服务理念和工作宗旨。&nbsp;</p><p style=\"text-align:right;margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	本报记者 杜文婷</p><p><br/></p>');
INSERT INTO `article` VALUES (42, '1532157317186', '平凡善举正能量 12万市民点赞“成华榜样”', '正能量', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p>\n	<br/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450245282.jpg\"/><br/></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 身边榜样，前行力量。12月11日，由成华区文明委主办的“榜样力量 感动成华”——2015年第四届“成华榜样”颁奖典礼在成华区政府会议室内隆重举行。主办方表彰了10名“成华榜样”和14名提名奖获得者。据成华区文明办负责人介绍，近年来，成华区通过创新建立身边典型发掘、推荐、推广机制，发掘了越来越多的优秀典型人物，塑造了成华名片，同时，也进一步激发了社会正能量。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>活动8月正式启动</strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 12万市民点赞“成华榜样”</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 当日上午10点，成华区政府会议室内，成华好人、道德模范、美德少年们齐聚一堂，现场十分火热。“你们活得平凡，平凡得像一棵棵小草，却创造了平凡中的伟大和光荣……”伴随着悠扬的旋律，主持人上台宣布由成华区文明委主办的“榜样力量 感动成华”——2015年第四届“成华榜样”颁奖典礼正式开始。据了解，成华榜样推荐评选活动从2012年开始，在前三届的评选中，共选出“成华好人”71名，其中吴永秀被评为全国道德模范、6人荣登中国好人榜、2人获得感动四川十大人物荣誉称号、6人被评为成都市道德模范、18人被评为四川好人。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“随着‘成华榜样’人物评选表彰活动的持续开展，在全区掀起了一个学好人、做好人、树正气的高潮。”主办方相关负责人说，本届“成华榜样”人物评选表彰活动以平凡善举好榜样，感恩传递正能量为主题，自今年8月正式启动以来，吸引了12万人参与，共推荐候选人105名，最终评选出助人为乐类榜样3名、敬业奉献类榜样3组、见义勇为类榜样2名、孝老爱亲类榜样1名、诚实守信类榜样1名。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>颁奖形式多样</strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp;&nbsp;</strong><strong>现场引发学习榜样热潮</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 随后，主办方通过观看事迹短片、致颁奖词的形式，对各类榜样进行了表彰。首先获奖的是诚实守信类成华榜样。“家住站北东街3号院的周群得知肖婆婆儿子有残疾，未来不知她和她儿子如何生活。细心的周群听出了老人家的担心，当即承诺自己会照顾肖婆婆。后来，肖婆婆被查出了肺癌晚期。周群信守承诺，担起照顾她的责任，不仅承担所有的费用，还做起了贴身护理……”在事迹短片中，不时播放出周群照顾肖婆婆的画面，两人之间，虽不是亲人，但流露出的情谊却让人为之感动。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;短片播放结束后，伴着现场阵阵掌声，周群走上了舞台。与此同时，现场响起了评委会授予周群的颁奖致辞，“你行走在人群中，像一粒尘土，微乎其微。你许下一句承诺，却铿锵有力，掷地有声。诚实守信，一诺千金，彰显了中华文明代代相传的善良天性。”接下来，第三届成都市“诚实守信”类道德模范姚忠义及美德少年为她颁发了奖品。“周大姐就是我们社区的，她的故事我们都知道。”市民李文会告诉记者，她也为周大姐投了票，也号召社区群众都要向她学习。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>音乐剧演绎姐妹情</strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 精彩表演引爆观众泪点</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;和周群一样，包括保和街道“儿童公益读书会”创办人、自闭症孩子们的“知心叔叔”赵寨峰在内的10名“成华榜样”和14名提名奖获得者都得到了奖励。活动现场，主办方还通过小品、清唱等方式穿插在颁奖过程中，特别是现场原创的情景音乐剧《你是我的幸福》，演员们精彩的表演，柔美的肢体语言，让现场响起一波又一波的掌声，观众们大呼“感人”“触动”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 音乐剧根据本届“成华榜样”——跳蹬河街道锦绣东方小区居民王健美与姐姐的故事改编。姐姐出生后就不能行走，一直瘫痪至今。当父母去世后，妹妹王健美和丈夫接过照顾姐姐饮食起居的重担，成为姐姐的“双腿”，并且一照顾就是26年。音乐剧一开始，十多位白衣舞者翩翩起舞，随后，在轮椅上的姐姐的扮演者抓狂、挣扎等场景掀起了高潮，妹妹王健美的扮演者抱着绝望的姐姐，并对姐姐说出了心里话，“我照顾你，我是你一辈子的亲人，我们心连心……”话音刚落，现场观众热泪盈眶。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;</strong><strong>“救人女侠”获杰出贡献奖</strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 媒体联动形成良好机制</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在颁奖典礼的末尾，现场颁发了成华区精神文明建设杰出贡献奖。获得该奖项的是第五届全国道德模范、四川好人、成都市道德模范、成华好人吴永秀大姐。40年间救起了24条鲜活的生命。这位“救人女侠”不仅自己救人,还拉着水性好的街坊邻居组成了“义务阻泳队”，人们称她为“救水110”。事迹短片播放结束后，全场观众全体起立，对吴大姐报以最热情的掌声。“吴大姐是我们成华的骄傲，更是我们成都的骄傲，我们都要向她学习。”市民陈慧激动地说道。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “成华榜样”自评选以来，与媒体进行了亲密合作。作为2015“成都榜样”主题活动的重要分活动，2015“成华榜样”评选活动启动以来吸引了社会各界的关注，今年9月，由本报与成华区文明办联办的“与榜样人物零距离面对面”交流活动也火热开展。此外，主办方介绍，本次活动还首次推出了“感恩,除了心动，更要行动”的身边榜样真人秀等展示活动，采取真人秀的模式全程跟踪拍摄寻找好人的过程，制作成视频节目,并挂上网络，“通过这样的形式，让‘好榜样 正能量’吸引大众关注、引发话题讨论,带动更多人内心向上向善。”</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 本报记者 安羽 文/图</span></p><p><br/></p>');
INSERT INTO `article` VALUES (43, '1532157356630', '成都榜样为癌症患者举办生日庆典', '癌症关爱', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450254851.png\"/> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"color: rgb(0, 51, 153);\"><strong>“我学榜样做公益”关爱癌症患者志愿服务活动现场</strong></span><strong style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\"><span style=\"color: rgb(0, 51, 153);\"></span></strong> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 15日，由成都市文明办主办、成都公益组织服务园、成都抗癌协会承办的“我学榜样做公益”关爱癌症患者志愿服务活动在成都三圣花乡开心农庄举行。成都榜样林德明、江艳、王乐和拉孟来到现场，和300多位癌症病人一起为33位癌龄五周年的病友举办隆重的生日庆典，祝贺他们重返健康人群。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 15日上午，在一曲“生命之河”的动人音乐里，300多位癌症病友和成都榜样们一起翩翩起舞，感恩我们每天的快乐生活，也拉开了“我学榜样做公益”关爱癌症患者志愿服务活动的序幕。在活动过程中，多位癌症病友走上舞台，与大家分享自己与癌症抗争的故事，呼吁大家要乐观面对生活。</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在成都榜样中，其实也不乏深处绝境却依然积极向上的正能量人群。在这次的活动中，成都榜样也来到现场，与癌症病友面对面相互交流，让他们感受到来自榜样和社会的真挚关怀。</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; line-height: 28px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450257180.png\"/> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\">成都榜样来到活动现场</span></strong><strong><span style=\"color: rgb(0, 51, 153);\"></span></strong><br/></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"border-bottom-color: rgb(0, 51, 153); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(0, 51, 153); border-left-style: none; border-left-width: 0px; border-right-color: rgb(0, 51, 153); border-right-style: none; border-right-width: 0px; border-top-color: rgb(0, 51, 153); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450261674.png\"/><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\">成都榜样拉孟送上自己的唐卡画<br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"border-bottom-color: rgb(0, 51, 153); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(0, 51, 153); border-left-style: none; border-left-width: 0px; border-right-color: rgb(0, 51, 153); border-right-style: none; border-right-width: 0px; border-top-color: rgb(0, 51, 153); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450258444.png\"/><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\">活动方为抗癌成功五年的人群颁发奖章</span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"border-bottom-color: rgb(0, 51, 153); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(0, 51, 153); border-left-style: none; border-left-width: 0px; border-right-color: rgb(0, 51, 153); border-right-style: none; border-right-width: 0px; border-top-color: rgb(0, 51, 153); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src/><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\">活动方为癌症病友准备的蛋糕<br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\"><img style=\"border-bottom-color: rgb(0, 51, 153); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(0, 51, 153); border-left-style: none; border-left-width: 0px; border-right-color: rgb(0, 51, 153); border-right-style: none; border-right-width: 0px; border-top-color: rgb(0, 51, 153); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151216/1450256322.png\"/><br/></span></strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(0, 51, 153);\">合影留念<br/></span></strong></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “我抗癌成功2年”、“我抗癌成功5年”、“我抗癌成功10年”......在活动现场，癌症病友们兴奋地告诉记者他们与癌症“赛跑”的故事，活动方还在现场宣布了重返健康人群抗癌五周年的英雄名单，并为他们颁发了奖章。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“生命掌握在自己的手中，我们要摆正心态，乐观地面对现在的生活。”78岁的徐继东老人已经抗癌成功24年了。他告诉记者，这些年他学着用一种感恩的心态面对生活，尽管现在年纪大了，可每天仍然坚持散步2公里，坚持做早操。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 和徐继东老人一样，在成都还有一大批癌症病人正在为生命努力抗争，他们未曾想过放弃，并积极投身志愿服务事业中。“这些癌症病人在生命的最后阶段聚在一起，形成一个爱的家庭，他们经常组织一些主题活动去帮助身边的人，值得我们称赞。”成都抗癌协会宣传部部长袁晓倩说到。（成都文明网记者 杨金祝 摄影报道）</span> </p><p><br/></p>');
INSERT INTO `article` VALUES (44, '1532157395979', '四名成都榜样走进高校 分享他们的人生历程', '榜样进高校', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151217/1450359095.jpg\"/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	<br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “林丹被称为超级丹，我就是个炒鸡蛋。”昨日下午，在成都纺织高等专科学校北苑学生活动中心，由中共成都市委宣传部、成都市精神文明建设办公室主办的成都榜样高校分享会上，秦坤的自我调侃一下使分享会的气氛轻松起来。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 此次分享会上，成都市金牛区有线电视台编导秦坤、成都市公安局武侯分局刑警大队物证鉴定室副主任法医师冯军、中航工业成都飞机设计研究所高级工程师付焕兵、成都电子科技大学学生刘沈厅。4名成都榜样给大家分享了自己的人生历程——“贵在坚持，梦想成真”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;秦坤第一个上台分享，并现场教了同学们“你好”“大家好”“谢谢”3个手语表达动作。“你好”是用食指指向对方，再竖起大拇指；“大家好”是手掌向下，手臂向前画圆后竖起大拇指；“谢谢”则是竖起大拇指再弯曲，就像鞠躬一样。“个性是与众不同的差异化，要有勇气去展示。另外，保持良好的心态，一直坚持下去，梦想就会成真。”秦坤就自己的人生历程，总结了“个性、勇气、坚持、心态”8个字。秦坤说2012年参加《中国梦想秀》时，第一轮海选他们就已经被淘汰了。就是因为坚持，没有放弃，第二轮海选继续参加，这才一路过关斩将，最终获得了“最受欢迎追梦人”大奖。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都市公安局武侯分局刑警大队物证鉴定室副主任法医师冯军很赞同秦坤的观点，因为他也是一直坚持在法医岗位上，“从来没挪过窝，干了23年”。冯军说，他一开始从事法医，只是顺了父亲的心意，他本人并不喜欢。但一旦做了，就不要轻易放弃，还要把它做好。23年的法医工作，让他体会到了自己的社会责任，并越来越喜欢这份职业。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 罗昊 摄影报道</span></p><p><br/></p>');
INSERT INTO `article` VALUES (45, '1532157476828', '“2015成都榜样”主题活动12月人物榜出炉', '人物榜单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;昨日，记者从市文明办获悉，12月“成都榜样”名单已经出炉，10位榜样在敬老孝亲、敬业奉献等方面表现突出。</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;12月成都榜样名单及事迹简介</strong></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;魏文博</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;魏文博是中国人民解放军五七〇一工厂高级技师，先后维修过10余种飞机机型，曾修复5架事故机及4架损伤机，修理过的飞机数量更是不计其数，多次出国执行技术支援任务，并获得一致好评。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;张瑗</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;张瑗是一个90后女孩，开办了全省首家专门为自闭、智障等残障人士提供就业支持的平台——“Happy Birthday cafe”咖啡馆。同时牵头成立了咖啡公益联盟，主要对残障孩子提供就业机会，让这些特殊的孩子有更多接触世界的机会。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;朱刚</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;朱刚是英特尔产品（成都）有限公司生产技术员。过去一年，他利用所有空闲时间参与志愿服务公益活动，服务范围包括助残、敬老、扶贫、教育，环保等项目，贡献了809个志愿服务小时，他还带动英特尔公司很多员工一起参与志愿服务活动。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;李国宾</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;李国宾是成华区万年场街道居民。为帮助大山深处老乡脱贫，一辈子省吃俭用的她，在2014年全国“扶贫日”四川活动中，以个人名义捐资4万元，成为该项活动四川捐赠首人。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;周济立</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;周济立是郫县德源镇东林村人。2014年4月2日，在绵阳打工的他，面对歹徒抢劫他人时，不顾个人安危，勇于同歹徒作斗争，被歹徒刺断主动脉血管和脑神经血管也不退缩，被送到医院抢救时，先后缝了29针。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;陈聪</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;陈聪是彭州市法律援助中心公职律师。他无偿为广大群众特别是贫困群众、弱势群体提供便民高效的法律服务。工作三年来，共计为群众提供法律咨询1万余人次，为困难群众代写法律文书200余件，办理各类法律援助案件40余件。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;张志华</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;张志华是温江区涌泉街道共耕3组人。2004年丈夫与她离婚后，丢下儿子和公公一走了之，面对尚未成年的儿子和年老体弱多病的公公，张志华用她柔弱的肩膀撑起了这个家。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;李向前</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;李向前是成都理工大学社区研究中心常务副主任、成都市和谐社区发展促进会学术委员会副主任。5年来，他通过“党委政府指导+高校整合跨领域研究+传媒主题推动”合作方式，推出8个具有全国推广意义的社会事业、民生工程模式，得到国家民政部、人力资源与社会保障部、教育部、团中央等部门的肯定。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;林秀芳</strong></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;林秀芳是新都区泰兴镇瓦窑村人。1985年，小叔子柳中才因意外事故高位截肢，女友因此离开了他，而作为嫂子的林秀芳，义无反顾地承担起了照顾瘫痪在床的柳中才的责任，这一照顾，就是30年。</span></span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"><br/></span></span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;林婕</strong></span> </p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;林婕是国网成都供电公司员工。她是一位曾患癌症的单亲妈妈，困境之中，她乐观向上，热心公益事业，坚持到养老院做义工，主动帮助孤寡老人、留守儿童等弱势群体。</span></span> </p><p><br/></p>');
INSERT INTO `article` VALUES (46, '1532157528454', '成都榜样走进西华大学 分享做公益经历', '榜样进西华大学', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151224/1450929282.jpg\"/><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 12月23日下午3时，由中共成都市委宣传部、成都市精神文明建设办公室主办的成都榜样高校分享会在西华大学艺术大楼C座音乐厅举行，百余名同学来到现场，听成都市青羊区残疾人联合会工作人员曾帅、成都市成华区万年场街道华润社区居民陈春元、成都市青白江工商行政管理局公务员江艳和成都大学学生严柳4位成都榜样，分享他们的故事。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “路见不平一声吼，该出手时就出手。”4位成都榜样中，陈春元左脸上8.2厘米长、缝了28针的伤痕让同学们为之一震。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;2014年11月7日，63岁的陈春元乘坐由八里小区到万年场的公交车，其中一名50多岁的男子引起了陈春元的注意，“他上车就东瞧西瞧，手底下开始搞些小动作，我第一反应就是，这个人是个小偷”。为了防止乘客被盗，陈春元上前和该男子搭话：“哎，师傅，你往里头靠点，我后头还宽。”陈春元这一喊，吸引了周围乘客的注意力，该男子无从下手，就和陈春元争吵起来。陈春元再上了一辆公交车，可该男子却堵在车头，不让公交车开走。待车门打开的一瞬间，他跑上车来，用手掌打了陈春元的左脸一下就跑了。“当时就满脸是血。”原来，该男子在手指中夹了一个刀片，陈春元的左脸被划了一道长8.2厘米的口子，缝了28针。建设路派出所经过一个月的侦查，才将该男子抓获。据了解，该男子是惯偷，有吸毒史，在公交车上偷盗时，选择监控盲区下手。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 陈春元也提醒同学们，遇到小偷不要和他正面冲突，首先可以选择报警，其次可以偷偷拍照留下证据，“但要把手机调成静音哈，不要咔嚓响了。”</span></p><p><br/></p>');
INSERT INTO `article` VALUES (47, '1532157577017', '成都榜样高校分享会 走进四川大学锦江学院', '高校分享会', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151229/1451380172.jpg\"/><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20151229/1451384187.jpg\"/><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 12月28日晚，由中共成都市委宣传部、成都市精神文明建设办公室主办，成都市广播电视台、成都传媒集团承办，成都市广播电视台全媒体运营中心执行的2015“成都榜样”高校分享会活动在四川大学锦江学院F201学术报告厅隆重举行。本次分享会邀请了4名嘉宾：王春波、牟章兵、胡 斌、刘沈厅参与并分享榜样故事，传递向上正能量，活动吸引了400多名学生参加。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>胡斌</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “不顾一切代价，要给星星的孩子一个有尊严和有品质的未来”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;来自成都市武侯区善工家园助残中心的主任胡斌，爱越野、爱音乐、爱生活。然而，1992年，出生几个月的儿子被发现是重度脑瘫，他不顾一切代价寻求“秘方”，只为“奇迹”出现。为了孩子能有一个有尊严和品质的将来，胡斌更在2011年创办了为重度智障和脑瘫等残疾人提供托养和教育服务的非营利性公益机构“武侯区善工家园助残中心”，为孩子们拼搏，已成为胡斌生命的一种本能。&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong>牟章兵</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “时间是看得见抓得住的，把时间投在哪儿，它就会在哪儿开花结果”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;来自三六三医院的眼科副主任、副教授牟章兵，是近视眼手术的专家。凭着亲切和蔼的服务态度和娴熟的技术，他成了深受患者信赖的“成都好医生”100强。为了确保手术的安全，在开展每一项新技术之前，他都会在动物眼球上实施上百次手术流程，直到技术娴熟。这种兢兢业业的工作态度和专注刻苦的精神不但吸引了全球各地的患者慕名而来，也进一步提升了团队的技术水平和信心。&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>王春波</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“创业是一种生活方式，不为追求财富，在压力中前进，坦然面对任何困难！”</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 来自成都亿盟恒信科技有限公司的董事长王春波，2003年10月在电子科技大学攻读硕士学位期间开始自主创业。在创业之初他顶住了来自家庭和社会的舆论压力，经受住了伙伴们的纷纷离去，咬牙坚持直到现在。如今他带领的公司已然成为了中国物联网领域的行业领先者。每当被学生们问到如何才能成功时，他给予的回答总是这句激励和改变他一生的话：“不抛弃，不放弃，坚持才能成功。”&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;<strong> 刘沈厅</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“当你决定要去做一件事情的时候，不要瞻前顾后，别给自己退路，机会从来不是等你准备好了才来！”</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 而年纪最轻的嘉宾是来自成都电子科技大学的学生刘沈厅，他在校期间成绩优异，多次获得国家奖学金、国家励志奖学金、人民一等奖学金等重量级奖项。同时，他是是在校学生创业的杰出代表，在做好研究生学习的同时，他自学财务管理、市场推广和客户维护，从来没有周末和节假日，创建了成都欧朗装饰材料有限公司。创业之余，他还热心公益，设立了“启梦奖学金”，发起“微梦党建平台”，用自己的所学所长回报社会。&nbsp;</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 在社会竞争日益激烈的今天，许多人仅仅将理想局限于“房子、车子、票子”，四位嘉宾用自己的亲身经历，对“成都榜样”精神进行了生动的阐释，也通过现身说法，为现场同学做了极佳的示范和表率。当代大学生更应抵制社会上一些浮躁狭隘思想的影响，学会感恩，担当责任，坚持梦想，不忘初心。就像刘沈厅给广大同学分享的一样： “我有梦想，我的梦想只有与“中国梦”同频共振时，才能更好实现。”</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;两个小时的分享会在同学们积极的发言和热烈的掌声中圆满落下帷幕。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “其实四位嘉宾的事迹都挺激励我，我觉得在我们的人生道路上，就是需要这样的正面榜样，能够给我们多传递正能量，以后有这样的分享会，我一定还会参加。”现场学生纷纷发表自己的感想。</span></p><p><br/></p>');
INSERT INTO `article` VALUES (48, '1532157626043', '榜样志愿者齐聚金沙剧场 倡导环保节约文明理念', '生态文明', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 1月3日，由成都市文明办主办，成都公益组织服务园、成都根与芽环境文化交流中心联合承办的“我学榜样做公益”环保绿色行动在金沙剧场举行。活动中，成都榜样田金和、王琮、周俊明、张蓉、陈春元、林德明、周胡彬等7人来到现场，共同欣赏了环保公益儿童剧《千循百乐》，并向39名儿童剧志愿者颁发了根与芽志愿者证书，倡导了环保、节约的文明理念。</span> </p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 环保儿童剧公演吸引观众</strong></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;下午3点，成都市金沙剧场内，热闹非凡。由成都市文明办主办,成都公益组织服务园、成都根与芽环境文化交流中心联合承办的“我学榜样做公益”环保绿色行动在这里火热开展。活动一开始，39位打扮可爱的儿童登上舞台，为观众们带来了环保公益儿童剧《千循百乐》的精彩公演。儿童剧围绕着“千循”、“百乐”二人的经历展开，剧中穿插了环保节约的知识。台下观众们也深深地被剧情吸引，他们在台下同步跟熊猫宝宝一起“历险”。</span> </p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 榜样现场交流 倡导环保理念</strong></span></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;儿童剧演出结束后，台下掌声雷动。主持人邀请成都榜样田金和、王琮、周俊明、张蓉、陈春元、林德明、周胡彬等7人走上台与观众们进行互动交流。与此同时，现场的大屏幕同步播出了榜样们的事迹。作为天天和垃圾、污水打交道的田金和来说，在昨日现场，他说出了自己对于环保的理解。“我是成都市固体废弃物卫生处置场污水处理站维修班长，听起来名字很长，其实我的工作就是焊接污水管道。”田金和说，看了孩子们的演出，他很受感动和鼓舞，“希望大家行动起来，为环保出一份力！”</span> </p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\"></span><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 和田金和一样，其余6位榜样都在活动中倡导了环保节约的文明理念。活动的末尾，主办方邀请39名儿童剧志愿者上台，榜样们为他们颁发了根与芽志愿者证书，并进行了合影。一名儿童志愿者告诉记者，他从成都榜样手中接过了证书，感到很开心。“今后要向叔叔阿姨们学习，从小养成做公益的好习惯，把环保的知识传递给更多人。”</span></span> </p><p><br/></p>');
INSERT INTO `article` VALUES (49, '1532157762224', '山坳里的特殊课 “成都榜样”讲解垃圾场污水哪里来', '污水处理', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都晚报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; “地上覆盖的黑布是什么呀？”一个扎着“马尾”的小女孩举手发问。“那个不是黑布，而是PE膜。它的材质防水耐踩不易烂，所以用它来覆盖垃圾，使之与外界隔离……”1月8日下午，泡桐树小学的孩子们上了一堂特殊课。13个三至六年级孩子没有坐在教室里，而是跟着“成都榜样”田金和一起走进了成都市固体废弃物卫生处置场，这是成都市唯一一座大型生活垃圾填埋场。</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; 此次活动名为“我学榜样做公益 环保绿色行动——垃圾去哪儿环保之旅”，由成都市文明办主办，成都公益组织服务园和成都根与芽环境文化交流中心联合承办。“做这个活动是为了让孩子们了解垃圾处理方法，认识环保的重要性，从而带动影响身边的人一起来维护绿色家园。”根与芽工作人员梁园说。</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong>孩子们的环保问题五花八门</strong></span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; 下午两点过，孩子们到达垃圾填埋场的观景台。该垃圾填埋场位于成都市龙泉驿区洛带镇附近，20多万平方米的黑色薄膜覆盖了整个山坳。在巨幅薄膜下面，掩埋着深达100米的垃圾。据了解，每天都有5000多吨垃圾送到这里。</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp;&nbsp;看着山坳里满目的灰黑色，一阵弥漫着食物腐烂气味的寒风扑面而来……对于刺鼻的臭气，孩子们立马拿出口罩戴上，做好防护措施，但依然觉得“被臭晕了”。然而，被评为“成都榜样”的田金和却是天天在这样的环境中埋头苦干，一干就是35年。</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; 在当天的活动现场，田金和主要是给孩子们介绍生活垃圾的去向并回答孩子们五花八门的环保问题。“为什么覆盖垃圾的PE膜上有很多小孔？”“垃圾填埋场的污水是哪里来的？”对于孩子们的问题，田金和耐心地给予了作答：“覆盖膜上开孔是为了透气，防止垃圾产生的甲烷爆炸。一吨生活垃圾中包含20%的污水，这种污水叫做‘垃圾渗滤液’。”</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp;<strong> “亲眼看到垃圾填埋场很震撼”</strong></span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp;&nbsp;此外，田金和还和孩子们分享了自己工作的故事。作为成都市固体废弃物卫生处置场潜水处理站维修班班长，田金和早已习惯与恶臭为伴。当遇到暴雨造成污水管道堵塞，为确保污水不外溢，他经常冒着40℃的高温，没日没夜地在臭得让人作呕的污水旁抢修管道。</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; 田金和表示，孩子们来参观垃圾填埋场让他很感动。孩子们虽然年龄小，但却能花心思关注环境问题，值得称赞。田金和还表示，“我也建议每家每户扔垃圾的时候尽量注意垃圾分类，特别是废旧电池，不要和一般的生活垃圾混合在一起扔，这样会给垃圾处理工作和环境本身造成很大压力。”</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp;&nbsp;据悉，本次参与活动的13个小学生是来自泡桐树小学环保社团“循乐童年”的成员，他们中很多人都在平时学习中掌握了不少环保知识，但亲自到垃圾填埋场看看都还是第一次。其中，六年级的万小诺表示，“亲眼看到垃圾填埋场很震撼，数不清的垃圾让人感到环保很重要。杜绝浪费粮食、不随地乱扔垃圾、注意垃圾分类，这些都是我们可以从身边做起的事。”</span></p><p>\n	<span style=\"line-height: 21px;\">&nbsp; &nbsp; &nbsp; 此外，孩子们也表示要向“成都榜样”学习，“像田叔叔一样，用自身的力量去影响更多人参与环保。”</span></p><p><br/></p>');
INSERT INTO `article` VALUES (50, '1532157831163', '10位警察获得“成都榜样·最美警察”殊荣', '人物榜单', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都日报</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160112/1452585919.jpg\"/><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 历时4个月，经过群众网络投票和社会各界代表评选，首届“成都榜样·最美警察”评选活动于1月11日落下帷幕。抓捕歹徒时英勇负伤的“黑豹”特警朱伟华、日夜奋战在打击街面盗抢犯罪一线的武侯巡警大队便衣中队中队长徐飞、英勇负伤体内仍留下数根钢针的“钢铁侠”成华公安民警罗健等10位民警，从30位候选人中脱颖而出，获得“最美警察”的荣誉。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 1月11日下午，在成都广播电视台一号演播厅，由成都市公安局、成都市精神文明建设办公室联合主办，由成都传媒集团、成都市广播电视台协办的2015年首届“成都榜样·最美警察”颁奖典礼圆满举行。市委常委、政法委书记王忠林出席并致辞，副市长、市公安局局长左正上台为获奖的特警朱伟华颁奖。来自成都各行各业的群众代表、警风监督员代表、公安民警及家属代表共400多人见证了这一感动人心的时刻。据了解，颁奖典礼的实况录像将于本周内在成都电视台黄金时间播出。</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\"><strong>受伤事迹让观众落泪</strong></span></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 特警朱伟华荣耀“归队”</strong></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">2015年5月27日，成都公安“黑豹”特警队接到指令，火速赶往德阳什邡市抓捕一名持枪歹徒。队员朱伟华接到命令，和市公安局刑侦局侦查三处侦查员王刚一起，负责现场外围的侦查工作。</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 强攻尚未开始，有所警觉的嫌疑人徐某便从三楼跳至二楼平台，并随后逃向废弃学校，朱伟华发现了徐某的踪迹后，果断鸣枪示警，“我亮明身份，喊嫌疑人缴械投降。”凶残的徐某一边逃窜一边拔枪射击，朱伟华开枪还击并紧追其后。在追击过程中，徐某突然窜出射击，朱伟华腿部、腹部连中两枪。受伤倒地的朱伟华匍匐还击，徐某被击中后逃入一屋内藏匿。增援的特警随后赶到，在多次喊话无效后，依法将徐某击毙。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 在颁奖典礼上，朱伟华的事迹被制作成短片播放，当他的爱人出镜接受采访，回忆当时的心路历程时，现场响起热烈的掌声，不少台下的观众都忍不住落下泪水。更让大家感动的是，20名全副武装的“黑豹”特警队员，在演播大厅集合，以最高的礼节迎接兄弟朱伟华荣耀“归队”。“虽然特警队员都带着黑色面罩，但是出场那个气势，战术动作那么标准，给我很震撼的感觉。”作为颁奖嘉宾出场的站东小学学生张睿，在台下看到特警帅气的出场表演，有些激动。</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">虽然接下来朱伟华还将接受腿部手术，但他表示他舍不得“黑豹”这个团结温暖的集体，他把自己得奖的这天当作自己的“回归日”，朴实的山东汉子拄着拐杖，再次站在了“黑豹”特警队伍中。正如颁奖辞上写的：“同战友血战顽敌，无愧特警队的威猛黑豹……”站在台上的朱伟华当得起“黑豹”的威名。</span></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 和朱伟华一起上台的，还有他心目中最美的妻子，经主持人介绍朱伟华的爱人也曾被评为“最美护士”。站在舞台上，朱伟华的妻子聊起了当时的心情，“那一刻好怕永远失去丈夫”，虽然经历了惊心动魄和苦痛，但当丈夫提出将继续归队干特警工作时，他美丽的妻子仍然坚定地支持着丈夫。</span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20160112/1452584407.jpg\"/><br/></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\"><strong>“城市英雄”刘军上台</strong></span></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 为“钢铁侠”颁奖打气</strong></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">颁奖典礼上，一名特殊的颁奖嘉宾引起了全场不小的轰动，坐着轮椅、身体尚未完全康复的“城市英雄”刘军被推上舞台，在这个特殊的日子里，他赶来为同样是在抓捕歹徒中英勇负伤的“钢铁侠”罗健颁奖。</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2013年8月，刘军在抓捕中遭遇凶徒暴力拒捕，身受重伤。本报连续推出多期报道，刘军的事迹迅速传遍这座城市，许多群众自发到医院看望他，大家共同祈祷“城市英雄”能早日站起来。爱让奇迹发生，身体遭遇重大创伤的刘军，凭着自己的坚强意志，从病床上站了起来。记者在颁奖现场看到，目前刘军已经可以自己步行，“能走的时间越来越长，现在还不能坚持太久。”刘军告诉记者，“成都榜样·最美警察”颁奖对他也有特别的意义，获奖者里有他多年的兄弟吴世红，他自己也有心里话想说给大家听。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 在为罗健颁奖后，刘军深情地说：“不仅要祝贺罗健，更要感谢罗健为城市平安的努力，感谢全社会给予成都公安的关注和支持，感谢大家对他个人的关心。”坐在轮椅上的刘军用一个标准的敬礼，将一名老警察的心声传递给现场的每一个人。来自成华区的居民何锐在现场看到这一幕后双眼闪着泪光，“看到这些为城市平安洒热血的警察，内心非常感动，希望他们的伤早日康复，祝福他们。”</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">获得“成都榜样·最美警察”特别奖的吴世红，因为仍在养伤没有出现在颁奖典礼现场，大家通过大屏幕看到了吴世红的最新情况，“希望兄弟能快点好起来，我们都早日重返岗位。”刘军坚定地说。</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 从前来参加颁奖典礼到在后台候场，刘军的出现总是惹人关注，所有的警察同事看到他，都会亲切地称呼一声“刘哥”，询问一下他的康复状况，这名老警察是成都警队的骄傲，“他的精神应该能够代表成都公安的精神，看到他就看到这支队伍的战斗力，我们也感觉到了这座城市的平安。”来自武侯区的居民廖雪对记者说。</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;<strong>&nbsp;</strong></span><span style=\"line-height: 28px;\"><strong>“烈火雄心”的救人英雄</strong></span></span></p><p>\n	<span style=\"line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 现场看孕妻视频潸然泪下</strong></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">2015年4月13日，两名驴友在银厂沟失足坠崖。而当时赶往事发地点对两人进行火线救援，冲在第一线的，便是成都市公安消防支队特勤大队一中队担任中队长助理的狄旺。当天，在接到求救命令后，狄旺与战友们火速赶往事发地点。到达后，经过一番艰难的搜寻，终于找到了被困者的位置，那是一面非常陡峭的悬崖。狄旺凭借多年的救援经验，先仔细地勘察了地形，选好路线，然后绑上绳子一边往山崖上爬，一边做好支点，让另一位战友顺着他的步子向上攀登。经过几个小时的艰难攀爬，狄旺与战友们终于到达了1名生还者处。他一边为生还者讲解如何利用绳索到达山底的营救方案，一边细心观察着对方。见被困者情绪绝望，狄旺通过聊天的方式不停向被困者加油打气，“我问他是哪里人，什么时候上山的等等，聊着聊着他就不那么紧张了。我也一直告诉他，相信我们，不会有事的。”经过整整11个小时的战斗，狄旺和战友最终将这名生还者救出。</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 类似这样的经历，在狄旺8年的军旅生涯中还有很多。8年时间里，凭着自己的一双手、两条腿，狄旺从火场、废墟、泥石流中挽救了上千人的性命。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2015年年初，狄旺与相恋3年的女友步入了婚姻殿堂，但是，在老家仅仅待了一个星期，狄旺就回到了工作岗位。后来妻子怀孕了，他也没能回去看看。在昨日的颁奖典礼现场，一条由正在怀孕的妻子所录制的视频让这位烈火雄心的铮铮汉子在舞台上忍不住流下泪来。视频里，怀着孕的妻子一直冲着镜头笑着，展示着铺在面前早已为孩子准备好的衣物。镜头这端，看着一直没能陪伴在身边的妻子，狄旺忍不住红了眼眶。“一直觉得很对不起家人，但这是我的工作，他们的理解让我能够更加安心地执行每一次的救援任务。”</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">本报记者 俸奎 李旻 曹宇阳 摄影 杨永赤</span></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 21px;\"><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\"><strong>首届“成都榜样·最美警察”</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong>获奖名单</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 温江区公安分局公平派出所所长 &nbsp;陈晓林</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">武侯区公安分局巡警大队便衣中队中队长 &nbsp;徐飞</span></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 锦江区公安分局出入境管理大队民警 &nbsp;</span><span style=\"line-height: 28px;\">黄涔涔</span></span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">交警二分局二大队副大队长 &nbsp;雷强</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都市公安消防支队特勤大队一中队中队长助理 &nbsp;狄旺</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">天府新区公安分局兴隆派出所教导员 &nbsp;徐苑榕</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 青羊区公安分局刑警大队副大队长 &nbsp;阮立波</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都市公安局经济犯罪侦查支队三大队大队长 &nbsp;林正良</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">成华区公安分局双水碾派出所民警 &nbsp;罗健</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都市公安局特警支队一大队民警 &nbsp;朱伟华</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\"><strong>特别奖一名</strong></span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 金牛区公安分局光荣派出所民警 &nbsp;吴世红</span></p><p><br/></p>');
COMMIT;

-- ----------------------------
-- Table structure for event
-- ----------------------------
DROP TABLE IF EXISTS `event`;
CREATE TABLE `event` (
  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '事迹的ID，自增',
  `event_time` varchar(255) NOT NULL COMMENT '推荐时间',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '正文内容和图片链接',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '关键词',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=43 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of event
-- ----------------------------
BEGIN;
INSERT INTO `event` VALUES (38, '1532262099787', '<p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\"><img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150528/1432808357.jpg\" style=\"border: 0px;vertical-align: middle\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\"><span style=\"color: rgb(51, 127, 229)\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color: rgb(51, 127, 229)\">受王东明书记委托，省委副秘书长马波接见电子科大优秀学生代表（左一为刘沈厅）</span></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 刘沈厅，男，1988年3月生，中共党员。电子科技大学微电子与固体电子学院研究生二年级学生，电子科技大学“微梦党建委员会”主席。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 勤勉刻苦，勇于进取的奋斗精神。该生以高中三年全额奖学金的实力考入电子科技大学英才学院，成绩一直保持年级第一名。本科毕业，获得清华大学和国外名校的保送资格，但家庭突然遭遇变故----母亲突发疾病不幸离世。家庭的变故没有打倒刘沈厅，反而使他更加的坚强和乐观，为了更好的照顾父亲，他选择继续留在成都进行深造。本科和研究生期间，先后获得国家奖学金、国家励志奖学金、研究生一等奖学金、研究生国家奖学金、人民一等奖学金。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\"><img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150528/1432808936.jpg\" style=\"border: 0px;vertical-align: middle\"/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\"><br/></p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 他积极参加各种学术竞赛和交流，先后获得全国数学竞赛四川赛区三等奖、全国英语竞赛三等奖、电子科大与新加坡义安理工学院辩论赛一等奖，发表了Influence of graphite oxide drying temperature on ultra-fast microwave synthesis of grapheme、Facile Synthesis of Phase Pure Iron Pyrite (FeS2) Nanocrystals for Low Cost Photodetectors（JMCC）等高质量论文。2012年，他组织并参加Virtual Conference on Nanoscale &nbsp;Science and Technology (VCNST)国际会议并作口头报告，得到参会专家学者的高度认可。此外，他还申请国家发明专利一项：砷化镓量子环红外光电探测器及其制造方法。因为在校期间的优秀表现，他多次被表彰为优秀学生干部，并荣获2012年校级优秀毕业生。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp;&nbsp; 乐观坚韧，战胜困难的人生态度。母亲的离世给家庭带来了沉重的打击，面对这样的打击，刘沈厅没有消沉和放弃，而选择了担当---用男子汉的肩膀承担起照顾父亲和家庭的重任。他发过传单，做过推销，从事过各种各样的兼职。生活的艰辛让他明白只有将学到的管理知识与实践结合起来，才能创造更好的效益。2011年，在学校的鼓励和帮助下，依托国家鼓励大学生创业的好政策，刘沈厅和朋友成立了成都欧朗装饰材料有限公司，从事家装软包业务。在做好研究生学习的同时，他自学财务管理、市场推广和客户维护，从来没有周末和节假日，数不尽的酸甜苦辣、道不尽的困难挫折，刘沈厅都坚持下来了。现在公司运行良好，总部也搬迁到新世纪环球中心，员工从最初的几个人发展到20多个员工，注册资本由最初的50万元增加到500万元。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 在经营公司时，刘沈厅遵循“守法经营、诚信服务”的理念，获得了市场和客户的认可。公司先后被成都市工商局授予成都市“重合同守信用”企业、3A级信用企业、5星级客户满意单位等荣誉称号。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 善良质朴，敢于担当的时代使命。感恩是刘沈厅品质里最闪光的一点，他常说“我来自地震灾区，经历了家庭变故，是国家、社会和学校帮助我走出困境，让我看到希望和未来。穷则独善其身、达则兼济天下。穷时有‘穷’的报效国家方式，达时有‘达’的报效方式”。在公司没有成立前，他申请了导生（电子科技大学一项高年级学生引导低年级学生的举措，选拔优秀的高年级学生对大一新生进行指导和帮助），用行动感染学生、用力量影响学生，被低年级学生亲切地成为“厅哥、厅长”。公司成立后，刘沈厅积极为学校毕业生提供就业岗位，并为学弟学妹们的职业成长创造优厚的条件，现在有的学弟们已经成长为公司的骨干力量。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 和学生的长期接触过程中，刘沈厅对大学生思想政治教育有了一些自己的认识和探索。作为党员，他认为应该充分利用党建平台引领学生思想政治教育。2013年，刘沈厅在学院党委书记李雪梅等老师领导下谋划成立了微梦党建委员会，以“传递微力量、成就大梦想”为宗旨，以身作则做好党员的先锋模范作用。为了活动的更好开展，刘沈厅决定每月从自己公司的收益里提取一部分作为微梦公益活动的经费赞助。微梦党建委员会成立以后，刘沈厅带领同学开展了形式多样的公益推广和实践活动，先后举办了“百万微笑”、“毕业生义捐、义卖、义换”、崇州鸡冠山小学爱心志愿服务、救助温江车祸女孩吴双等活动，在全校和社会上引起了极大反响。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 2014年3月，电子科技大学与栋梁工程联合成立“栋梁工程基地班”（钧业班），探索在理工科高校背景下培养一批具有担当意识、领袖气质、人文底蕴、创新精神的优秀青年学子的方式。刘沈厅得知后，非常激动和赞同，捐赠10万元设立“启梦”奖学金，鼓励学弟学妹们追求梦想、实现梦想。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 在不了解的人看来，刘沈厅通过自己的努力创造了物质收益，是学生中的“土豪”。但现实中的他非常节省和朴素。使用的是一部廉价的红米手机，能坐公交时坚决不打车……在与“栋梁工程基地班”的学生交流后，一位叫翁卓的学生在他的成长日记里这样写道：“他背后的故事还有太多太多：失去最亲之人彻骨之痛令其成长与坚强；善于利用分分秒秒与一切手边资源令其在学术领域超霸封神；为实现强国梦放弃留学名校而坚守本校同时创业不断打拼；因对国人自爱程度甚不及美国友人对我们的关切程度而下决心投身公益……太多的辛酸苦累，太多的毅然决断。他的故事因离我们这样迫近触手可及而弥足珍贵。一位仅仅超出我们几岁的学长，与我们就读于一所高校，却完成了令我难以想象的一件件看似平凡的小事。他热爱我们这群学弟学妹，捐助资金的同时，他也是另一个行政班的导生。身体力行，说做就做，我想这便是这位刘沈厅学长最可敬可爱之处吧。”</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 2014年7月，作为电子科技大学的优秀学生代表，刘沈厅将带领团队赴井冈山参加团中央主办的“井冈情中国梦”专题社会实践。</p><p style=\"margin-top: 0px;margin-bottom: 0px;padding: 0px;color: rgb(81, 81, 81);font-family: verdana, 微软雅黑, sans-serif;font-size: 14px;white-space: normal\">&nbsp;&nbsp;&nbsp; 2014年4月30日，四川省委书记王东明回信电子科技大学“栋梁工程基地班”（钧业班）。省委副秘书长马波，省委教育工委委员、省教育厅党组成员李光华，团省委副书记张荣来校递送王东明书记回信并召开五四座谈会，作为全校参会的5名优秀大学生代表之一，刘沈厅在会上说了这样了一段话，或许可以窥见这位经历苦难而仍怀梦想的青年人的心里：曾经的他也与周围众多同学一样，仅仅将理想局限于“房子、车子、票子”上面。但家中突逢变故，生命中至爱的母亲突然离我而去，只给他留下了无私的爱与思念，促使他从新思考生命的价值和意义。回想自己在国家、社会、学校和亲人帮助下走过的那些路，通过身边优秀青年人的引导，他认知到以往的“自私、狭隘与精神的匮乏”，学会了“知恩、感恩、重责任”；学会了对他人的关怀，对社会的奉献，对国家的担当；学会了将自己的追求与国家的需要紧密结合起来。他说：“我有梦想，我的梦想只有与“中国梦”同频共振时，才能更好实现。”</p><p><br/></p>', '刘民主：勤勉刻苦 自主创业的青年典范', '勤勉刻苦 自主创业');
INSERT INTO `event` VALUES (39, '1532262636691', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://by.chengdu.cn/uploads/image/20150729/1438161282.jpg\" alt=\"\" style=\"border: 0px; margin: 0px; vertical-align: middle; line-height: 1.5;\"/><span style=\"line-height: 1.5;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"line-height: 1.5;\"><br/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p><span style=\"line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 第五届全国道德模范评选表彰活动正在如火如荼的进行中，成都市共有6位市民入选全国道德模范候选人。他们分别是入选助人为乐类别的：秦坤、董贵生、刘源；入选诚实守信类别的：赵庆珍；入选见义勇为类别的：吴永秀和入选敬业奉献类别的：芮银超。</span></span></p><p><span style=\"color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; line-height: 1.5;\"><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 金牛区电视台的“80后”编导秦坤</span></strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;一次偶然机会中，秦坤接触了手语，此后，便与公益结下了缘分。2011年12月，他参与创办推广手语的节目，此后，又继续创办公益团队，帮助特殊群体。“做公益的过程中充满着快乐，自己也非常享受这个过程。”这是秦坤常挂在嘴边的话。</span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 与公益结缘 他钟情于手语</span></strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;黝黑的皮肤、锐利的眼神、亲和力的笑容……记者眼前的秦坤是金牛区电视台的一名编导，这位长相酷似体育明星林丹的80后小伙子热心公益，他参与创立《欢行手语》栏目，通过手语的推广，让更多人关注到聋哑人这个特殊群体。提起最初学习手语的原因，秦坤笑得格外腼腆，“聋哑人拥有和普通人一样的梦想，我想帮助他们实现。”</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 秦坤告诉记者，与公益结缘是源于自己4年前的一次偶然经历。“2011年年初，我作为志愿者参加了一个金牛区电视手语大赛，当时就被几百人一起表演手语的场景深深震撼了。”秦坤回忆道。这次活动以后，一个问题一直困扰着他，“如何走进残疾人的内心，让他们真正能和普通人无障碍交流呢？”</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2011年中下旬，秦坤尝试着去成都市内的特殊教育学校，跟聋人孩子一起学习手语。“最开始去学习的时候，手语没有想象中那么容易。”秦坤说，比如“动物”两字的手语，要将双手的食指互碰一下，然后分开并张开五指。“某些复杂的地方，我就反复练习，主要是改变自己的固有思维方式。”通过和这些孩子交流的过程中，秦坤发现孩子们缺少的未必是一个书包，一套文具，他们需要的是更多了解外面的世界，如果我们每一个人都能了解和运用一些手语，那我们与聋哑人之间将没有距离，没有障碍。</span></p><p><span style=\"line-height: 2;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;创新手语教学 推出公益项目</span></strong></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 在学习手语的过程中，秦坤创新了学习方法，他和几个朋友自费拍了些公益短片放在网上。“最初发在网上的视频点击率并不高，效果很不理想。”秦坤就琢磨着如何以一种更轻松、更快乐的方式来推广手语。2011年12月，秦坤参与创办的以手语推广为主的《欢行手语》栏目正式在金牛有线电视台推出，为了让更多的人接受手语，秦坤和他的团队摒弃了单调枯燥的传统手语教学方式，以新媒体网络作为播放平台。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;“并且，在手语表现形式上加入一些面部和肢体语言的辅助，或用唱歌、舞蹈、模仿情景剧的手段等，更能帮助手语意义的全面表达。”秦坤说，这样的形式一出，立即得到大家的喜爱，于是欢行手语团队又做了更多的视频放在网上，慢慢地拥有了众多粉丝的关注，“我们在手语学习中很快乐，所以把我们的手语推广项目叫做欢行手语。”2012年12月，秦坤参加《中国梦想秀》成为“最佳追梦人”，将欢行手语推向了全新的舞台。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 除了推广手语的视频形式要创新，更需要的就是将热爱手语、推广手语的联盟集合在一起，形成合力。因此，2013年5月9日，秦坤带领团队在清华大学启动了全国高校手语协会联盟及“欢行手语 欢行中国”项目量。“做公益的过程中充满着快乐，自己也非常享受这个过程。”秦坤说道。2014年，秦坤通过给聋哑孩子们播放一个大海的视频，灵感浮现在脑海。“当我看到他们激动的眼神的时候，我就明白了一切。”秦坤突然意识到，“帮助孩子们健康成长，不仅仅是送去一大摞的书包、文具那么简单，更重要的是走进孩子们的内心，去了解他们真正想要的是什么。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;于是，秦坤通过多方努力号召社会爱心人士、爱心企业积极参与，最终帮助德阳特殊教育学校、成都特殊教育学校的20多个聋人孩子们实现了看海的梦想。“看到小孩子们梦想成真时纯真的笑容，心里有一种被融化了的感动。”今年，欢行公益又陆续推出了普特共融项目“欢行爱棒球”，让农民工子弟学校、特殊教育学校的聋人孩子们感受到了棒球运动的快乐，拓宽他们放眼世界的角度。</span></p></span></p><p><br/></p>', '秦坤：用快乐手语为折翼天使插上翅膀', '快乐手语');
INSERT INTO `event` VALUES (40, '1532263187553', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-stretch: normal; line-height: 30px;\"><span style=\"line-height: 2;\"><img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150529/1432864043.jpg\" style=\"border: 0px; margin: 0px; vertical-align: middle;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-stretch: normal; line-height: 30px;\"><span style=\"line-height: 2;\">江艳，女，中共党员，成都市青白江工商局公务员</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center; font-stretch: normal; line-height: 30px;\"><span style=\"line-height: 1.5;\"></span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 江艳坚持公益18年，用行动将爱心传播，常年活跃在敬老院、偏远山村，先后被成都市团委、市志愿者工作委员会、市青年志愿者协会联合表彰为“2008年度优秀青年志愿者”，被青白江团委、青白江志愿者工作委员会表彰为“抗震救灾先进个人”，被成都市妇联评为成都市“2014我身边的优秀女性”。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 1996年，江艳开始资助第一个贫困学生，和两个朋友一起承担了小男孩小学期间的全部学习费用。2001年，她参与“15元计划”资助甘孜州孤儿学校的学生。2005年，她与几位热心公益的团干朋友一起发动网友开展志愿者服务活动，成为“成都阳光彼岸爱心合作社”（“成都义工网”前身）首批志愿者。她以极大的热情投入到志愿者服务中，很快便成为“成都义工”的骨干。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;在江艳的影响和带动下，老公潘馨也加入到义工队伍中来，两人经常一同参加义工活动。女儿潘思睿5岁起，就在父母的带领下开始参与关爱敬老院老人、留守儿童、结对贫困学生的活动，在公益活动中得到锻炼。“5.12”地震期间亲自为同龄人募集新年礼物，并在父母的带领下到地震重灾区绵竹市金花镇捐赠新年礼物。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2008年8月，江艳与4名义工朋友一起深入凉山州布拖县开展“牵手布拖，传递文明”活动，当他们冒着极大的风险，承受着身体的极限，克服难以预料的重重困难，艰难地来到布拖县收古村时，展现在眼前的情景令人震惊，那里的基础卫生条件极差，大多数孩子都衣不遮体。虽是秋天，但地处高原的凉山天气异常寒冷，这群孩子中有一位10多岁的盲女孩叫李子么儿科，只穿了一件单衣，光着脚站在雨地里，冷得浑身直打哆嗦。江艳见此情景，上前紧紧把盲女孩搂在怀里，眼泪夺眶而出。江艳和其他义工忙找来衣服和鞋子给李子么儿科穿上，盲女孩感动得热泪盈眶。从那以后，江艳便多了一份牵挂，牵挂这群大山深处的孩子们。2008年11月，江艳带领包括自己的丈夫和女儿在内的13名义工再次到布拖开展公益援助，女儿潘思睿触动很大，看到同龄人背着用猪饲料口袋制作的书包，她的心久久不能平静，回家后便发动10名小朋友在公园开展义卖，向路人介绍她眼中的布拖县，争取爱心人士对布拖贫困学生的帮助，现场共募集近300元善款，将这笔钱交到“成都义工”网负责人老黑叔叔的手中，参与为布拖孩子买书包的行动。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;江艳先后4次深入大凉山，和志愿者朋友一起，为布拖的孩子们送去学习用具、御寒衣物、清洁卫生用品，并协助建立“爱心书屋”，教孩子们洗脸、洗手，培养彝族同胞卫生习惯。通过大家6年多的努力，当地孩子的卫生习惯得到了极大改善，生存状态有了明显改观。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 2007年，江艳全家在参与关爱留守儿童活动中，认识了清泉镇一名特殊留守儿童罗然。罗然父亲是服刑人员，妈妈是残疾人。她家的居住条件很差，简陋的三间房屋没有电灯，也没有窗户。江艳看在眼里，疼在心中，决定帮助这一家。她先找到自己做工程的同学，拉来混凝土平整了房屋地面，新砌了水泥院坝，又和“成都义工网”朋友们联系电力公司为罗然家拉了用电专线。从此，江艳一家将罗然当成家庭成员，对她倾注了更多的关爱。在江艳一家和“成都义工”志愿者们的关心、帮助下，罗然得以健康成长，2013年以优异成绩考上了大学。如今，罗然把江艳一家当成了自己的第二个家，取得成绩会第一时间打电话回家分享快乐，遇到困难会在自己无法克服时会寻求江阿姨的帮助。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 除了罗然，江艳一家还长期照顾着两名特殊留守儿童曾小波和庄代云。小波的父亲曾是服刑人员，为让孩子心理健康不受影响，江艳让丈夫专门给处于青春期的小波送去有关青春期教育的书籍，帮助其解除青春期困惑。庄代云是名孤儿，父亲在他出生前就去世了，母亲生下他不到一个月就离家出走了，他和奶奶相依为命，而奶奶又常年多病，生活比较困难。江艳知道后，将他确定为第四个长期帮扶的对象，寻找多种途径帮助这位孤儿，让他感受家的温暖，帮助其健康成长。如今，这些受资助的孩子都接受了良好的教育。</span></p><p><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;“5.12”大地震后，江艳全家和志愿者一起多次冒着余震危险前往北川、绵竹、什邡等重灾区运送救灾物资，积极参与救援工作，帮助灾区群众恢复生产。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: justify; font-stretch: normal; line-height: 1.5;\"><span style=\"line-height: 2;\">&nbsp; &nbsp; &nbsp; 如今，做义工已成为江艳日常生活中的一部分，不求回报，不求名利，做自己力所能及的善事。这就是他的生活状态和目标追求。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: justify; font-stretch: normal; line-height: 1.5;\"><span style=\"line-height: 2;\"><br/></span></p><p><span style=\"font-stretch: normal; line-height: 1.5;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\"><strong>好人寄语：</strong>赠人玫瑰，手有余香。简单义工，简单快乐。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: justify;\"><span style=\"line-height: 2;\"><br/></span></p><p><span style=\"font-stretch: normal; line-height: 1.5;\"><p><span style=\"line-height: 2;\"><strong>道德点评：</strong>江艳是幸福的，她的幸福来自于他的爱心。她的爱心感召着她周围的亲人以及朋友，助人为乐，一件一桩，仅凭一片赤诚。江艳用自己的行动对中华道德操守坚守着并发扬光大着，她的这种爱心善举，增强了人格的魅力，凝聚了文明的精粹，淋漓地演绎爱心，无私地践行文明的义举，让生命的花园吐露芬芳</span></p><p><span style=\"line-height: 2;\"><br/></span></p></span></p><p></p><p><br/></p>', '基层公务员江艳：坚持公益18年 爱心感召亲人朋友齐参与', '热心公益');
INSERT INTO `event` VALUES (41, '1532263552490', '<p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;<img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150529/1432873319.jpg\" style=\"border: 0px; margin: 0px; vertical-align: middle;\"/></p><p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">应凯同志遗体告别仪式</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\"><span style=\"color: rgb(229, 51, 51);\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong></span><span style=\"color: rgb(229, 51, 51);\"><strong>个人简介：</strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　应凯，男，汉族，1987年10月生，三级警司，四川省双流县人，2012年7月参加公安工作，系彭州市公安局交警大队勤务四中队民警，11月21日6时46分因患结肠癌永远地闭上了双眼，享年27岁。　　他身患结肠癌晚期，却对外只字不提，只因热爱这份平凡工作，只要能再站在岗位上，就是他唯一的精神寄托；他痛到无法开车，唯有恳求妻子代驾，只要能再回到路面上，就是他最好的镇痛剂；他明明是个孝子，但选择“抛弃”父母，只要能再穿上警服，就是他弥留之际的唯一希望；他被病魔百般折磨，却丝毫不改执勤时标志性的笑容，只要能守护一方安畅，就是他与死神拉锯的源源动力。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　27岁，在这个即将扬帆启航的美好时刻，他却离开了......离开了牙牙学语的女儿，离开了新婚不久的妻子，离开了年迈多病的父母，他的离开太过突然，他曾那样信誓旦旦地向妻子许诺：选择我，你绝对不会后悔，我会让你永远幸福。可是他却“背弃”了这个诺言，妻子哭泣着说他是个“骗子”。从警以来，应凯休过最长的假是婚嫁，短短的20天却仍时常收到工作上的电话，这让好不容易期盼“两人甜蜜时刻”的妻子埋怨纷纷，但是看着丈夫接听电话时熠熠有神的神情，她也就淡然了。从警是应凯从小的梦想，2010年从贵州师范大学毕业后，他在达州市宣汉县任特岗教师，可是心中那份梦想的火苗却从未熄灭，在2012年通过全省政法干警选拔考试后，他如愿地穿上了这藏青色的警服，许下了“全心全意为人民服务”的庄严承诺。</p><p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\"><img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150529/1432873233.jpg\" style=\"border: 0px; margin: 0px; vertical-align: middle;\"/></p><p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">应凯参加抗洪救灾</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　两份诺言注定不能相持，记得2013年7月9日，彭州市遭遇特大洪灾，许多村民被困洪水之中。凌晨接到队里紧急集结的电话，应凯二话没说、一个翻身，披上衣服就奔出了家门，将刚刚怀孕四个月的妻子独自留在家中。凌晨4时40分到达了丽春镇碧鸡村的应凯立刻配合队友实施救援，带头渡过湍急的河水前往灾民受困区域。湍急浑浊的洪水，不满青苔的石头、尖锐零散的瓦片，加之电闪雷鸣、滂沱大雨让这条大约百米的距离变得异常遥远，但是应凯毫不退缩，牵着救生绳头，拉起希望的生命线，组织灾民有序通过绳索到达安全地点。一位老人行动不便，应凯二话不说脱下了自己的救生衣披在老人身上，将老人背在背上，淌过冰冷湍急的河水，直到将老人送到其家人身边，又转身走向河对岸。救援中，破碎的陶罐碎片将他的雨靴划破，脚划伤，他全然不顾伤痛，往返河岸十余次，硬是靠着顽强的意志和坚忍不拔的毅力同战友们一起救出了受困村民40人，疏散群众100人。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　数不清是有多少次，应凯就这样把妻子留在家中，又是有多少次刚刚走到家门的脚步又匆匆掉头回到了工作岗位，就连妻子生产的当天，他也“姗姗来迟”，因妻子心脏检测不能顺产，必须由应凯签字同意剖腹才能实施手术，久久未到产房的应凯被医生“狠狠”地进行了批评教育。妻子有怨过他的不能陪伴，每次应凯都摸着妻子的手说：“以后老了，我就一直陪着你，你赶我我也不走，我是你一个人的……”话语还清晰在耳边，诺言却消逝在了风中。如果在给应凯一次重新选择的机会，他会如何选择？那银光熠熠的警徽，还是父母妻儿的声声呼唤，这个答案在他放弃老师那份安稳职业，在他一次一次过门不入的时候，就已经为这个答案给出了一个肯定的句号。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　2014年新年刚过，应凯时常觉得胃腹疼痛，可是为了开展好双超整治行动，他只是简单地吃了点儿胃药，又投身到了工作中，寒冬中的马牧河执勤卡点冷风刺骨，他却毫不在乎，点点星空下认真排查着每一辆过往的货车。越来越频繁的阵阵疼痛有时让他连腰都不能直立，需要大口地喘息和用力地按压才能挺过去，发现异状的大队领导强制让他停下手中的工作去检查。经过医院检查，确诊为胆囊管堵塞，4月14日胆囊切除手术刚刚结束，医生发现切除物内有许多红点，噩耗袭来，已是结肠癌晚期！</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　知道病情的当晚，应凯跪在父母的面前，泣不成声地说着对不起，本来该当儿子的照顾您们，可是儿子可能无法尽孝陪伴。妻子劝说着要不和大队领导或同事说说，可是妻子的话还没说完，平时随和温柔的应凯却突然发起了脾气，“如果你敢告诉领导，我就悄悄走了。”面对丈夫的执意，妻子只能默默地抹着眼泪，她知道他是怕给队里添麻烦，他就是这样一个闷头做事的“傻瓜”，什么事情都只知道自己抗。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　休息了一段时间的应凯很快又回到了工作岗位上，站卡点、出现场、处事故，每项工作他做得和以前一样认真仔细。只是他用手用力按压腹部的频率逐日增加，脸是一种病态的黄，短短一个月时间，整个人就已经瘦得有些脱形，有时在书写处罚单时手甚至无法将笔握稳。熟悉的同事都以为是刚刚做完手术可能是伤了元气，还在恢复中，抢着想帮他多分担一些工作，但都被应凯婉言拒绝，他说：“我是一名交通民警，这是我应该做的份内事。”可谁也没想到，那时的应凯正独自忍受着多么难熬的痛楚。与他同期入警的三界派出所民警李海涛说，在应凯病重的时候曾给自己打过电话，说想一起聚一聚，当时由于周末加班无法抽身，只能拒绝，现在想想真的很难过，如果当时出去和他聊聊，应凯就不会一个人面对病痛的煎熬。警察，是一个守护的职业，但却往往不能守护身边最亲的人，他们太忙了，他们太累了，他们有太多的话无法倾诉。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　粉嘟嘟刚刚满1岁的女儿是应凯的宝贝明珠，所以在女儿还没出生时他就为女儿取名“朱铢”。记得女儿刚出生第二天一直哭个不停，又要照顾刚刚剖腹的妻子，不知所措的应凯急得流了鼻血，可就是这样一位深深爱着女儿的父亲，却不能好好陪一下可爱的女儿。为此，他是愧疚的，妻子朱琳说，在应凯走的前一天，女儿用小手一直拍打父亲瘦削的脸庞，妻子连忙拉住女儿的手，应凯却不在乎地说：“让朱铢打吧，很温暖的呢！”可是现在他却悄悄地消失在了女儿的生命中，在离去地最后一刻也未能再看一眼女儿那温暖的笑容。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　朋友们眼中的应凯是“缺席常客”，聚会时他要加班，邀约出去玩儿时他要加班，国庆五一大假时他就更忙了，有时连电话都是说一两句就匆匆挂了，大家都不明白一个小小的交通民警能有这么忙吗？面对朋友的“指责”，应凯总是习惯性的憨憨一笑，挠挠头也不辩解。朋友们对他的缺席也渐渐习惯，无奈地叮嘱他好好注意身体，可曾想喜欢打篮球阳光少年就这样突然的离开了。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">　　从警的道路艰辛、心酸，但是他走的却毅然的坚定，在应凯生命的最后几天，他时常牢牢握住警服上的警号“078310”，用尽全身力气紧握，手指骨节微微泛青，是啊，你让他如何舍弃这仅仅只佩戴了两年的警号，他一生的梦想为什么只能停留这短短的两年，他一直这样对妻子重复着：“琳，到时就简单一点儿，但是记得我必须穿着警服走，下辈子我还要当警察。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;</p><p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\"><img alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150529/1432877588.jpg\" style=\"border: 0px; margin: 0px; vertical-align: middle;\"/></p><p style=\"text-align:center;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">应凯生活照</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\"><span style=\"color: rgb(229, 51, 51);\"><strong>　　好人寄语：</strong></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; “人要记得感恩，要知恩图报。”是他做人的准则；“全心全意为人民服务”是他庄严的承诺；“我必须穿着警服走，下辈子我还要当警察。”是他最后的愿望。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\"><strong><span style=\"color: rgb(229, 51, 51);\">　　道德点评：</span></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;&nbsp;&nbsp;&nbsp; “为何从警、如何做警、为谁用警”-- 彭州市公安局交警大队“80”后民警应凯，用生命给出了答案。在人生的最后阶段，做了一个让所有人肃然起敬的决定：隐瞒绝症真相，把最后的日子奉献给自己钟爱的公安事业。&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal;\">&nbsp;</p><p><br/></p>', '好警察应凯：隐瞒病情坚守岗位 最后心愿穿警服离开', '好警察，服务人民');
INSERT INTO `event` VALUES (42, '1532265131641', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://by.chengdu.cn/uploads/image/20150624/1435113828.jpg\" alt=\"\" style=\"border: 0px; margin: 0px; vertical-align: middle; font-size: 12px; line-height: 1.5;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: justify;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(81, 81, 81); font-family: verdana, 微软雅黑, sans-serif; font-size: 14px; white-space: normal; text-align: center;\"><br/></p><p><span style=\"font-size: 12px; line-height: 1.5;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"line-height: 2;\">早上6时30分，家住武侯区玉林一巷的武宇华家就开始了一天的生活。由于儿子患有先天痉挛性脑瘫，武宇华收拾好自己，便帮儿子打理起来。7时40分，他背着儿子准时从家中出发；8时30分，等儿子上第一堂课后，武宇华回家整理家务；9时30分，武宇华再到学校，背儿子上厕所、换教室；直到中午12时，看着儿子吃完午饭上课后，武宇华才回家吃午饭……这是儿子的作息表，也是武宇华的作息表。面对生活的困难，已步入花甲之年的武宇华继续选择坚韧。他如今最担心儿子的婚姻问题，他希望儿子也能有个完整的家庭，一起幸福地生活下去。</span></span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong><span style=\"color: rgb(229, 51, 51);\">放弃事业照顾儿子</span></strong></span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp; 每天，武宇华不仅从家到学校至少往返4个来回，背儿子上下学，还要照顾儿子的饮食。“对于儿子，如果生活再来一次，我还是会做出这样的选择。”武宇华说。</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;1993年11月的一天，武宇华的儿子武晋出生了。他沉浸在初为人父的喜悦中，但快乐很快被一个坏消息粉碎——儿子被检查出患有先天痉挛性脑瘫。“这意味着他今后都不能像正常的孩子一样走路说话……”武宇华和妻子的眼泪都要流尽了，尽管家境并不富裕，两人还是决定不放弃，要好好照顾儿子一辈子。在铁路局工作的武宇华，当时是技术骨干，为了圆儿子的上学梦，他毫不犹豫地放弃了自己的事业，专心照顾武晋上学。</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp; 武宇华开始了背儿子上学的路程。这一背就是13年，从儿子7岁到20岁，儿子也从55斤长到55公斤。如今，武宇华背子上学的长度超过了2.5万公里，电动自行车也骑坏了3辆，但从未迟到过一次。</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp;&nbsp;<strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; 和儿子一起传递爱心</span></strong></span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;背着儿子去上学，武宇华的故事感动了很多人。有好心人给武宇华介绍临时工作，希望能增加他们的家庭收入；学校里，老师专门给武晋开“小灶”，帮助他补习没有弄懂的功课；还有好心人给武晋家送来一台电脑，希望喜欢捣鼓电脑的武晋在家也能学会应用、修理……</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp; 在父亲的教诲下，武晋在班级里也成为了一位“热心肠”。班里打扫卫生，尽管武晋无法参加，但他总会多带一块抹布，以备其他同学使用；班会课老师要求大家准备PPT，武晋忙里忙外找资料、打印，帮大家设计封面；武晋还会主动找迟到的同学“谈话”，看他们有没有什么需要自己帮忙的……</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp;&nbsp;面对众多好心人的帮助和资助，细心的武宇华悄悄地记了一本账。只有武晋看病的确困难，需要支出时才会动这个钱，更多的时候，他都把好心人的名字和他们的爱心存在那里。“希望有朝一日，我们能将这份爱心传递下去。”武宇华说，不管未来面临什么样的困难，全家人都会一起努力，帮助更多人，以回报社会对他们全家的支持。</span></p><p><span style=\"font-size: 12px; line-height: 2;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 谢红 图据市文明办</span></p><p><br/></p>', '武宇华：背儿子上学 父亲已经坚持13年', '13年如一日背儿子上学');
COMMIT;

-- ----------------------------
-- Table structure for event_pass
-- ----------------------------
DROP TABLE IF EXISTS `event_pass`;
CREATE TABLE `event_pass` (
  `ID` int(11) NOT NULL,
  `event_time` varchar(255) DEFAULT NULL,
  `admin_id` varchar(255) DEFAULT NULL,
  `admin_name` varchar(255) DEFAULT NULL,
  `content` text,
  `title` varchar(255) DEFAULT NULL,
  `keyword` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of event_pass
-- ----------------------------
BEGIN;
INSERT INTO `event_pass` VALUES (13, '1532177857248', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; line-height: 21px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150729/1438160147.jpg\"/><span style=\"line-height: 21px;\"></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<span style=\"line-height: 21px;\"><br/></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center;\">\n	<br style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\"/></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 第五届全国道德模范评选表彰活动正在如火如荼的进行中，成都市共有6位市民入选全国道德模范候选人。他们分别是入选助人为乐类别的：秦坤、董贵生、刘源；入选诚实守信类别的：赵庆珍；入选见义勇为类别的：吴永秀和入选敬业奉献类别的：芮银超。</span></p><p><span style=\"line-height: 21px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 国网四川省电力公司成都市高新供电分公司副总经理刘源，也是成都高新共产党员服务队队长。2005年担任党员服务队队长以来，十年如一日，用心血和汗水保证了党员服务队服务的29个社区、46户困难群众常年生活无忧，用实际行动赢得了社会和广大群众的赞誉。“辛苦我一人，幸福千万家。”刘源告诉记者，这是他的座右铭，激励着他一步步前行。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; 上任10年</span></strong></span></p><p>\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 扎实业务水平获好评</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“在工作中，服务质量一定要好，综合素质一定要过硬。”近日，记者来到成都市高新供电分公司，刘源正在公司的会议上发言。浓浓的眉毛，挺直的鼻梁……记者眼前的刘源，踏实沉稳，说起话来，头头是道。他除了担任国网四川省电力公司成都市高新供电分公司副总经理，也是成都高新共产党员服务队队长。“在党员服务队已经有10年了，为大家服务的同时，我感觉很充实。”</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “我们服务队负责成都市29个社区、10多万人的用电故障抢修，平均每月抢修500次以上，同时还要承担社区用电服务及扶贫济困工作。”刘源说，刚上任初期，并不轻松，为了保证优质服务质量，他一直在努力提升业务技能和综合素质。“在那段时间，一上班我就仔细观察队里老师傅们的‘一招一式’，一有时间就在电表旁琢磨技巧。”据刘源回忆，晚上，他就捧着《配电线路运行与检修手册》等专业书籍苦读。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color: rgb(229, 51, 51);\"><strong>解决困难</strong></span></span></p><p>\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 党员服务队获赞“电力110”</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;专业扎实了，业务也丝毫不懈怠。刘源带领党员服务队，认真服务，得到了居民的肯定和拥护。2009年初，玉林北路社区相关负责人来党员服务队反映：“我们社区内的电动自行车不断增多，线路私拉乱接现象突出，火灾隐患严重。”刘源立即带领队员深入社区，走访了解用电需求，广泛征求了社区居民的意见和建议。考察好相关情况后，刘源利用休息时间，带领服务队员在15个具备条件的社区成立了用电服务站，设立了16个电动自行车充电点，安装充电箱71个。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “谢谢你，刘队长，避免了安全隐患，我们现在也放心了！”居民们纷纷感谢刘源。“作为党员服务队队员，自己代表的不仅仅是一个人的形象，自己就是窗口，行动就是誓言。”刘源告诉记者，这是他一直以来的工作准则。在10年的服务期间，群众给服务队送来锦旗和感谢信200多封，亲切称党员服务队为“电力110”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">照顾老人</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;</span></strong><strong><span style=\"color: rgb(229, 51, 51);\">成了空巢老人的贴心“儿子”</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 除了在业务上贴心为群众服务，刘源也是多位空巢老人的贴心“儿子”，陈宝萍老人就是其中的一位。“我认识小刘有9年了，我家里的灯泡都是小刘帮着换的，这个插座是小刘买的，那个电饭煲是小刘送来的，还有我家里的新电线也是小刘和其他小伙一起帮我换的。”提起小刘，陈宝萍滔滔不绝地告诉记者。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;不仅陈婆婆，刘源为孤寡老人伍爷爷更换灯泡，为瘫痪卧床的杜爷爷问诊买药，为患有肝癌的谢婆婆募捐，陪孤寡老人杨爷爷聊天，为困难户李女士购买油米，这些都成了他的分内事。“把他们当作亲人，我的收获远比我付出的多得多。”刘源说，帮助别人自己心里也感觉很充实。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">热心公益</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;</span></strong><strong><span style=\"color: rgb(229, 51, 51);\">让自己和团队获得正能量</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 10年间，刘源主动带领队员走访社区，关爱孤寡老人、残疾人等社会弱势群体，积极参与社会公益活动，组织队员深入邛崃、大邑、崇州等“川电留守儿童之家”开展服务活动。“通过带领队员们做公益，我发现大家在工作中更有劲，更出色了。”刘源告诉记者，公益活动能让自己和团队获得正能量。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 此外，他还在服务队中创新开展了“电力连心桥系列活动”，特服对象拨一个电话键便能够连通服务队的“连心桥热线”。“我们每月都会主动上门为特服对象检查室内用电安全，提醒缴费充值。”刘源创新招募公司青年员工组成义工队伍，点对点进行服务。“辛苦我一人，幸福千万家。”刘源告诉记者，这是他的座右铭，激励着他一步步前行。(记者 安羽 摄影 马丁)</span></p><p>\n	<span style=\"line-height: 21px;\"></span></p><p><br/></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\">\n	</p><p><br/></p>', '张根齐：十年坚守 带出优秀党员服务队', '助人为乐');
INSERT INTO `event_pass` VALUES (14, '1532177943170', '4', 'admin', '<p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; line-height: 21px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150729/1438167680.jpg\"/><span style=\"line-height: 21px;\"></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 21px;\"><br/></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<br style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px;\"/></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 第五届全国道德模范评选表彰活动正在如火如荼的进行中，成都市共有6位市民入选全国道德模范候选人。他们分别是入选助人为乐类别的：秦坤、董贵生、刘源；入选诚实守信类别的：赵庆珍；入选见义勇为类别的：吴永秀和入选敬业奉献类别的：芮银超。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;<strong><span style=\"color: rgb(229, 51, 51);\"> “女侠”传奇人生</span></strong></span></p><p>\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>&nbsp; &nbsp; &nbsp; 她用大半辈子去救人</strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;吴永秀，1958年2月生，成都市成华区青龙街办白莲社区居民。在她的背后，有着一个响亮的美称——“救水女侠”，故事还得从1975年的冬天说起。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “救命啊！救命啊！”那年，一声凄厉的尖叫声划破了成都东风渠的天空，围观人群挤满岸边。正当千钧一发之际，忽然一个小小的身影“扑通”一声就跳进渠里，奋力向水中挣扎的落水者游去，这人正是吴永秀。那一年，吴永秀还是一个年仅17岁的小姑娘。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;也就是这样的一个冬天，开启了吴永秀此后长达40年的“救人”传奇人生。位于成都的东风渠始建于上世纪50年代，即便是较浅的地方也有两三米深，渠面看似风平浪静，实则急波暗涌，潜藏着不少的漩涡。渠水水温很低，人泡在里面极易抽筋，落水之后，很难获救。青年、老人、轻生的夫妇……一个个即将失去的生命，在吴永秀拼命营救下获得了新生。40年间，吴永秀共拯救24名溺水者，成为了东风渠当之无愧的“救水女侠”。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “我每次救人都是拿命去赌的。”这是吴永秀的大实话，可每次遇见有人落水时，她又会毫不犹豫地冲上去。在她54岁时，还与儿子一同救起了落水的婆孙二人。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">延续救人之路</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;</span></strong><strong><span style=\"color: rgb(229, 51, 51);\">她成立“义务阻泳队”</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 吴永秀自小在东风渠边长大，她非常清楚，跳进湍急的河水中救人是一件很危险的事情。每当面临拯救生命和未知的危险时，吴永秀的选择都很简单，那就是跳入东风渠中，将一条条生命拉上岸来。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;尽管东风渠存在潜在危险，但仍然有人会忽视安全，尤其在夏天，前往那游泳的人事络绎不绝。可单凭一人之力，很难阻止东风渠溺水悲剧的发生。于是在2004年，吴永秀组织成立了“义务阻泳队”，白天轮流巡逻，劝阻人们别到东风渠内游泳，同时还要及时营救自杀轻生者和意外落水者。10多年来，“阻泳队”力量逐渐壮大，队员已从过去的几个增加到了几十人。</span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; “虽然我现在年龄大了，体力不如从前，但是遇到有人出现生命危险时，我还是会第一时间挺身而出的。”在吴永秀家中，长期放置着救生绳、救生圈和救生衣，吴永秀说，准备这些就是为了应对随时发生的意外情况。</span></p><p>\n	<br/></p></span></p><p><br/></p>', '吴永秀：“救水女侠” 40年间东风渠救24人', '助人为乐');
INSERT INTO `event_pass` VALUES (15, '1532178030918', '4', 'admin', '<p><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 第五届全国道德模范评选表彰活动正在如火如荼的进行中，成都市共有6位市民入选全国道德模范候选人。他们分别是入选助人为乐类别的：秦坤、董贵生、刘源；入选诚实守信类别的：赵庆珍；入选见义勇为类别的：吴永秀和入选敬业奉献类别的：芮银超。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 战友飞行事故牺牲，因一句“将代你照顾母亲”的承诺，徐州小伙赵庆珍义无反顾到邛崃给程秀莲当了22年的“儿子”。在部队时天天打电话，隔三岔五给程妈妈寄钱；结婚专门找邛崃姑娘，在邛崃安家；转业也选择背井离乡，到邛崃和程妈妈一起生活。2001年,赵庆珍和妻子的爱情结晶降生了,孙子出生让程秀莲分外激动,主动请缨照顾孙子,忙得不亦乐乎。自从有了孙子,程妈妈感情也有了寄托,生活得幸福美满。如今,在别人眼中,赵庆珍和程秀莲母慈子孝,一家四口其乐融融。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">战友牺牲</span></strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(229, 51, 51); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><strong>&nbsp; &nbsp; &nbsp; 不叫干妈就叫妈妈</strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 1989年,徐州小伙赵庆珍和邛崃小伙余江同时入伍,成为空军飞行学员。两人在一个班学习生活，建立了深厚友谊。赵庆珍早年丧母,余江早年丧父,大家都说他们像亲兄弟一样。飞行任务危险性大，由于都在单亲家庭长大，赵庆珍和余江都挂念着家里。“要是你有什么意外，我就帮你照顾家里。”</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;&nbsp;一天，兄弟俩互相许下承诺，然而没有想到，意外真的发生了。余江在一次飞行中撞山，不幸牺牲。再次来到部队，程妈妈痛哭流涕，悲痛欲绝，赵庆珍当即给程妈妈跪下，“我和余江以前说过,如果一方将来在战场上牺牲了或有其他意外,另一方要代为尽孝，从今天起，我就给您当儿子。”程秀莲虽然感动，但她知道赵庆珍有自己的家庭要照顾，没有答应。“我爸有大哥和二哥照顾,家里没负担,这个事我也写信给他们说了,他们很支持。”赵庆珍三天两头找到程妈妈，软磨硬泡，程妈妈终于含泪答应。“我不叫您干妈,就叫您妈妈。”赵庆珍一句话打开了程秀莲的心门,两人相拥而泣,从此母子相称。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; 常去邛崃</span></strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;</span></strong><strong><span style=\"color: rgb(229, 51, 51);\">回江苏的时间很少</span></strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 余江以前每十天半月都要给妈妈写信说说自己的近况，而赵庆珍性格内向,不善表达,一般都是有重要的事才给家里去信。余江走后,赵庆珍改变了自己的习惯,每月都和程秀莲通两三封信,以让程秀莲不会感觉孤单。后来,程秀莲家里 安了电话,赵庆珍隔三岔五就到电话亭打长途。长途电话一分钟要一块多,而赵庆珍每个月的工资才400元,工资的很大一部分都花在了打电话上。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;&nbsp;入伍之后,赵庆珍过年基本上都没回过家,1994年春节，他被批准可以探亲休假,然而他没有回徐州，而是买了一张到成都的站票。赵庆珍陪着“妈妈”办年货、守岁、走亲戚,春节过得热热闹闹的,大家都把这个小伙子当成了亲人。这么多年，赵庆珍回江苏的时间很少,回去也是看望父亲和哥哥后又赶回邛崃。父亲还时常催促他,叫他有时间就多去邛崃多陪陪程妈妈,“我有你两个哥哥照顾,你不用担心。”这么多年最遗憾的是他没有见到父亲最后一面,在电话里听到父亲去世的消息,赵庆珍悲伤得不能自已。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;<strong><span style=\"color: rgb(229, 51, 51);\"> 安家邛崃</span></strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(229, 51, 51); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><strong>&nbsp; &nbsp; &nbsp; 一家四口其乐融融</strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;&nbsp;1995年,赵庆珍被分配到空军导弹某旅工作。这么优秀的小伙子，20多岁了还单身,部队领导也帮着为他操心，给他介绍了不少对象，然而赵庆珍都没有上心。其实,赵庆珍的真实想法是要找邛崃姑娘。那段时间,程妈妈也常写信催赵庆珍,说他年纪不小了,赶快在驻地或者老家找个合适的人成家。赵庆珍回信说不想在驻地找,让妈妈在邛崃给他找个对象。1998年，经朋友介绍赵庆珍认识了小学老师孟永凤，回到部队之后,赵庆珍和孟永凤开始鸿雁传书,虽然从没见过面,但他们非常合拍。孟永凤被赵庆珍的孝心打动，认为这个男人值得依靠。2000年,两人终于修成正果,收获一段幸福婚姻。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 一年后,赵庆珍和孟永凤的爱情结晶降生了。程秀莲亲自给孙子取名叫赵鹏程,还主动请缨帮忙照顾。洗衣做饭、接送上幼儿园、读书识字,程秀莲把所有的爱都倾注在孙子身上，一家四口比很多家庭都要幸福。2007年,赵庆珍转业到邛崃工作,一家人住在一起,经常晚饭之后出去散步,其乐融融。“这些年来,‘妈妈’对我的照顾更多,料理家务、带孩子,让我没有后顾之忧。其实,‘妈妈’也给了我一个幸福的家。”赵庆珍告诉记者。(记者 杜文婷 李旻)</span></p>', '赵庆珍：一句承诺 照顾战友妈妈22年', '助人为乐');
INSERT INTO `event_pass` VALUES (16, '1532178073812', '4', 'admin', '<p><span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">第五届全国道德模范评选表彰活动正在如火如荼的进行中，成都市共有6位市民入选全国道德模范候选人。他们分别是入选助人为乐类别的：秦坤、董贵生、刘源；入选诚实守信类别的：赵庆珍；入选见义勇为类别的：吴永秀和入选敬业奉献类别的：芮银超。</span></span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"> <p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 金牛区电视台的“80后”编导秦坤</span></strong></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;一次偶然机会中，秦坤接触了手语，此后，便与公益结下了缘分。2011年12月，他参与创办推广手语的节目，此后，又继续创办公益团队，帮助特殊群体。“做公益的过程中充满着快乐，自己也非常享受这个过程。”这是秦坤常挂在嘴边的话。</span> </p><p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp; 与公益结缘 他钟情于手语</span></strong></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;黝黑的皮肤、锐利的眼神、亲和力的笑容……记者眼前的秦坤是金牛区电视台的一名编导，这位长相酷似体育明星林丹的80后小伙子热心公益，他参与创立《欢行手语》栏目，通过手语的推广，让更多人关注到聋哑人这个特殊群体。提起最初学习手语的原因，秦坤笑得格外腼腆，“聋哑人拥有和普通人一样的梦想，我想帮助他们实现。”</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 秦坤告诉记者，与公益结缘是源于自己4年前的一次偶然经历。“2011年年初，我作为志愿者参加了一个金牛区电视手语大赛，当时就被几百人一起表演手语的场景深深震撼了。”秦坤回忆道。这次活动以后，一个问题一直困扰着他，“如何走进残疾人的内心，让他们真正能和普通人无障碍交流呢？”</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2011年中下旬，秦坤尝试着去成都市内的特殊教育学校，跟聋人孩子一起学习手语。“最开始去学习的时候，手语没有想象中那么容易。”秦坤说，比如“动物”两字的手语，要将双手的食指互碰一下，然后分开并张开五指。“某些复杂的地方，我就反复练习，主要是改变自己的固有思维方式。”通过和这些孩子交流的过程中，秦坤发现孩子们缺少的未必是一个书包，一套文具，他们需要的是更多了解外面的世界，如果我们每一个人都能了解和运用一些手语，那我们与聋哑人之间将没有距离，没有障碍。</span> </p><p>\n	<span style=\"line-height: 28px;\"><strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; &nbsp;&nbsp;创新手语教学 推出公益项目</span></strong></span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 在学习手语的过程中，秦坤创新了学习方法，他和几个朋友自费拍了些公益短片放在网上。“最初发在网上的视频点击率并不高，效果很不理想。”秦坤就琢磨着如何以一种更轻松、更快乐的方式来推广手语。2011年12月，秦坤参与创办的以手语推广为主的《欢行手语》栏目正式在金牛有线电视台推出，为了让更多的人接受手语，秦坤和他的团队摒弃了单调枯燥的传统手语教学方式，以新媒体网络作为播放平台。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“并且，在手语表现形式上加入一些面部和肢体语言的辅助，或用唱歌、舞蹈、模仿情景剧的手段等，更能帮助手语意义的全面表达。”秦坤说，这样的形式一出，立即得到大家的喜爱，于是欢行手语团队又做了更多的视频放在网上，慢慢地拥有了众多粉丝的关注，“我们在手语学习中很快乐，所以把我们的手语推广项目叫做欢行手语。”2012年12月，秦坤参加《中国梦想秀》成为“最佳追梦人”，将欢行手语推向了全新的舞台。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 除了推广手语的视频形式要创新，更需要的就是将热爱手语、推广手语的联盟集合在一起，形成合力。因此，2013年5月9日，秦坤带领团队在清华大学启动了全国高校手语协会联盟及“欢行手语 欢行中国”项目量。“做公益的过程中充满着快乐，自己也非常享受这个过程。”秦坤说道。2014年，秦坤通过给聋哑孩子们播放一个大海的视频，灵感浮现在脑海。“当我看到他们激动的眼神的时候，我就明白了一切。”秦坤突然意识到，“帮助孩子们健康成长，不仅仅是送去一大摞的书包、文具那么简单，更重要的是走进孩子们的内心，去了解他们真正想要的是什么。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;于是，秦坤通过多方努力号召社会爱心人士、爱心企业积极参与，最终帮助德阳特殊教育学校、成都特殊教育学校的20多个聋人孩子们实现了看海的梦想。“看到小孩子们梦想成真时纯真的笑容，心里有一种被融化了的感动。”今年，欢行公益又陆续推出了普特共融项目“欢行爱棒球”，让农民工子弟学校、特殊教育学校的聋人孩子们感受到了棒球运动的快乐，拓宽他们放眼世界的角度。</span> </p></span></p><p><br/></p>', '秦坤：用快乐手语为折翼天使插上翅膀', '助人为乐');
INSERT INTO `event_pass` VALUES (17, '1532178246831', '4', 'admin', '<p><span style=\"font-size: 12px; line-height: 18px;\"><span style=\"line-height: 24px;\">早上6时30分，家住武侯区玉林一巷的武宇华家就开始了一天的生活。由于儿子患有先天痉挛性脑瘫，武宇华收拾好自己，便帮儿子打理起来。7时40分，他背着儿子准时从家中出发；8时30分，等儿子上第一堂课后，武宇华回家整理家务；9时30分，武宇华再到学校，背儿子上厕所、换教室；直到中午12时，看着儿子吃完午饭上课后，武宇华才回家吃午饭……这是儿子的作息表，也是武宇华的作息表。面对生活的困难，已步入花甲之年的武宇华继续选择坚韧。他如今最担心儿子的婚姻问题，他希望儿子也能有个完整的家庭，一起幸福地生活下去。</span></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp;&nbsp;<strong><span style=\"color: rgb(229, 51, 51);\">放弃事业照顾儿子</span></strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp; 每天，武宇华不仅从家到学校至少往返4个来回，背儿子上下学，还要照顾儿子的饮食。“对于儿子，如果生活再来一次，我还是会做出这样的选择。”武宇华说。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp;&nbsp;1993年11月的一天，武宇华的儿子武晋出生了。他沉浸在初为人父的喜悦中，但快乐很快被一个坏消息粉碎——儿子被检查出患有先天痉挛性脑瘫。“这意味着他今后都不能像正常的孩子一样走路说话……”武宇华和妻子的眼泪都要流尽了，尽管家境并不富裕，两人还是决定不放弃，要好好照顾儿子一辈子。在铁路局工作的武宇华，当时是技术骨干，为了圆儿子的上学梦，他毫不犹豫地放弃了自己的事业，专心照顾武晋上学。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp; 武宇华开始了背儿子上学的路程。这一背就是13年，从儿子7岁到20岁，儿子也从55斤长到55公斤。如今，武宇华背子上学的长度超过了2.5万公里，电动自行车也骑坏了3辆，但从未迟到过一次。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; &nbsp; 和儿子一起传递爱心</span></strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp;&nbsp;背着儿子去上学，武宇华的故事感动了很多人。有好心人给武宇华介绍临时工作，希望能增加他们的家庭收入；学校里，老师专门给武晋开“小灶”，帮助他补习没有弄懂的功课；还有好心人给武晋家送来一台电脑，希望喜欢捣鼓电脑的武晋在家也能学会应用、修理……</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp; 在父亲的教诲下，武晋在班级里也成为了一位“热心肠”。班里打扫卫生，尽管武晋无法参加，但他总会多带一块抹布，以备其他同学使用；班会课老师要求大家准备PPT，武晋忙里忙外找资料、打印，帮大家设计封面；武晋还会主动找迟到的同学“谈话”，看他们有没有什么需要自己帮忙的……</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp;&nbsp;面对众多好心人的帮助和资助，细心的武宇华悄悄地记了一本账。只有武晋看病的确困难，需要支出时才会动这个钱，更多的时候，他都把好心人的名字和他们的爱心存在那里。“希望有朝一日，我们能将这份爱心传递下去。”武宇华说，不管未来面临什么样的困难，全家人都会一起努力，帮助更多人，以回报社会对他们全家的支持。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"font-size: 12px; line-height: 24px;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 谢红 图据市文明办</span></p><p><br/></p>', '武宇华：背儿子上学 父亲已经坚持13年', '感动中国');
INSERT INTO `event_pass` VALUES (18, '1532178314381', '4', 'admin', '<p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"><span style=\"line-height: 28px;\">去年底，成都退休工人陈春元在公交车上喝止小偷，被小偷划伤了脸部，留下一道8公分的伤疤。陈师傅的见义勇为事迹受到社会各界的关注，在今年公布的“2015成都榜样”主题活动5月人物榜中，陈春元名列其中。陈师傅的伤口目前仍在恢复期，但对于当时的举动并不后悔，他说，“我就是看不惯这些欺负人的行为，就是要管一管。”本月22号，陈师傅又在公交车上制止了两名小偷的行窃行为。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(229, 51, 51); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><strong>&nbsp; &nbsp; &nbsp; 手机拍现行 及时制止小偷行窃</strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;&nbsp;“就是这两个人，其中一个刚把手放下来。”昨日，陈师傅拿着手机，翻阅着他拍下的现场照片，向记者讲述起22日发生的事。“我也不是专门来抓小偷，就是偶尔碰到了，看不惯，就这性格。”</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 陈师傅告诉记者，当天大概14时30分，他坐2路公交车到数码广场。车上人较多，他站在车中部，不一会儿，感觉背后有人总是碰他，陈师傅立刻警惕起来，站在一旁戴眼镜的男子和身后背包的男子引起了陈师傅的注意。“我移开点，看到背包的男子正用外套和包包做掩护，反手摸与他相背而站的女士的包包。”陈师傅悄悄拿起手机拍下了两名男子的照片。手机的声响惊动了打掩护的眼镜男，背包男立刻停止了动手。眼镜男发出恐吓，“你吃多了，拍啥子照”，又与背包男交流道，“就是他，想挨刀子了？”陈师傅不动声色，“我不怕他们有刀。”陈师傅说，上次不知道小偷手指间夹有刀片，没有防备才受伤，“以我多年锻炼的体质，完全可以控制住他们的手。”</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp;&nbsp;两名男子在万年场站下车后，陈师傅立刻提醒该女士检查包包，发现女士的包已被打开一半，幸未丢失物品。记者联系上这位姓熊的女士，她不停感谢热心肠的陈师傅：“我和三个朋友从达州到成都游玩，没想遇到小偷。”回达州后熊女士曾通过微信，向陈师傅表达谢意。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">不愿上镜 以后还要打抱不平</span></strong></span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp; &nbsp; &nbsp; 陈师傅的伤势也牵动着大家的心。一家整形医院看到媒体报道后，主动提出要免费为陈师傅做脸部整形手术。摸着从脸颊一直拉到嘴角的刀疤，陈师傅心怀期待：“医院说要等伤口再长一下，8月份做手术最佳。”做好事得到了社会的认可，这让陈师傅很欣慰。</span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;陈师傅从小就坚持1到2个小时的长跑，如今也每天打球锻炼身体，年过六旬的他看上去比许多年轻人还要结实。此次男子的一句“就是他”，也让陈师傅有所警醒，“此前我的事被多次报道过，有些贼娃子可能认识我了。”他说今后自己还要打抱不平，但再也不愿面对镜头。多次与小偷交手，加上警方的提示，陈师傅也总结经验：“拍照有声音，不好，下次最好能录视频，留下实际证据。”对于此次拍下的两张照片，陈师傅表示将会交给警方，提供破案线索。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 谢红</span></p><p><br/></p>', '陈春元：制止小偷被伤仍执着不悔', '见义勇为');
INSERT INTO `event_pass` VALUES (19, '1532178392107', '4', 'admin', '<p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"><strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">为老百姓办“放心菜市”</span></strong></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　“在光明路菜市场买菜的市民过来检测，我们全部免费。只有在品质安全上把好关，大家放心我们才安心。”——林德明</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　从做菜市场的那天开始，他就决心要为成都市民提供安全放心的食品，办一个“放心菜市”。他就是成都光明路菜市场总经理林德明。多年来，他不仅诚信经营坚守承诺，为了让顾客买得放心，他还投资700万建起了农产品鉴定所，只要顾客对自己买的食品不放心，都可以在这里免费检测。他说，不管投入多少钱，只要能让老百姓买到放心的食品，他都觉得值。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　正是凭借这种公开、公平、严谨、负责的态度，林德明的菜市场和农产品鉴定所获得了百姓的信赖，他的故事也先后多次引起央视等众多媒体的关注。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">　发现问题猪肉却无处检测</span></strong></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　2011年初，林德明投资4000万元，盘下了锦江区蓉上坊的一通商铺，办起了锦江区光明路菜市场。诚信经营为市民办一个“放心菜市”，是他办菜市场最基本的目标，为此，他的菜市场一开始就严把各种质量关。但12月的一天，还是发现了问题：一名顾客提着一块在他菜市场买的猪肉找到他，说这块肉晚上会发光，觉得有问题不敢吃。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　菜市有商贩卖问题猪肉？林德明绝不能允许这样的事情发生。第二天一早，他就提着猪肉到多个食品检测机构送检。但林德明跑了一圈，这些机构都不受理私人送检的食品。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　问题虽然没能解决，但此时的林德明发现，要让市民买到放心菜，光靠菜市场工作人员肉眼和经验来判断远远不够，咋办？</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">投资700万建农产品鉴定所</span></strong></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　要把好菜品“入口”关，必须得有专业的检测手段。于是，林德明萌生出一个大胆的想法，干脆自己建个实验室，对市场内的农产品进行检测。在得到了区商务局和区食药监局支持后，林德明联系上川农大食品学院老师周康以及该院副院长李诚，利用市场的空置办公区，开始筹备建立鉴定所。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　2012年12月19日，林德明投资700万建成的四川合泰农产品（食品）司法鉴定所，在光明路菜市场二楼正式揭牌成立。整个实验室面积达200平方米，按照国家标准设置了2个理化实验室、1个微生物研究所，检测设备均采自全球最先进的仪器生产商。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">　每天抽样检测自家菜市的菜</span></strong></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　鉴定所投入运营以来，每天都会从菜市场选择十几个到几十个品种的农产品（食品）进行取样检测，并将检测报告挂在商铺显眼位置，让顾客明明白白消费。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　目前，鉴定所已经获得四川省司法厅的认可，并得到四川农业大学食品学院等国内知名重点高校专家和教授的技术支持。四川农业大学食品学院与鉴定所签订了科技合作协议，这里的技术人员都是四川农业大学食品学院等重点高校的专家和教授，共30多人。鉴定所主要检测农产品（食品）中的农药残留，抗生素、激素等兽药残留，重金属（如贝类和海鱼中的汞、砷，皮蛋中的铅，动物内脏含有的镉、铅等），三聚氰胺、苏丹红、塑化剂等非法添加剂，黄曲霉素等微生物。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　<strong style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"><span style=\"color: rgb(229, 51, 51);\">对菜市的菜有疑问免费检测</span></strong></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　林德明给实验室定了个规定：相关机构不接受的个人送检，他的实验室可以接受检测（只收取少量检测费）；而消费者在自己菜市场买的食品，一律免费检测。从实验室投运至今，他已为数百名普通送检群众进行了食品检测。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　“我觉得这个鉴定所开得很有意义，我们感觉也很有安全感。”在光明路菜市场卖菜十多年的陈大姐表示，这个开在菜市场的鉴定所，对他们来说也很方便，以后要是有什么问题就可以拿过去检测。</p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	　　2014年春节，为帮助地震灾区、贫困地区农副产品扩大销路，方便成都市民就近采购安全放心的年货产品，实现产销对口、为农副产品商家与市民搭建方便、实惠的年货购物平台，林德明举办了为期一周的年货大展销、快乐一站购的2014年迎春年货会，并且把此次活动的全部收益用来购买了1万斤金龙鱼大米和570件大王酱油，全部赠送给了水井坊辖区的五保户、低保户和清洁工人，共计500份。（成都晚报记者 易启明）</p><p><br/></p>', '林德明：为老百姓办“放心菜市”', '忠于职守');
INSERT INTO `event_pass` VALUES (20, '1532178442489', '4', 'admin', '<p><span style=\"line-height: 21px;\"><span style=\"color: rgb(229, 51, 51); line-height: 28px;\"> <strong>“如果有一天我真的走不动了，希望能有个年轻人来接班，我就放心了。”——陈永根</strong></span></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 在成都市龙泉驿区山泉镇红花村，有一位“最美村医”——陈永根。3岁时因病失去左腿，17岁听从父命开始学医，学成返乡行医问诊。一根拐杖，一个药箱，一座大山，独腿村医陈永根在崎岖的山路上走了37年，徒步出诊600余户人家，足迹遍布6平方千米的崎岖山路……</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>拄杖行医 走遍红花村每个角落</strong></span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 昨日上午，记者来到红花村，却没见到陈永根，邻居指着对面的大山说：“他刚刚出门，给村里的老人看病去了。”记者顺着方向一路找寻，终于看到一位头发全白的老者，身着白大褂，拄着拐杖，背着医药箱，在山路上蹒跚前行。这也是54岁的陈永根最常见的模样。</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">陈永根是红花村卫生站医生，幼年时因骨髓炎，左腿被截肢。陈永根立志学医，17岁从龙泉卫生学校毕业后，回到家乡成为一名乡村医生。红花村位于龙泉山深处，全村600多户1600多人分散居住在6平方千米范围内。30多年来，陈永根拄着拐杖走遍了村里的每个角落。去年2月的一个晚上，龙泉山突降大雪，正准备上床休息的陈永根接到电话，被告知村里80岁的傅成芳老人肺气肿复发。放下电话陈永根立即出门奔向3里之外的傅成芳老人家中。大雪遮盖了本来就崎岖的小路，他在深一脚浅一脚中，不小心踩空滚下山沟，幸好被一根树桩挡住身体。“我爬起来继续赶路，就算摔得鼻青脸肿也得继续出诊啊，病人还在等着我！”说这话时，他的脸上挂着轻松的笑容。</span></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; <strong><span style=\"color: rgb(229, 51, 51);\">&nbsp; 不舍乡亲 婉拒私立门诊邀请</span></strong></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 陈永根至今未婚，尽管从医达37年，生活上还是经常要靠住在同村的哥哥接济。记者了解到，卫生站的收入主要靠绩效工资，但陈永根把药品全部零差价卖给村民，自己一个月的收入只有三四百元。陈永根说，村里人赚钱不容易，在用药上为村民能省就省点。当地村民告诉记者，一年365天，陈永根只有大年初一的时候会到哥哥家里团年，其他的时间，不是在卫生站驻守，就是蹒跚在出诊的山路上……</span></p><p>\n	<span style=\"line-height: 21px;\"><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"line-height: 28px;\">几年前龙泉驿区一家私立医院几次请陈永根到医院坐中医门诊，他每次都婉言谢绝。在城里当医生的大姐退休了，邀约他开办一家私人诊所，他依然没答应。“我从小就在红花村长大，名字里就有个‘根’字，只要乡亲们需要我，我就在这永远扎根。”陈永根说。</span></span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 如今，红花村卫生站从一间小小的泥瓦房，发展成几十平方米的标准化卫生站，村里还修好了公路。去年年底，在爱心人士的资助下，陈永根截肢长达50年的左腿也装上了义肢，但陈永根不能骑车，出诊依然靠步行。“如果有一天我真的走不动了，希望能有个年轻人来接班，我就放心了。”陈永根说。</span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 成都晚报记者 何亚男 摄影报道</span></p><p>\n	<span style=\"line-height: 28px;\"></span></p></span><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	</p><p><br/></p>', '陈永根：走遍大山服务村民37年', '忠于职守');
INSERT INTO `event_pass` VALUES (21, '1532179206883', '4', 'admin', '<p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"><span style=\"line-height: 28px;\">一条土路从牧马山顶蜿蜒到山脚，连接着花源镇，也方便了附近胜利镇、黄甲镇、黄水镇居民之间的来往。1966年，时任胜利镇白塔村村支书的朱少清自己扛起锄头，开凿了这段长约1公里的山路。半个世纪过去了，如今的朱少清已是86岁高龄的老人，头发花白了、背也佝偻了，但他仍然扛着锄头铁锹，清除杂草、刨开鹅卵石，疏通排水渠，来维护这条倾注了他大半辈子心血的路。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　<strong><span style=\"color: rgb(229, 51, 51);\">　坚持守护着那条路</span></strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>　　暴雨酷暑都挡不住</strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　天刚刚下过一场雨，住在牧马山山顶的朱少清早早就起了床。吃过早饭后，他收拾好锄头、铁铲等工具便出门了。“又出去啦？”邻居杨先民热情招呼。“才下了雨，怕有些地方遭冲垮了，我去看一下。”朱少清一边往前走一边回答。其实，这些年来交通越来越方便，朱少清修的泥巴路走的人并不多，不过对这一片区域熟悉的村民来讲，他们都知道这条连通花源镇和胜利镇的捷径。“只要有人走就必须要维护，要是我不去管，这条路早就消失了。”朱少清告诉记者。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　在牧马山住了一辈子，其间他本来有机会跟着儿子到条件更好的地方居住，但他一再坚持，在山顶修了间茅屋，要守着他的路。“朱大爷脾气倔，决定的事情就一定要做完。我记得有段时间几乎一直在下雨，他挖的那条路因山体垮塌被堵了，可朱大爷还是要去通路，劝都劝不住。”杨先民回想起当时的情景，既心疼又感激，“不光是下雨天，夏天正午的时候，他也顶着大太阳去修路，为此还多次中暑。”</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　<strong><span style=\"color: rgb(229, 51, 51);\">半个世纪三次改道</span></strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>　　土路至今仍在使用</strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　山路修建于1966年，距今已经有半个世纪，现在大家所看到的路已经改过三次道了。朱少清说，道路修好后，由于坡度太陡，五六月的时候下大雨，路很快就被冲成了一条水沟。见此情景，他便将道路向旁边改建，将坡度改缓，原来的道路则成了排水沟。道路延伸到山脚时，要经过一个陡坡，陡坡的旁边有一条小河。原来的道路靠着河，顺着坡势而建，路的坡度大，走起来不方便，也不安全，曾经有人从斜坡处滑到了河里。为了避免这样的情况，朱少清将道路向坡内改建，弯曲到山脚，减缓了坡度。记者注意到，在山坡上至今还能看见凿子凿过的痕迹。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　在修路的那段时间里，朱少清每天天未亮就从家里出发，他自购锄头、土箕、铁锹、铁铲等工具，开始清理一人多高的杂草杂树和大大小小的石块，路基形成后排除路边傍山的险情，然后在道路两边挖排水沟……1公里的道路，是朱少清靠人力一锄头一锄头挖出来的，他日复一日行走在山间，风吹日晒，扮演着“愚公”的角色。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　2002年后，村村都通了水泥路，但这条路依然是花源镇通往胜利镇的近路，方便着骑摩托车和步行的人们。&nbsp;提到朱少清的名字，胜利、花源两镇群众无人不知，大家对他敬佩不已，都说老朱是个大好人，啥都不图，就想为大家办点实事。</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　<strong><span style=\"color: rgb(229, 51, 51);\">　快90岁了依旧自立</span></strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"color: rgb(229, 51, 51); line-height: 28px;\"><strong>　　为大家做事乃是福</strong></span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　看着自己辛苦整修的道路，朱少清常常傻笑，路面铺着一些鹅卵石，两侧的排水沟锄头挖过的痕迹还很新，排水沟的边上长满了茅草，不时有骑摩托车或者步行的人行色匆匆地经过这条路。这样的场景让他觉得欣慰，“我是一名老党员，每当看着大家出入的泥巴路满是坡坡坎坎，心里就不好受，只是想在我还有力气的时候为大家做点事。”</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">　　朱少清有三个儿子，他们成家后就都搬出白塔寺老家。随后，朱少清老两口在牧马山顶修了间茅屋，开起了代销店，供过往行人歇脚，消暑解渴。2010年，81岁的朱少清老伴过世，儿孙们怕老人劳累孤独，软磨硬泡把他接到儿子家“享清福”。那段时间，喝茶散步便成了老人空闲时唯一的消遣方式。不到一个月老人就闲不住了，吃完饭就“溜”回老房子。家里人不放心，几次去看他，原来他又拿起铁锹在修路……&nbsp;在旁人看来，朱少清是有福不会享。但老人并不这么看。“啥子是福，福就是能够自立。”朱少清说，“我都快90岁了，不仅生活能够自理，还能继续为大家做事，这还不算是福气吗？”(</span><span style=\"line-height: 28px;\">记者&nbsp;杜文婷&nbsp;摄影&nbsp;杨永赤)</span></p><p><br/></p>', '朱少清：半世纪的坚守 一锄一锹为村民修路', '忠于职守');
INSERT INTO `event_pass` VALUES (23, '1532244441547', '4', 'admin', '<p><span style=\"color: rgb(81, 81, 81);\">来源：成都文明网</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(0, 0, 0); font-family: verdana,微软雅黑,sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">\n		</span></p><p>\n			</p><p style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\"><img style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150529/1432864043.jpg\"/></span></p><p style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">江艳，女，中共党员，成都市青白江工商局公务员</span> </p><p style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: center; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 21px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 江艳坚持公益18年，用行动将爱心传播，常年活跃在敬老院、偏远山村，先后被成都市团委、市志愿者工作委员会、市青年志愿者协会联合表彰为“2008年度优秀青年志愿者”，被青白江团委、青白江志愿者工作委员会表彰为“抗震救灾先进个人”，被成都市妇联评为成都市“2014我身边的优秀女性”。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 1996年，江艳开始资助第一个贫困学生，和两个朋友一起承担了小男孩小学期间的全部学习费用。2001年，她参与“15元计划”资助甘孜州孤儿学校的学生。2005年，她与几位热心公益的团干朋友一起发动网友开展志愿者服务活动，成为“成都阳光彼岸爱心合作社”（“成都义工网”前身）首批志愿者。她以极大的热情投入到志愿者服务中，很快便成为“成都义工”的骨干。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在江艳的影响和带动下，老公潘馨也加入到义工队伍中来，两人经常一同参加义工活动。女儿潘思睿5岁起，就在父母的带领下开始参与关爱敬老院老人、留守儿童、结对贫困学生的活动，在公益活动中得到锻炼。“5.12”地震期间亲自为同龄人募集新年礼物，并在父母的带领下到地震重灾区绵竹市金花镇捐赠新年礼物。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2008年8月，江艳与4名义工朋友一起深入凉山州布拖县开展“牵手布拖，传递文明”活动，当他们冒着极大的风险，承受着身体的极限，克服难以预料的重重困难，艰难地来到布拖县收古村时，展现在眼前的情景令人震惊，那里的基础卫生条件极差，大多数孩子都衣不遮体。虽是秋天，但地处高原的凉山天气异常寒冷，这群孩子中有一位10多岁的盲女孩叫李子么儿科，只穿了一件单衣，光着脚站在雨地里，冷得浑身直打哆嗦。江艳见此情景，上前紧紧把盲女孩搂在怀里，眼泪夺眶而出。江艳和其他义工忙找来衣服和鞋子给李子么儿科穿上，盲女孩感动得热泪盈眶。从那以后，江艳便多了一份牵挂，牵挂这群大山深处的孩子们。2008年11月，江艳带领包括自己的丈夫和女儿在内的13名义工再次到布拖开展公益援助，女儿潘思睿触动很大，看到同龄人背着用猪饲料口袋制作的书包，她的心久久不能平静，回家后便发动10名小朋友在公园开展义卖，向路人介绍她眼中的布拖县，争取爱心人士对布拖贫困学生的帮助，现场共募集近300元善款，将这笔钱交到“成都义工”网负责人老黑叔叔的手中，参与为布拖孩子买书包的行动。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;江艳先后4次深入大凉山，和志愿者朋友一起，为布拖的孩子们送去学习用具、御寒衣物、清洁卫生用品，并协助建立“爱心书屋”，教孩子们洗脸、洗手，培养彝族同胞卫生习惯。通过大家6年多的努力，当地孩子的卫生习惯得到了极大改善，生存状态有了明显改观。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2007年，江艳全家在参与关爱留守儿童活动中，认识了清泉镇一名特殊留守儿童罗然。罗然父亲是服刑人员，妈妈是残疾人。她家的居住条件很差，简陋的三间房屋没有电灯，也没有窗户。江艳看在眼里，疼在心中，决定帮助这一家。她先找到自己做工程的同学，拉来混凝土平整了房屋地面，新砌了水泥院坝，又和“成都义工网”朋友们联系电力公司为罗然家拉了用电专线。从此，江艳一家将罗然当成家庭成员，对她倾注了更多的关爱。在江艳一家和“成都义工”志愿者们的关心、帮助下，罗然得以健康成长，2013年以优异成绩考上了大学。如今，罗然把江艳一家当成了自己的第二个家，取得成绩会第一时间打电话回家分享快乐，遇到困难会在自己无法克服时会寻求江阿姨的帮助。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 除了罗然，江艳一家还长期照顾着两名特殊留守儿童曾小波和庄代云。小波的父亲曾是服刑人员，为让孩子心理健康不受影响，江艳让丈夫专门给处于青春期的小波送去有关青春期教育的书籍，帮助其解除青春期困惑。庄代云是名孤儿，父亲在他出生前就去世了，母亲生下他不到一个月就离家出走了，他和奶奶相依为命，而奶奶又常年多病，生活比较困难。江艳知道后，将他确定为第四个长期帮扶的对象，寻找多种途径帮助这位孤儿，让他感受家的温暖，帮助其健康成长。如今，这些受资助的孩子都接受了良好的教育。</span> </p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;“5.12”大地震后，江艳全家和志愿者一起多次冒着余震危险前往北川、绵竹、什邡等重灾区运送救灾物资，积极参与救援工作，帮助灾区群众恢复生产。</span> </p><p style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 如今，做义工已成为江艳日常生活中的一部分，不求回报，不求名利，做自己力所能及的善事。这就是他的生活状态和目标追求。</span> </p><p style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\"><br/></span></p><p><span style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"> </span></p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\"><strong>好人寄语：</strong>赠人玫瑰，手有余香。简单义工，简单快乐。</span> </p><p style=\"margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify;\">\n	<span style=\"line-height: 28px;\"><br/></span></p><p><span style=\"color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"> <p>\n	<span style=\"line-height: 28px;\"><strong>道德点评：</strong>江艳是幸福的，她的幸福来自于他的爱心。她的爱心感召着她周围的亲人以及朋友，助人为乐，一件一桩，仅凭一片赤诚。江艳用自己的行动对中华道德操守坚守着并发扬光大着，她的这种爱心善举，增强了人格的魅力，凝聚了文明的精粹，淋漓地演绎爱心，无私地践行文明的义举，让生命的花园吐露芬芳</span> </p></span></p><p></p><p><br/></p>', '基层公务员江艳：坚持公益18年 爱心感召亲人朋友齐参与', '助人为乐');
INSERT INTO `event_pass` VALUES (24, '1532244869066', '4', 'admin', '<p style=\"text-align:center;background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"><span style=\"line-height: 28px;\"><img width=\"500\" height=\"332\" title=\"\" style=\"border-bottom-color: rgb(81, 81, 81); border-bottom-style: none; border-bottom-width: 0px; border-image-outset: 0; border-image-repeat: stretch; border-image-slice: 100%; border-image-source: none; border-image-width: 1; border-left-color: rgb(81, 81, 81); border-left-style: none; border-left-width: 0px; border-right-color: rgb(81, 81, 81); border-right-style: none; border-right-width: 0px; border-top-color: rgb(81, 81, 81); border-top-style: none; border-top-width: 0px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; vertical-align: middle;\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150528/1432808137.jpg\"/>&nbsp;</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"line-height: 28px;\">鲁莉，女，汉族，1976年出生，郫县工商联蜀绣产业商会安靖镇高级蜀绣工作室带头人。</span><span style=\"line-height: 28px;\">作品《静物瓶花》获得“全国休闲农业创意大赛金奖”，《繁花似锦》获得第三届金针杯蜀绣技能大赛一等奖，全异绣《文君熊猫》获得了成都市创意作品评选活动二等奖。2013年，被评定为首批成都市工艺美术大师。</span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp;&nbsp;&nbsp; &nbsp; </span><span style=\"line-height: 28px;\">1992年高中毕业以后师从国家级工艺美术大师郝淑萍门下系统的学习蜀绣技艺，经过4年的专业指导和亲身实践，熟练的掌握了蜀绣的各种基本针法，绣制出来的作品深受业界的一致好评。2005，年分别前往法国、德国参加旅游推介会，向世界展示了蜀绣的风采。2006年鲁莉来到蜀绣大师孟德芝门下，继续学习蜀绣，通过自己刻苦学习和扎实的基本功，她很快就掌握了蜀绣传统作品《芙蓉鲤鱼》的绣制方法。安靖镇是蜀绣的发源地之一，有着深厚的人才基础，由于后来机绣的出现使得蜀绣这门制作流程复杂、产量低下的传统手工艺被埋没在历史的尘埃中，才渐渐淡出了人们的视野。2008年，安靖镇扛起了振兴蜀绣传统文化的大旗，完善了蜀绣产业相关配套设施，并建立了蜀绣培训基地，长期开展蜀绣人才技能培训，为广大妇女同胞提供了再就业培训的平台和就业岗位，鲁莉也来到安靖蜀绣之乡，成为了蜀绣基地七彩绣坊的一名绣娘。&nbsp;&nbsp; </span><br style=\"color: rgb(81, 81, 81); font-size: 14px; line-height: 30.66px; padding-top: 0px;\"/><span style=\"line-height: 28px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"line-height: 28px;\">自2008年安靖镇成功举办“第二届成都国际非物质文化遗产节”分会场的相关活动以来，得到了各级政府的大力支持以及新闻媒体的强力宣传，许多没有刺绣基础的人员都慕名前来蜀绣基地学习蜀绣技艺，使得七彩绣坊绣工在刺绣技艺方面存在着巨大的差异，作为绣坊的技术骨干，鲁莉经常都会耐心的对她们进行技术上的指导和帮助。之后鲁莉作为首批高级绣娘培养人才被选派到邬学强大师工作室学习，同时被选派到成都纺织高等专科学校艺术系进修，在蜀绣高端技艺和美术绘画、色彩、构图等方面得到综合性培养并取得很大的进步。成都蜀都绣娘刺绣专业合作社聘请她担任高级绣娘工作室的技术指导，她把自己所学毫无保留地教授给其他绣娘姐妹们。2011年初商会抽调了5名技术过硬的年轻绣工成立了安靖镇高级蜀绣工作室，专门制作高精欣赏品。近两年，高级绣娘工作室在鲁莉的带领下，先后制作了单面绣《繁花似锦》、《荷花》、《静物瓶花》，双面异色绣《芙蓉锦鲤》及双面全异绣《文君熊猫》等一大批蜀绣精品。</span> </p><p><br/></p>', '高级绣娘鲁莉：潜心钻研 向世界展示蜀绣风采', '助人为乐');
INSERT INTO `event_pass` VALUES (25, '1532245626375', '4', 'admin', '<p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: center;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><img style=\"border-bottom-color: rgb(81, 81, 81);border-bottom-style: none;border-bottom-width: 0px;border-image-outset: 0;border-image-repeat: stretch;border-image-slice: 100%;border-image-source: none;border-image-width: 1;border-left-color: rgb(81, 81, 81);border-left-style: none;border-left-width: 0px;border-right-color: rgb(81, 81, 81);border-right-style: none;border-right-width: 0px;border-top-color: rgb(81, 81, 81);border-top-style: none;border-top-width: 0px;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;vertical-align: middle\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150528/1432808357.jpg\"/> &nbsp;</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><span style=\"color: rgb(51, 127, 229)\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"color: rgb(51, 127, 229)\">受王东明书记委托，省委副秘书长马波接见电子科大优秀学生代表（左一为刘沈厅）</span> &nbsp;</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 刘沈厅，男，1988年3月生，中共党员。电子科技大学微电子与固体电子学院研究生二年级学生，电子科技大学“微梦党建委员会”主席。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 勤勉刻苦，勇于进取的奋斗精神。该生以高中三年全额奖学金的实力考入电子科技大学英才学院，成绩一直保持年级第一名。本科毕业，获得清华大学和国外名校的保送资格，但家庭突然遭遇变故----母亲突发疾病不幸离世。家庭的变故没有打倒刘沈厅，反而使他更加的坚强和乐观，为了更好的照顾父亲，他选择继续留在成都进行深造。本科和研究生期间，先后获得国家奖学金、国家励志奖学金、研究生一等奖学金、研究生国家奖学金、人民一等奖学金。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><img style=\"border-bottom-color: rgb(81, 81, 81);border-bottom-style: none;border-bottom-width: 0px;border-image-outset: 0;border-image-repeat: stretch;border-image-slice: 100%;border-image-source: none;border-image-width: 1;border-left-color: rgb(81, 81, 81);border-left-style: none;border-left-width: 0px;border-right-color: rgb(81, 81, 81);border-right-style: none;border-right-width: 0px;border-top-color: rgb(81, 81, 81);border-top-style: none;border-top-width: 0px;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;vertical-align: middle\" alt=\"\" src=\"http://by.chengdu.cn/uploads/image/20150528/1432808936.jpg\"/> &nbsp;</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><br style=\"margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px\"/> </p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><br style=\"margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px\"/> </p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 他积极参加各种学术竞赛和交流，先后获得全国数学竞赛四川赛区三等奖、全国英语竞赛三等奖、电子科大与新加坡义安理工学院辩论赛一等奖，发表了Influence of graphite oxide drying temperature on ultra-fast microwave synthesis of grapheme、Facile Synthesis of Phase Pure Iron Pyrite (FeS2) Nanocrystals for Low Cost Photodetectors（JMCC）等高质量论文。2012年，他组织并参加Virtual Conference on Nanoscale &nbsp;Science and Technology (VCNST)国际会议并作口头报告，得到参会专家学者的高度认可。此外，他还申请国家发明专利一项：砷化镓量子环红外光电探测器及其制造方法。因为在校期间的优秀表现，他多次被表彰为优秀学生干部，并荣获2012年校级优秀毕业生。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp;&nbsp; 乐观坚韧，战胜困难的人生态度。母亲的离世给家庭带来了沉重的打击，面对这样的打击，刘沈厅没有消沉和放弃，而选择了担当---用男子汉的肩膀承担起照顾父亲和家庭的重任。他发过传单，做过推销，从事过各种各样的兼职。生活的艰辛让他明白只有将学到的管理知识与实践结合起来，才能创造更好的效益。2011年，在学校的鼓励和帮助下，依托国家鼓励大学生创业的好政策，刘沈厅和朋友成立了成都欧朗装饰材料有限公司，从事家装软包业务。在做好研究生学习的同时，他自学财务管理、市场推广和客户维护，从来没有周末和节假日，数不尽的酸甜苦辣、道不尽的困难挫折，刘沈厅都坚持下来了。现在公司运行良好，总部也搬迁到新世纪环球中心，员工从最初的几个人发展到20多个员工，注册资本由最初的50万元增加到500万元。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 在经营公司时，刘沈厅遵循“守法经营、诚信服务”的理念，获得了市场和客户的认可。公司先后被成都市工商局授予成都市“重合同守信用”企业、3A级信用企业、5星级客户满意单位等荣誉称号。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 善良质朴，敢于担当的时代使命。感恩是刘沈厅品质里最闪光的一点，他常说“我来自地震灾区，经历了家庭变故，是国家、社会和学校帮助我走出困境，让我看到希望和未来。穷则独善其身、达则兼济天下。穷时有‘穷’的报效国家方式，达时有‘达’的报效方式”。在公司没有成立前，他申请了导生（电子科技大学一项高年级学生引导低年级学生的举措，选拔优秀的高年级学生对大一新生进行指导和帮助），用行动感染学生、用力量影响学生，被低年级学生亲切地成为“厅哥、厅长”。公司成立后，刘沈厅积极为学校毕业生提供就业岗位，并为学弟学妹们的职业成长创造优厚的条件，现在有的学弟们已经成长为公司的骨干力量。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 和学生的长期接触过程中，刘沈厅对大学生思想政治教育有了一些自己的认识和探索。作为党员，他认为应该充分利用党建平台引领学生思想政治教育。2013年，刘沈厅在学院党委书记李雪梅等老师领导下谋划成立了微梦党建委员会，以“传递微力量、成就大梦想”为宗旨，以身作则做好党员的先锋模范作用。为了活动的更好开展，刘沈厅决定每月从自己公司的收益里提取一部分作为微梦公益活动的经费赞助。微梦党建委员会成立以后，刘沈厅带领同学开展了形式多样的公益推广和实践活动，先后举办了“百万微笑”、“毕业生义捐、义卖、义换”、崇州鸡冠山小学爱心志愿服务、救助温江车祸女孩吴双等活动，在全校和社会上引起了极大反响。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 2014年3月，电子科技大学与栋梁工程联合成立“栋梁工程基地班”（钧业班），探索在理工科高校背景下培养一批具有担当意识、领袖气质、人文底蕴、创新精神的优秀青年学子的方式。刘沈厅得知后，非常激动和赞同，捐赠10万元设立“启梦”奖学金，鼓励学弟学妹们追求梦想、实现梦想。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 在不了解的人看来，刘沈厅通过自己的努力创造了物质收益，是学生中的“土豪”。但现实中的他非常节省和朴素。使用的是一部廉价的红米手机，能坐公交时坚决不打车……在与“栋梁工程基地班”的学生交流后，一位叫翁卓的学生在他的成长日记里这样写道：“他背后的故事还有太多太多：失去最亲之人彻骨之痛令其成长与坚强；善于利用分分秒秒与一切手边资源令其在学术领域超霸封神；为实现强国梦放弃留学名校而坚守本校同时创业不断打拼；因对国人自爱程度甚不及美国友人对我们的关切程度而下决心投身公益……太多的辛酸苦累，太多的毅然决断。他的故事因离我们这样迫近触手可及而弥足珍贵。一位仅仅超出我们几岁的学长，与我们就读于一所高校，却完成了令我难以想象的一件件看似平凡的小事。他热爱我们这群学弟学妹，捐助资金的同时，他也是另一个行政班的导生。身体力行，说做就做，我想这便是这位刘沈厅学长最可敬可爱之处吧。”</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 2014年7月，作为电子科技大学的优秀学生代表，刘沈厅将带领团队赴井冈山参加团中央主办的“井冈情中国梦”专题社会实践。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 2014年4月30日，四川省委书记王东明回信电子科技大学“栋梁工程基地班”（钧业班）。省委副秘书长马波，省委教育工委委员、省教育厅党组成员李光华，团省委副书记张荣来校递送王东明书记回信并召开五四座谈会，作为全校参会的5名优秀大学生代表之一，刘沈厅在会上说了这样了一段话，或许可以窥见这位经历苦难而仍怀梦想的青年人的心里：曾经的他也与周围众多同学一样，仅仅将理想局限于“房子、车子、票子”上面。但家中突逢变故，生命中至爱的母亲突然离我而去，只给他留下了无私的爱与思念，促使他从新思考生命的价值和意义。回想自己在国家、社会、学校和亲人帮助下走过的那些路，通过身边优秀青年人的引导，他认知到以往的“自私、狭隘与精神的匮乏”，学会了“知恩、感恩、重责任”；学会了对他人的关怀，对社会的奉献，对国家的担当；学会了将自己的追求与国家的需要紧密结合起来。他说：“我有梦想，我的梦想只有与“中国梦”同频共振时，才能更好实现。”</p><p><br/></p>', '刘沈厅：勤勉刻苦 自主创业的青年典范', '刻苦勉励');
INSERT INTO `event_pass` VALUES (26, '1532246400826', '4', 'admin', '<p><font face=\"verdana,微软雅黑,sans-serif\"><font color=\"#001000\">胡律律，男<span style=\"text-align: left; text-transform: none; line-height: 28px; text-indent: 0px; letter-spacing: normal; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; text-decoration: none; word-spacing: 0px; orphans: 2; -webkit-text-stroke-width: 0px; background-color: transparent;\">，汉族，1976年出生，郫县工商联蜀绣产业商会安靖镇高级蜀绣工作室带头人。</span><span style=\"text-align: left; text-transform: none; line-height: 28px; text-indent: 0px; letter-spacing: normal; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; text-decoration: none; word-spacing: 0px; orphans: 2; -webkit-text-stroke-width: 0px; background-color: transparent;\">作品《静物瓶花》获得“全国休闲农业创意大赛金奖”，《繁花似锦》获得第三届金针杯蜀绣技能大赛一等奖，全异绣《文君熊猫》获得了成都市创意作品评选活动二等奖。2013年，被评定为首批成都市工艺美术大师。</span></font></font><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp;&nbsp;&nbsp; &nbsp; </span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">1992年高中毕业以后师从国家级工艺美术大师郝淑萍门下系统的学习蜀绣技艺，经过4年的专业指导和亲身实践，熟练的掌握了蜀绣的各种基本针法，绣制出来的作品深受业界的一致好评。2005，年分别前往法国、德国参加旅游推介会，向世界展示了蜀绣的风采。2006年鲁莉来到蜀绣大师孟德芝门下，继续学习蜀绣，通过自己刻苦学习和扎实的基本功，她很快就掌握了蜀绣传统作品《芙蓉鲤鱼》的绣制方法。安靖镇是蜀绣的发源地之一，有着深厚的人才基础，由于后来机绣的出现使得蜀绣这门制作流程复杂、产量低下的传统手工艺被埋没在历史的尘埃中，才渐渐淡出了人们的视野。2008年，安靖镇扛起了振兴蜀绣传统文化的大旗，完善了蜀绣产业相关配套设施，并建立了蜀绣培训基地，长期开展蜀绣人才技能培训，为广大妇女同胞提供了再就业培训的平台和就业岗位，鲁莉也来到安靖蜀绣之乡，成为了蜀绣基地七彩绣坊的一名绣娘。&nbsp;&nbsp; </span><br style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; padding-top: 0px; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\"/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 28px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\">自2008年安靖镇成功举办“第二届成都国际非物质文化遗产节”分会场的相关活动以来，得到了各级政府的大力支持以及新闻媒体的强力宣传，许多没有刺绣基础的人员都慕名前来蜀绣基地学习蜀绣技艺，使得七彩绣坊绣工在刺绣技艺方面存在着巨大的差异，作为绣坊的技术骨干，鲁莉经常都会耐心的对她们进行技术上的指导和帮助。之后鲁莉作为首批高级绣娘培养人才被选派到邬学强大师工作室学习，同时被选派到成都纺织高等专科学校艺术系进修，在蜀绣高端技艺和美术绘画、色彩、构图等方面得到综合性培养并取得很大的进步。成都蜀都绣娘刺绣专业合作社聘请她担任高级绣娘工作室的技术指导，她把自己所学毫无保留地教授给其他绣娘姐妹们。2011年初商会抽调了5名技术过硬的年轻绣工成立了安靖镇高级蜀绣工作室，专门制作高精欣赏品。近两年，高级绣娘工作室在鲁莉的带领下，先后制作了单面绣《繁花似锦》、《荷花》、《静物瓶花》，双面异色绣《芙蓉锦鲤》及双面全异绣《文君熊猫》等一大批蜀绣精品。</span><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"> </span></p>', '高级绣娘胡律律：潜心钻研 向世界展示蜀绣风采', '潜心钻研');
INSERT INTO `event_pass` VALUES (27, '1532246801169', '4', 'admin', '<p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">张羿儿，男，1988年3月生，中共党员。电子科技大学微电子与固体电子学院研究生二年级学生，电子科技大学“微梦党建委员会”主席。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 勤勉刻苦，勇于进取的奋斗精神。该生以高中三年全额奖学金的实力考入电子科技大学英才学院，成绩一直保持年级第一名。本科毕业，获得清华大学和国外名校的保送资格，但家庭突然遭遇变故----母亲突发疾病不幸离世。家庭的变故没有打倒刘沈厅，反而使他更加的坚强和乐观，为了更好的照顾父亲，他选择继续留在成都进行深造。本科和研究生期间，先后获得国家奖学金、国家励志奖学金、研究生一等奖学金、研究生国家奖学金、人民一等奖学金。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">他积极参加各种学术竞赛和交流，先后获得全国数学竞赛四川赛区三等奖、全国英语竞赛三等奖、电子科大与新加坡义安理工学院辩论赛一等奖，发表了Influence of graphite oxide drying temperature on ultra-fast microwave synthesis of grapheme、Facile Synthesis of Phase Pure Iron Pyrite (FeS2) Nanocrystals for Low Cost Photodetectors（JMCC）等高质量论文。2012年，他组织并参加Virtual Conference on Nanoscale &nbsp;Science and Technology (VCNST)国际会议并作口头报告，得到参会专家学者的高度认可。此外，他还申请国家发明专利一项：砷化镓量子环红外光电探测器及其制造方法。因为在校期间的优秀表现，他多次被表彰为优秀学生干部，并荣获2012年校级优秀毕业生。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp;&nbsp; 乐观坚韧，战胜困难的人生态度。母亲的离世给家庭带来了沉重的打击，面对这样的打击，刘沈厅没有消沉和放弃，而选择了担当---用男子汉的肩膀承担起照顾父亲和家庭的重任。他发过传单，做过推销，从事过各种各样的兼职。生活的艰辛让他明白只有将学到的管理知识与实践结合起来，才能创造更好的效益。2011年，在学校的鼓励和帮助下，依托国家鼓励大学生创业的好政策，刘沈厅和朋友成立了成都欧朗装饰材料有限公司，从事家装软包业务。在做好研究生学习的同时，他自学财务管理、市场推广和客户维护，从来没有周末和节假日，数不尽的酸甜苦辣、道不尽的困难挫折，刘沈厅都坚持下来了。现在公司运行良好，总部也搬迁到新世纪环球中心，员工从最初的几个人发展到20多个员工，注册资本由最初的50万元增加到500万元。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 在经营公司时，刘沈厅遵循“守法经营、诚信服务”的理念，获得了市场和客户的认可。公司先后被成都市工商局授予成都市“重合同守信用”企业、3A级信用企业、5星级客户满意单位等荣誉称号。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 善良质朴，敢于担当的时代使命。感恩是刘沈厅品质里最闪光的一点，他常说“我来自地震灾区，经历了家庭变故，是国家、社会和学校帮助我走出困境，让我看到希望和未来。穷则独善其身、达则兼济天下。穷时有‘穷’的报效国家方式，达时有‘达’的报效方式”。在公司没有成立前，他申请了导生（电子科技大学一项高年级学生引导低年级学生的举措，选拔优秀的高年级学生对大一新生进行指导和帮助），用行动感染学生、用力量影响学生，被低年级学生亲切地成为“厅哥、厅长”。公司成立后，刘沈厅积极为学校毕业生提供就业岗位，并为学弟学妹们的职业成长创造优厚的条件，现在有的学弟们已经成长为公司的骨干力量。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\">&nbsp;&nbsp;&nbsp; 和学生的长期接触过程中，刘沈厅对大学生思想政治教育有了一些自己的认识和探索。作为党员，他认为应该充分利用党建平台引领学生思想政治教育。2013年，刘沈厅在学院党委书记李雪梅等老师领导下谋划成立了微梦党建委员会，以“传递微力量、成就大梦想”为宗旨，以身作则做好党员的先锋模范作用。为了活动的更好开展，刘沈厅决定每月从自己公司的收益里提取一部分作为微梦公益活动的经费赞助。微梦党建委员会成立以后，刘沈厅带领同学开展了形式多样的公益推广和实践活动，先后举办了“百万微笑”、“毕业生义捐、义卖、义换”、崇州鸡冠山小学爱心志愿服务、救助温江车祸女孩吴双等活动，在全校和社会上引起了极大反响。</p><p style=\"background-color: transparent;color: rgb(81, 81, 81);font-family: verdana,微软雅黑,sans-serif;font-size: 14px;font-style: normal;font-variant: normal;font-weight: 400;letter-spacing: normal;margin-bottom: 0px;margin-left: 0px;margin-right: 0px;margin-top: 0px;orphans: 2;padding-bottom: 0px;padding-left: 0px;padding-right: 0px;padding-top: 0px;text-align: left;text-indent: 0px;-webkit-text-stroke-width: 0px;white-space: normal;word-spacing: 0px\"><br/></p><p><br/></p>', '张羿儿：勤勉刻苦 自主创业的新时代青年', '勤勉刻苦 自主创业');
INSERT INTO `event_pass` VALUES (28, '1532248094934', '4', 'admin', '<p>郭乾坤<span style=\"line-height: 28px;\">坚持公益18年，用行动将爱心传播，常年活跃在敬老院、偏远山村，先后被成都市团委、市志愿者工作委员会、市青年志愿者协会联合表彰为“2008年度优秀青年志愿者”，被青白江团委、青白江志愿者工作委员会表彰为“抗震救灾先进个人”，被成都市妇联评为成都市“2014我身边的优秀女性”。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 1996年，郭乾坤开始资助第一个贫困学生，和两个朋友一起承担了小男孩小学期间的全部学习费用。2001年，她参与“15元计划”资助甘孜州孤儿学校的学生。2005年，她与几位热心公益的团干朋友一起发动网友开展志愿者服务活动，成为“成都阳光彼岸爱心合作社”（“成都义工网”前身）首批志愿者。她以极大的热情投入到志愿者服务中，很快便成为“成都义工”的骨干。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp;&nbsp;在郭乾坤的影响和带动下，老公潘馨也加入到义工队伍中来，两人经常一同参加义工活动。女儿潘思睿5岁起，就在父母的带领下开始参与关爱敬老院老人、留守儿童、结对贫困学生的活动，在公益活动中得到锻炼。“5.12”地震期间亲自为同龄人募集新年礼物，并在父母的带领下到地震重灾区绵竹市金花镇捐赠新年礼物。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2008年8月，郭乾坤与4名义工朋友一起深入凉山州布拖县开展“牵手布拖，传递文明”活动，当他们冒着极大的风险，承受着身体的极限，克服难以预料的重重困难，艰难地来到布拖县收古村时，展现在眼前的情景令人震惊，那里的基础卫生条件极差，大多数孩子都衣不遮体。虽是秋天，但地处高原的凉山天气异常寒冷，这群孩子中有一位10多岁的盲女孩叫李子么儿科，只穿了一件单衣，光着脚站在雨地里，冷得浑身直打哆嗦。江艳见此情景，上前紧紧把盲女孩搂在怀里，眼泪夺眶而出。郭乾坤和其他义工忙找来衣服和鞋子给李子么儿科穿上，盲女孩感动得热泪盈眶。从那以后，江艳便多了一份牵挂，牵挂这群大山深处的孩子们。2008年11月，江艳带领包括自己的丈夫和女儿在内的13名义工再次到布拖开展公益援助，女儿潘思睿触动很大，看到同龄人背着用猪饲料口袋制作的书包，她的心久久不能平静，回家后便发动10名小朋友在公园开展义卖，向路人介绍她眼中的布拖县，争取爱心人士对布拖贫困学生的帮助，现场共募集近300元善款，将这笔钱交到“成都义工”网负责人老黑叔叔的手中，参与为布拖孩子买书包的行动。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p><span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 郭乾坤先后4次深入大凉山，和志愿者朋友一起，为布拖的孩子们送去学习用具、御寒衣物、清洁卫生用品，并协助建立“爱心书屋”，教孩子们洗脸、洗手，培养彝族同胞卫生习惯。通过大家6年多的努力，当地孩子的卫生习惯得到了极大改善，生存状态有了明显改观。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; &nbsp; 2007年，郭乾坤全家在参与关爱留守儿童活动中，认识了清泉镇一名特殊留守儿童罗然。罗然父亲是服刑人员，妈妈是残疾人。她家的居住条件很差，简陋的三间房屋没有电灯，也没有窗户。江艳看在眼里，疼在心中，决定帮助这一家。她先找到自己做工程的同学，拉来混凝土平整了房屋地面，新砌了水泥院坝，又和“成都义工网”朋友们联系电力公司为罗然家拉了用电专线。从此，江艳一家将罗然当成家庭成员，对她倾注了更多的关爱。在郭乾坤一家和“成都义工”志愿者们的关心、帮助下，罗然得以健康成长，2013年以优异成绩考上了大学。如今，罗然把江艳一家当成了自己的第二个家，取得成绩会第一时间打电话回家分享快乐，遇到困难会在自己无法克服时会寻求江阿姨的帮助。</span> </p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p>\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp;&nbsp;</span></p><p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 30.66px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px;\"></span></p><p style=\"background-color: transparent; color: rgb(81, 81, 81); font-family: verdana,微软雅黑,sans-serif; font-size: 14px; font-size-adjust: none; font-stretch: 100%; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 21px; margin-bottom: 0px; margin-left: 0px; margin-right: 0px; margin-top: 0px; orphans: 2; padding-bottom: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px; text-align: justify; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; white-space: normal; word-spacing: 0px;\">\n	<span style=\"line-height: 28px;\">&nbsp; &nbsp; </span>&nbsp;</p><p><br/></p>', '基层公务员郭乾坤：坚持公益18年 爱心感召亲人朋友齐参与', '坚持公益');
