  `list_status` int(11) DEFAULT '0',
  `list_time` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`identity_ID`),
  KEY `identity_ID` (`identity_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

